css:
@-moz-document domain("kshowdaily.net")
{html,
body {
    margin: 0;
    padding: 0;
    border: none;
    color: #fff !important;
    background: #151515 !important;
    font-family: lato,sans-serif;
    font-size: 17px;
}
.header-wrapper {
    background: #151515 !important;
}
.wrapper {
    position: relative;
    background-color: #151515 !important;
}
.wrapper .search-button .fa {
    font-size: 24px;
    position: relative;
    top: -1px;
    color: #151515 !important;
}
.section-inner {
    width: 100%;
    max-width: 100%;
    margin: 0 auto;
}
.content {
    width: 68%;
    float: left;
    padding-left: 220px;
}
.sidebar {
    width: 28.5%;
    float: right;
    padding: 50px 0 50px 3.5%;
    padding-top: 50px;
    padding-right: 220px;
    padding-bottom: 50px;
    padding-left: 3.5%;
}
.navigation {
    margin-left: 220px;
    margin-right: 220px;
}
.navigation .section-inner {
    background: #151515;
}
.header {
    margin-left: 220px;
    margin-right: 220px;
}
.widget-content li {
    line-height: 140%;
    padding: 12px 0;
    border-bottom: 1px solid #000;
}
.widget_archive li a, .widget_categories li a, .widget_meta li a, .widget_nav_menu li a, .widget_pages li a {
    color: #969595;
}
.widget_archive li, .widget_categories li, .widget_meta li, .widget_nav_menu li, .widget_pages {
    color: #cacaca;
}
.widget+.widget {
    padding-top: 20px;
    border-top: 1px solid #000;
    margin-top: 30px;
}
.content:before {
    content: "";
    background: #151515;
    position: absolute;
    z-index: -9999;
    top: 0;
    right: 28.5%;
    bottom: 0;
    left: -10000px;
}
.posts .post+.post {
    border-top: 2px solid #000;
}
.post-title a:hover {
    color: #0093c2;
    text-decoration: underline;
}
.post-title a {
    color: #cacaca;
    text-decoration: underline;
}
.credits .copyright {
    font-weight: 900;
    text-transform: uppercase;
    letter-spacing: 1px;
    text-shadow: 0 1px 0 #111;
    padding-left: 120px;
}
.comment-inner {
    margin-left: 80px;
    padding: 25px;
    background: #000;
    border: 0px solid #ddd;
    border-radius: 6px;
    position: relative;
}
.related-post .title {
    margin: 4px 0 0;
    font-size: .85em;
    line-height: 130%;
    font-weight: 700;
    color: #cacaca;
}
.comments-title-container, .comment-reply-title {
    border-top: 4px solid #000;
    padding: 22px 0;
    border-bottom: 2px dotted #000;
    margin-bottom: 40px;
}
.comment-respond form label {
    display: block;
    margin-bottom: 8px;
    font-size: 1rem;
    font-weight: 700;
    color: #fff;
}
}
