css:
@-moz-document domain("gbstudio.dev") {

  body,
  html {
    height: 100%
  }

  body {
    background-color: #33302f;
    margin: 0;
    font-family: -apple-system, blinkmacsystemfont, 'segoe ui', roboto, oxygen, ubuntu, cantarell, 'open sans', 'helvetica neue', sans-serif;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column
  }

  h1 {
    font-weight: 300;
    color: #f6f6f6;
    font-size: 2rem
  }

  h2 {
    font-size: 1.6rem
  }

  li {
    color: #f6f6f6;
  }

  h2,
  h3 {
    font-weight: 300;
    color: #f6f6f6;
    margin: 2rem 0
  }

  h3 {
    font-weight: 400
  }

  h3,
  p {
    font-size: 1.1rem
  }

  p {
    font-family: -apple-system, blinkmacsystemfont, 'segoe ui', roboto, oxygen, ubuntu, cantarell, 'open sans', 'helvetica neue', sans-serif;
    color: #f6f6f6;
    line-height: 1.6
  }

  em {
    color: #f6f6f6;
  }

  ul {
    font-family: -apple-system, blinkmacsystemfont, 'segoe ui', roboto, oxygen, ubuntu, cantarell, 'open sans', 'helvetica neue', sans-serif;
    line-height: 1.6;
    font-size: 1.1rem;
    padding-left: 20px
  }

  ul,
  ul ul {
    list-style-type: disc
  }

  li {
    line-height: 2rem
  }

  a {
    color: #e98b3d;
  }

  img {
    max-width: 100%;
    border: 0
  }

  header {
    background: #4e4845;
  }

  .swatch {
    display: inline-block;
    clear: both;
    background: #071821;
    width: 20px;
    height: 20px;
    margin-right: 2px;
    border-radius: 2px;
    margin-right: 5px;
    vertical-align: middle
  }

  .swatchlabel {
    display: inline-block;
    font-family: -apple-system, blinkmacsystemfont, 'segoe ui', roboto, oxygen, ubuntu, cantarell, 'open sans', 'helvetica neue', sans-serif;
    margin-bottom: 5px;
    -moz-user-select: all;
    -ms-user-select: all;
    user-select: all;
    -webkit-user-select: all
  }

  .helpsprite {
    height: 48px;
    -ms-interpolation-mode: nearest-neighbor;
    image-rendering: -webkit-optimize-contrast;
    image-rendering: -moz-crisp-edges;
    image-rendering: pixelated
  }

  hr {
    background: #eee;
    height: 1px;
    border: 0;
    margin: 90px 0
  }

  nav {
    max-height: 80px;
    background: #4e4845;
    color: #fff;
    overflow: hidden;
    text-align: center
  }

  nav .title {
    float: left;
    display: block;
    text-align: left;
    max-width: 920px;
    padding: 0;
    margin: 20px auto;
    font-size: 32px;
    font-weight: 700;
    height: 40px
  }

  nav a {
    color: #fff;
    text-decoration: none
  }

  header .title:before {
    content: "";
    display: inline-block;
    background-position: 0 100%;
    background-size: contain;
    background-repeat: no-repeat;
    vertical-align: text-bottom;
    width: 38px;
    height: 38px;
    margin-right: 10px
  }

  nav #hamburger,
  nav #menu_open {
    display: none
  }

  nav .container {
    max-width: 960px;
    padding: 0 20px;
    box-sizing: border-box;
    margin: 0 auto
  }

  nav .nav_right {
    float: right
  }

  nav ul {
    font-family: -apple-system, blinkmacsystemfont, 'segoe ui', roboto, oxygen, ubuntu, cantarell, 'open sans', 'helvetica neue', sans-serif;
    display: block;
    text-align: right;
    list-style-type: none;
    vertical-align: top
  }

  nav ul,
  nav ul li {
    margin: 0;
    padding: 0
  }

  nav ul li,
  nav ul li a {
    display: inline-block;
    line-height: 80px
  }

  nav ul li a {
    color: #fff;
    text-decoration: none;
    font-size: 18px;
    padding: 0 29px;
    transition: .3s ease-in-out
  }

  nav ul li a:hover {
    background: rgba(157, 168, 185, .8);
    background: #4e4845
  }

  @media (max-width:1100px) {
    nav ul li a {
      padding: 0 20px
    }
  }

  @media (max-width:700px) {
    nav #hamburger {
      display: block;
      padding: 21px;
      margin-right: -21px;
      fill: #fff
    }

    nav svg {
      display: inline-block;
      width: 30px;
      height: 30px
    }

    nav #menu_open:checked~span .menufade {
      background-color: transparent;
      transition: background-color .3s linear;
      background-color: rgba(0, 0, 0, .5);
      position: fixed;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      z-index: 999
    }

    nav #menu_open~span ul {
      right: -300px;
      opacity: 0;
      position: absolute;
      display: block;
      z-index: 1000;
      top: 0;
      width: 0;
      overflow: hidden;
      height: 100%;
      background: #4e4845;
      transition: all .3s linear
    }

    nav #menu_open:checked~span ul {
      opacity: 1;
      right: 0;
      width: 300px
    }

    nav #menu_open~span ul li {
      display: block;
      text-align: left
    }

    nav #menu_open~span ul li a {
      display: block
    }
  }

  main {
    width: 100%;
    max-width: 960px;
    padding: 20px;
    box-sizing: border-box;
    margin: 0 auto;
    -webkit-box-flex: 1;
    -ms-flex: 1;
    flex: 1
  }

  footer {
    background-color: #413e3c;
    padding: 50px 20px;
    clear: both
  }

  footer .container {
    max-width: 960px;
    padding: 0 20px;
    box-sizing: border-box;
    margin: 0 auto
  }

  footer svg {
    margin-right: 20px;
    fill: #f6f6f6
  }

  footer a {
    text-decoration: none
  }

  footer a:hover svg {
    fill: #acb4c1
  }

  .pre,
  pre {
    overflow-x: auto;
    overflow-y: hidden;
    overflow: scroll
  }

  code {
    background-color: #4e4845;
    border-radius: 4px;
    padding: 4px
  }

  pre code {
    display: block;
    padding: 1.5em;
    font-size: 1rem;
    line-height: 2;
    background-color: transparent;
    border-radius: 0
  }

  pre,
  pre code {
    white-space: pre
  }

  pre {
    background-color: #222;
    color: #ddd;
    -webkit-hyphens: none;
    -ms-hyphens: none;
    hyphens: none;
    position: relative;
    border-radius: 4px
  }

  .homepage-landing {
    background-color: #4e4845;
    margin-bottom: 0;
    max-height: 420px;
    -ms-flex-negative: 0;
    flex-shrink: 0;
    overflow: hidden;
    text-align: center
  }

  .homepage-landing .container {
    position: relative;
    max-width: 960px;
    max-height: 420px;
    padding: 0 20px;
    box-sizing: border-box;
    margin: 0 auto
  }

  .homepage-landing img {
    width: 100%;
    max-width: 820px;
    -webkit-transform: translate(394px);
    transform: translate(394px)
  }

  .homepage-landing__content {
    position: absolute;
    max-width: 440px;
    top: 0;
    bottom: 0;
    color: #fff
  }

  .homepage-landing__content h2 {
    color: #fff;
    font-size: 2rem;
    text-align: left;
    margin-bottom: 40px
  }

  .homepage-landing__button {
    float: left;
    display: block;
    background: #33302f;
    color: #f6f6f6;
    border-radius: 4px;
    padding: 15px 20px;
    margin-bottom: 10px;
    clear: both;
    text-decoration: none
  }

  .homepage-landing__link {
    text-align: left;
    display: block;
    clear: both;
    color: #e98b3d;
    text-decoration: underline
  }

  section.homepage-left,
  section.homepage-right {
    clear: both;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    margin: 0 -20px 20px
  }

  section.homepage-right {
    -webkit-box-orient: horizontal;
    -webkit-box-direction: reverse;
    -ms-flex-direction: row-reverse;
    flex-direction: row-reverse
  }

  section.homepage-left h2,
  section.homepage-right h2 {
    font-size: 2em;
    margin: 1em 0
  }

  section.homepage-left div.homepage-center,
  section.homepage-right div.homepage-center {
    width: 50%;
    padding: 0 20px;
    box-sizing: border-box;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: top;
    -ms-flex-pack: top;
    justify-content: top;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column
  }

  div.homepage-center p {
    padding-right: 50px;
    margin-bottom: 40px
  }

  .homepage-content {
    width: 100%
  }

  section.homepage-left img,
  section.homepage-right img {
    max-width: 300px
  }

  .video {
    max-width: none;
    background-color: #f1f1f1;
    text-align: center
  }

  .content {
    max-width: 746px;
    margin: 0 auto
  }

  .videocontainer {
    position: relative;
    padding-bottom: 52.25%;
    padding-top: 30px;
    height: 0;
    overflow: hidden
  }

  .videocontainer embed,
  .videocontainer iframe,
  .videocontainer object {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%
  }

  @media (max-width:1100px) {
    .homepage-landing__content {
      max-width: 320px
    }

    .homepage-landing img {
      -webkit-transform: translate(310px);
      transform: translate(310px)
    }
  }

  @media (max-width:700px) {
    .homepage-landing__content {
      position: static;
      max-width: none
    }

    .homepage-landing,
    .homepage-landing .container {
      max-height: none
    }

    .homepage-landing__image {
      margin-top: 40px;
      margin-bottom: -20%
    }

    .homepage-landing img {
      margin-left: -9.1%;
      margin-right: -9.1%;
      width: 110%;
      -webkit-transform: translate(0);
      transform: translate(0)
    }

    section.homepage-landing h1 .bad {
      -webkit-transform: translate(14px, 10px);
      transform: translate(14px, 10px)
    }

    section.homepage-landing h1 .gb span {
      display: block
    }

    section.homepage-landing h1 .web {
      -webkit-transform: translate(63px, 30px);
      transform: translate(63px, 30px)
    }

    section.homepage-left,
    section.homepage-right {
      display: block;
      margin-bottom: 0
    }

    section.homepage-left div.homepage-center,
    section.homepage-right div.homepage-center {
      width: 100%;
      -webkit-box-align: center;
      -ms-flex-align: center;
      align-items: center
    }

    div.homepage-center p {
      padding-right: 0
    }
  }

  @media print {
    hr {
      background: transparent;
      margin: 0;
      page-break-after: always
    }

    nav #hamburger {
      display: none !important
    }
  }
}
