css:
@-moz-document domain("trashbox.ru") {
/*v etom kode ochenb mnogo govna
ne lez debil
ono tebya sozhryot
*/

header #div_top_logo_header .div_cell:not(.cell2){
    position: relative;
}

header #div_top_logo_header .div_cell:not(.cell2):after{
    content: '';

    display: block;

    width: 100%;
    height: 100%;

    position: absolute;
    top: 0;
    left: 0;
    /*logo*/
    background-color: rgb(34,34,34);
    background-image: url('https://i.ibb.co/T8pNHrS/logo-darkwhitetrash-stereo-128.png');
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
}

/*head*/ 

#div_menu_page {
    background: #2220 !important;
}

header {
      background: #000000d9 !important;
}

#div_top_menu_text a,
#div_top_menu_text a:visited {
    color: white;
    background: none;
}

#div_top_menu_text a.current {
    color: white !important;
    background: #434343 !important;
}

#div_top_menu_text a:hover {
    background: #434343 !important;
}

 /*bokovaya huinya*/

body {
    background: #111;
    color: #bfbfbf !important ;
}

.editor_area {
    background: #222;
    color: #bfbfbf !important;
} 

.unread3.mode1 {
    background: #343434;
    color: #bfbfbf !important;
    padding: 10px;
}

/*#textarea_editor_comments {
    background: #bfbfbf;
    color: #bfbfbf !important;
}*/

#input_html_comments {
    color: white !important;
}

.div_editor_resize{
    background: #404040;
}

.div_editor_button_white {
    color: #bfbfbf !important;
}


.left {
    float: left;
    color: #bfbfbf !important;
}

/*strelo4ka u profilya*/

.carret {
    border-left: 6px solid transparent;
    border-right: 6px solid transparent;
    border-top: 5px solid rgba(255,255,255,0.5);
}

/*scrubscribe to notifications*/

#div_push_notify_button_postload {
    background: #222;
}

/*copyright*/

center,
#div_social_bottom,
.div_bottom_block
{
    background: #222;
}

/* links */

a, a:visited{
    color: #bfbfbf !important;
}

a:hover{
    color: #bfbfbf !important;
    background-color: #222;
}

/*profile*/

#div_user_menu a, 
#div_user_menu a:visited {
    color: #bfbfbf !important;
}

#div_user_menu {
    background: #434343;
}

#div_about_user {
    background: #222;
    color: white;
}

.symbol {
    color: white;
} 

#div_notify {
    background: #222;
}

/* news block */

#center clear div_index_topics_more_block,
#a_topic_cover,
#div_topic_cover topic_cover_1 standart,
#div_topic_cover topic_cover_2 standart,
#div_topic_cover topic_cover_3 standart,
#div_topic_cover_caption {
    background: #bfbfbf !important;
}

.a_topic_cover {
    color: white !important;
}

#div_index_topics_next_news_0_button {
    background: #3e3e3e !important;
}

.div_index_topics_more {
    border-color: #434343 !important;
}


blockquote{
    background: #222;
}

/*obsuzhdaemoe*/

.div_panel_bordered {
    background: #222;
}

/*header*/

.div_panel_bordered_caption {
    background: #222;
    color: #fef7cf !important;
    border-left: 5px solid #222;
    border-right: 5px solid #222;
}

/*topics headers*/

.div_most_commented_item span {
    color: #bfbfbf !important;
}

#div_page_right {
    background: #222;
}

.div_panel_no3.div_site_info {
    background: #222;
    text-align: center;
    color: white;
}

.div_panel_no3 {
    border-left: 20px solid #222;
}

.div_panel_no2 {
    border-right: 10px solid #222;
    border-left: 10px solid #222;
}

.div_panel_no1 {
    border-right: 20px solid #222;
}

.div_panel_view {
    background: #434343;
}

.div_site_info .block1 {
    border: 1px solid #222;
}

.div_right_rec_text {
    background: #434343;
}

.div_panel_gray_caption {
    background: #434343;
    color: #fef7cf;
}

/* primary color */

#div_top_logo .cell2,
.div_top_black_buttons {
    border-color: black !important;
    background: #0000 !important;
}

.div_top_news_highlight,
#div_add_push_notify,
#div_user_menu a:hover,
.black a:hover, a.black:hover,
.zero_space_padding2,
.div_index_topics_more,
.div_panel_digest_top,
#div_menu_page:after,
.div_panel_bordered_50,
.div_auth_input,
.div_auth_button_submit,
.div_panel_bordered_50,
.div_panel_bordered,
.div_most_commented_item {
    border-color: black !important;
    background: #222 !important;
}

.div_panel_caption {
    color: #fef7cf;
    border-color: #222 !important;
    background: #222 !important;
}

.div_topic_votes_bar tbody tr td {
    background: #00ceff !important;
}

.div_gallery_preview_block.text_gallery {
    outline: 5px solid #222;
    background: #222;
    height: 50px;
}

#div_new_comment_check {
    background: #434343;
}

/* index begin */
.zero_space_padding2
/* index end */{
    box-shadow: 0px 0px 0px 1px #222 !important;
    -webkit-box-shadow: 0px 0px 0px 1px #222 !important;
}

/*comments*/

.div_comment .div_header,
.div_avatar_small {
    background: #434343;
}

.div_comment {
    border-left: 5px solid #434343;
} 

.div_topic .div_text {
    color: white;
}

.div_vote_zero {
    opacity: 0.5;
    color: white;
}

.div_comment_votes_current {
    color: #bfbfbf !important;
}

.span_new_comment_button {
    color: #bfbfbf !important;
}

/* other */

.div_panel_more a,
.div_right_rec_text .div_topic_cover_caption,
.span_good_medals{
    color: white !important;
}

.div_tags_menu span .a, .div_tags_menu .a {
    color: #c0c0c0;
} 

#div_page_left {
    background: #222;
    color: white;
}

.div_text_content {
    color: white;
}

.div_topic_cover.reviews .div_topic_cover_caption {
    border-left: 10px solid rgb(0,0,0,0);
}

.div_index_highlight_block .div_topic_cover_caption {
    border-left: 8px solid #777777;
}

.div_topic_tcapt_content {
    color: white;
}

/* forum */

.div_forum_caption {
    color: white;
}

.div_forum_dark div {
    background: #434343;
}

.a_forum_answer,
.a_forum_answer:visited {
    background-color: #0099ff;
    color: white;
}

.black {
    color: white;
    background: none;
}

.div_tags_menu_top .a:first-child {
    margin-left: 40px;
}

.black a, a .black, a.black:visited, .black a:visited {
    color: white;
    text-decoration: none;
}

.div_tags_menu .active {
    background: #0099ff;
}

.div_tags_menu span .a, .div_tags_menu .a {
    color: white;
}

/* forum-end */

.div_panel_more a:hover,
.div_topic_day:hover span,
.div_most_commented_item a:hover span,
#div_user_menu a:hover .dark,
/* buttons begin */
.div_sumbenu_item,
.blue a, a .blue, a:visited .blue, .blue a:visited,
.div_auth_button_submit,
/* buttons end */
/* forum begin */
.div_forum_tags a, .div_forum_tags a:visited,
.div_tags_menu a:hover,
.div_tags_menu_top .a
/* forum end */{
    color: #fff !important;
    background-color: #222;
}

#div_index_topics_next_news_0_button{
    background: #434343;
}

.page_index .div_timeline_topics:before {
    width: 80%;
    left: 0px;
    background: #777;
}

.td_rank_pos > div{
    color: #fff !important;
    background-color: #222 !important;
}

.td_rank_pos a{
    color: #fff !important;
    background: transparent;
}

.td_rank_pos a:hover{
    background: #222 !important;
}

/* very primary azazaz (forum) */

.a_forum_answer:hover, .div_forum_tags a:hover,
/* buttons begin */
.div_sumbenu_item_active,
.blue a:hover, a:hover .blue, a.blue:hover
/* buttons end */{
    background-color: #222 !important;
}

.dark2,
.dark {
    color: #ffffff !important;
}

.h_left_header {
    background: #434343;
}

.div_topic .div_text h1, .div_topic .div_text h2, .div_topic .div_text h3 {
    color: white;
}

.div_youtube_videos .div_line_header {
    background: #222;
}

.div_line_header_text {
    color: #fef7cf;
    background: #222;
}

#iframe_editor_comments,
.editor_area {
    color: white !important;
    background: #bfbfbf !important;
}

#div_add_files, .comp_left {
    padding: 10px;
}

.td_dark {
    background: #111;
}
/* no-primary */

.div_index_topics_more,
.div_comment.highlighted_comment{
    border-color: #ffffff00 !important;
}

.div_comment.highlighted_comment:hover{
    border-color: #222 !important;
} 

.a_social_link_2016 {
    background-color: #434343;
} 

.div_best_user_place,
.div_top_user_rank,
.div_top_user_rank,
.div_top_user_rank,
.div_top_user_caption {
    color: white;
}

.h_tags_menu_top {
    color: white !important;
}  

.span_item_active {
    color: white;
    background: #0099ff;
}

.div_youtube_videos {
    background: #222;
}

.div_panel_bordered_50 {
    background: #434343;
    margin-top: 0px;
}

.div_topic_day span {
    color: white !important;
} 

.div_topic_circle_descr_top {
    color: #ddd;
} 

#div_top_news_row {
    white-space: nowrap;
    background: #222;
}

.div_top_news_b1 {
    background: #222;
}

#div_top_search {
    background: #222;
}

html, body, button {
    color: white !important;
}

#frm_topic > div.div_form_block > table > tbody > tr:nth-child(2) > td:nth-child(1) {
    background: #111 !important;
}

.comp_left {
    background: #111 !important;
}

h2 {
    color: #c0c0c0;
}

/*votes*/

.div_votes_control tr .div_comment_votes_buttons:last-child td {
    background: #00ceff !important;
}

/* remove (kebab) tarans privet, bratan, ya vzyal tvoi ishodniki (ot xiaomitop) za osnovu, nadeyusb ty ne protiv */

.div_most_commented_item a{
    background: transparent !important;
}

.div_index_text_promo_caption {
    color: white !important;
}

.div_index_text_promo:hover .div_index_text_promo_caption {
    color: black !important;
}

.div_index_text_promo2:hover .div_index_text_promo_caption {
    color: black !important;
    border-bottom: 1px solid #000;
}

.div_index_text_promo2{
    border-color: #000;
}

.div_index_text_promo2 .div_index_text_promo_caption {
    border-bottom: 1px solid #fff;
}

#div_menu_page {
        background: #222 !important;
}
}
