css:
@-moz-document domain("lebunker.net") {
.lebunkerNav {
    height: 50px;
}

#mainNav .search-block input {
    border-radius: .25rem !important;
}

.container-index .forum-list, .container-index .forum-list .post-icon img {
    margin-top: 0px;
}

.container-subforum {
    margin-top: 10px;
}

.count-online-user {
    font-size: 16px;
}

.count-online-user > .text, .network, .footer-contact, .menu, .post_title > label, .breadcrum, .post_nsfw > label, #mainNav .search-block button, .subforum-title, .section-separator:nth-child(3) {
    display: none;
}

tr, tr > * {
    padding: 0 !important;
    vertical-align: middle !important;
    margin: 0;
}

p {
    margin: 1px !important;
    color: white;
}

.post-icon {
    padding: 3px !important;
}

.title-section, .card-header {
    padding: 10px !important;
}

.title-section {
    margin: -80px 0 -20px 0;
}

.post-user {
    white-space: nowrap;
}

.likeunlike {
    padding: 10px 0 10px 10px;
}

.post-container {
    margin-top: -50px;
}

.container-index .forum-list .post-bg1 {
    background: #1c2938;
}

.container-index .forum-list .post-bg2 {
    background: #15202b;
}

.container-index .forum-list .post-bg1:hover {
    background: #1c2938;
    text-shadow: 0px 0px 8px white;
}

.container-index .forum-list .post-bg2:hover {
    background: #15202b;
    text-shadow: 0px 0px 8px white;
}
.container-index .forum-list .post-bg2 a, .container-index .forum-list .post-bg1 a, .not-selected > *, .gap, select, input, .like:hover, .unlike:hover, .post-message > p, .post-container .info-post, .upvote-count, .upvote-count-posts, .mce-charactercount, .mce-ico, .card-text-bunker, .col-sm-12, .title-section > *, .pagination .previous-next a, .previous-next-disable {
    color: #fff !important;
}

.like, .unlike, .like-posts, .unlike-posts {
    color: #3d5368 !important;
}

.like-selected {
    color: #23bf23 !important;
}

.unlike-selected {
    color: #d60606 !important;
}

#mainNav, .likeunlike, .card-header, .not-selected, .gap, .pagination .previous-next, .previous-next-disable, .report-section > div, .report-section > a > div, .container-index .forum-list .thead-bunker, #new_post, .notification-collapse .list-notif, .profil-header, #myTab, .search-section-user, .search-section {
    background: #1c2938 !important;
}

.card-body, .card-body-post, .post-form, input, select, .notification-collapse, .notification-collapse .custom-separator, .desc, #profile {
    background: #15202b;
}

.table td, .card, input, select, .notification-collapse .notif_link {
    border: none !important;
}

.post-container {
    background: none;
}

body, .title-section {
    background: #10171e !important;
    
}

body, h1, h2, h3, h4, h5, h6, .block-identifiant > span {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
    color: white;
}

.block-identifiant > span {
    letter-spacing: 0px;
    text-transform: none !important;
}

.mce-container, .mce-widget, .mce-reset {
    border: 0 !important;
    background: #15202b;
}

.like, .unlike, .like-posts, .unlike-posts {
    border-color: #3d5368 !important;
}

.search-section-user, .search-section {
    border: 1px solid #1c2938 !important;
}

.table th {
    border: 0 !important;
}

.card-text-bunker {
    font-size: 0.9em;
}
}
