css:
@-moz-document domain("tokybook.com")
{html,

body, p {
    font-family: Verdana,Geneva,sans-serif;
    font-size: 14px;
    line-height: 21px;
    color: white;
    background-color: black;
}
.td-header-style-5 #td-header-menu {
    min-height: 60px;
    background-color: black;
}
.td-gadgets .td-header-style-5 .sf-menu > li > a:hover, .td-gadgets .td-header-style-5 .sf-menu > .sfHover > a, .td-gadgets .td-header-style-5 .sf-menu > .current-menu-item > a, .td-gadgets .td-header-style-5 .sf-menu > .current-menu-ancestor > a, .td-gadgets .td-header-style-5 .sf-menu>.current-category-ancestor>a {
    color: #f48a20;
    background-color: transparent !important;
}
.td-container, .tdc-row {
    background-color: black;
}
tdc-content-wrap {
    background-color: black;
}
.header-search-wrap .td-drop-down-search-open {
    background-color: #353535;
}
.td-container-wrap {
    background-color: #000;
    margin-left: auto;
    margin-right: auto;
}
h1>a, h2>a, h3>a, h4>a, h5>a, h6>a {
    color: #8d8d8d;
}
.single .td-category {
    background-color: #222222;
}
.td-category a {
    color: #fff;
    background-color: #f28920;
    padding: 3px 6px 4px 6px;
    white-space: nowrap;
    display: inline-block;
}
article, aside, details, figcaption, figure, footer, header, hgroup, main, menu, nav, section, summary {
    display: block;
    background-color: black;
}
.post header .entry-title {
    color: white;
}
.td-page-content, .td-post-content, .wpb_text_column, .td_block_text_with_title, .woocommerce #tab-description, .mce-content-body {
    font-size: 15px;
    line-height: 26px;
    color: #fff;
    background-color: black;
}
h1, h2, h3, h4, h5, h6 {
    font-family: 'Roboto',sans-serif;
    color: #f48a20;
    font-weight: 400;
    margin: 6px 0 6px 0;
}
.post {
    background-color: #000;
}
.g-background-default {
    background-color: #1b1b1b !important;
}
.widget {
    border: 1px solid #000000 !important;
}
.td-post-small-box a {
    display: block;
    float: left;
    border: 1px solid #171717;
    margin-left: 4px;
    line-height: 8px;
    color: #fff4f4;
    padding: 5px 8px;
    height: 20px;
}
.td-post-next-prev-content a {
    display: block;
    font-size: 15px;
    color: #7d7d7d;
    line-height: 21px;
    margin-bottom: 43px;
}
.td_block_template_5 .td-block-title {
    font-size: 16px;
    font-weight: 500;
    margin-top: 0;
    margin-bottom: 26px;
    line-height: 25px;
    color: #fff;
}
.comment cite {
    font-family: 'Open Sans',arial,sans-serif;
    font-weight: 700;
    font-style: normal;
    font-size: 14px;
    line-height: 1;
    background-color: black;
    color: white;
}
cite a {
    color: #fff;
}
.widget a {
    color: #ffffff;
}
}
