css:
@-moz-document domain(teamtreehouse.com) {
body {
    background: #3e474f !important;
    color: #b2bac2 !important;
    border-bottom-color: #000;

  }

.control-container .subnav {
    background-color: #31383e;
    border-top: 1px solid #576366;
    display: block;
    padding: 0 30px;
    border-radius: 0 0 4px 4px;
}

.degree-completed-activities-toggle {
    border-top: 2px solid #31383e;
    position: relative;
}

.card-degree-unit-courses {
    background-color: #31383e;
    border-radius: 0 0 4px 4px;
    position: absolute;
    bottom: 0;
    width: 100%;
    color: #7b8b8e;
    cursor: pointer;
    font-size: 13px;
    font-weight: 500;
    transition: color 200ms ease-in-out;
}

  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    color: #b2bac2 !important;
  }

  p {
    color: #b2bac2 !important;
  }

  a {
    color: #5fcf80;
  }

  a:hover {
    color: #34ad58;
  }

  .header,
  nav {
    background: #384047 !important;
  }

  .header-nav-link {
    color: #acb2b7;
  }

  .nav-anchor {
    color: #b2bac2;
  }

  .nav-anchor:hover {
    color: #5fcf80;
  }

  .nav-item.current a {
    color: #fff;
  }

  .header-nav-item-profile-chevron {
    fill: #5fcf80;
  }

  .header-nav-item-notifications-icon {
    fill: #e8ae15c9;
  }

  ul#view.nav {
    background: #323a40 !important;
  }

  .control-container {
    background: #323a40;
  }

  .control-container .nav li.current a,
  .control-container .nav a.selected {
    color: #fff;
  }

  .control-container .nav a:hover,
  .control-container .nav li a:hover {
    color: #5fcf80;
  }

  .control-container .dropdown-parent .dropdown-parent-label {
    color: #5fcf80;
  }

  .control-container .dropdown-parent:hover > a,
  .control-container .dropdown-parent:hover a:hover {
    color: #fff;
  }

  div.search {
    background: #323a40;
  }

  .achievement-steps {
    background: #323a40;
  }

  .achievement-steps li a strong {
    color: #fff;
  }

  .achievement-steps li a {
    border-bottom: 1px solid #31383e;
  }

  .dashboard-index .content-suggestions .box-footer .suggestion-time-estimate strong {
    color: #b2bac2;
  }

  .tabs li a:hover {
    color: #fff;
  }

  .tabs li.current a {
    color: #fff;
  }

  .tabs li.current a:hover {
    color: #7b8b8e;
  }

  .card-box {
    background: #384047 !important;
  }

  a.card-box.card-link::before,
  a.card-box.card-link::after {
    background: #323a40 !important;
  }

  .card-status-title {
    color: #b2bac2;
  }

  #track-card:after,
  #track-card:before {
    background: #323a40;
  }

  .section-heading p {
    color: #7b8b8e;
  }

  .section-heading a {
    color: #5fcf80;
  }

  #no-results p {
    color: #b2bac2;
  }

  .topic-filter a span {
    color: #b2bac2;
    background: #384047;
  }

  .videos-show #video-meta span#video-authors a,
  .markdown-zone a,
  .videos-show #workspaces-tab-content .workspace-item--notcreated a {
    color: #5fcf80;
  }

  .videos-show #video-meta span#video-authors a:hover {
    color: #fff;
  }

  .markdown-zone a:hover {
    color: #34ad58;
  }

  .markdown-zone code {
    color: #7b8b8e;
  }

  .markdown-zone .highlight {
    overflow: hidden;
  }

  #cc-editor .editor .CodeMirror .CodeMirror-scroll,
  #cc-editor .preview .CodeMirror .CodeMirror-scroll {
    overflow: hidden !important;
  }

  #cc-editor .cm-s-solarized.cm-s-dark .CodeMirror-linenumber {
    color: #b2bac2;
  }

  #cc-editor .cm-s-solarized .cm-comment {
    color: #7b8b8e;
  }

  .videos-show #questions-container .content-discussion,
  .videos-show #downloads-tab-content .content-discussion {
    background: #323a40;
  }

  #quiz-feedback .alert {
    background: #323a40;
  }

  #quiz-answers .multiple-choice li .answer,
  #quiz-answers .true-false li .answer {
    color: #fff;
  }

  .button-secondary {
    color: #b2bac2 !important;
    border: 2px solid #b2bac2 !important;
  }

  .button-secondary:hover {
    color: #5fcf80 !important;
    border: 2px solid #5fcf80 !important;
  }

  .button.secondary {
    color: #b2bac2 !important;
    border: 2px solid #b2bac2 !important;
  }

  .button.secondary:hover {
    color: #5fcf80 !important;
    border: 2px solid #5fcf80 !important;
  }

  .workspace-table,
  .workspace-table .top,
  .workspace-table .bottom {
    background: #384047;
    border: none;
  }

  .workspace-item {
    border-top: 1px solid #7b8b8e !important;
    border-bottom: 1px solid #4b5658 !important;
  }

  .discussion-answer .discussion-byline a,
  .discussion-comment .discussion-byline a {
    color: #b2bac2;
  }

  .discussion-byline a:hover {
    color: #fff !important;
  }

  .discussion-answer-best {
    background: #384047;
  }

  .discussion-answer-voting,
  .discussion-answer-voting .vote-up a {
    background: #384047;
  }

  .discussion-tags:after,
  #forum-post-breadcrumb:after {
    display: none;
  }

  #filter-breadcrumb {
    background: #323a40;
  }

  #filter-breadcrumb-tags li {
    color: #b2bac2;
  }

  .sorting-container a.selected, .sorting-container .dropdown-parent-label.selected {
    color: #b2bac2;
  }

  .leaderboard-list table {
    background: #323a40;
  }

  .leaderboard-truncate {
    background: #3e474f;
  }

  .leaderboard-user {
    color: #b2bac2;
  }

  .leaderboard-user:hover {
    color: #576366;
  }

  .leaderboard-total {
    color: #5fcf80;
  }

  .form-item p a {
    color: #5fcf80;
  }

  .box,
  .contained {
    background: #323a40;
  }

  .sidebar #sidebar-nav {
    background: #323a40;
  }

  .discussion-title a {
    color: #b2bac2;
  }

  .discussion-title a:hover {
    color: #fff;
  }

  .dashboard-profile div#profile-info figure figcaption ul#social-links li a {
    background: #323a40;
  }

  .mini-profile-heading {
    background: #323a40;
  }

  .modal.mini-profile .student-stats strong {
    color: #b2bac2;
  }

  .data-item a {
    color: #5fcf80;
  }

  .module p,
  .contained .secondary-module p {
    color: #7b8b8e;
  }

  .disclaimer {
    background: #384047;
  }

  .mixed-box .box-header,
  .mixed-box .box-footer {
    background: #384047 !important;
  }

  .mixed-box .box-actions.alert.info,
  .mixed-box .box-actions.alert.icons-only {
    background: #384047;
  }

  .mixed-box .box-header, .mixed-box .box-content, .mixed-box .box-actions, .mixed-box .box-alert, .mixed-box .box-footer {
    background: #384047;
  }

  .plan-billed strong {
    color: #7b8b8e;
  }

  html.accounts-controller .percent .value {
    color: #b2bac2;
  }

  .accounts-subscription #pause-enrollment-cta p a,
  .accounts-subscription .mixed-box .box-footer p a,
  a.business-callout-anchor {
    color: #5fcf80;
  }

  .business-callout .right-arrow-icon {
    fill: #5fcf80;
  }

  .newsletter {
    background: #323a40 !important;
  }

  .page-content .tag-bar li a {
    border: 2px solid #5fcf80;
    color: #5fcf80;
  }

  .page-content .tag-bar li a:hover {
    border: 2px solid #34ad58;
    color: #34ad58;
  }

  .footer-bonus-description a {
    color: #5fcf80;
  }

  .footer-social-link {
    background: #3e474f;
  }

  footer .social li a {
    background: #3e474f center no-repeat;
  }

  footer p a {
    color: #b2bac2;
  }

.syllabi-show #syllabus-actions, .syllabi-upcoming #syllabus-actions {
    border-bottom: 2px solid #55616c;
    padding: 0 0 20px;
    margin: -5px 0 30px;
}

.syllabi-show #syllabus-stages .steps-list>li, .syllabi-upcoming #syllabus-stages .steps-list>li {
    border-top: 1px solid #444e56;
}

.card-degree .degree-completed-activities-toggle {
    border-top: 2px solid #55616c;
    position: relative;
}

.degree-activity-item:not(:last-child):not(.degree-activity-alert):not(.degree-activity-current) .degree-activity-content {
    border-bottom: 1px solid #474d52;
}

hr {
    height: 0;
    margin: 30px 0;
    border-top: 1px solid #55616c;
}

.degree-items {
    border-right: 1px solid #313940;
    padding-right: 20px;
}

.footer-list {
    clear: both;
    border-top: 1px solid #495056;
    padding: 20px 0;
    margin: 20px 0 0;
}

.mixed-box .box-header.secondary:not(.alert), .mixed-box .box-content.secondary:not(.alert), .mixed-box .box-actions.secondary:not(.alert), .mixed-box .box-alert.secondary:not(.alert), .mixed-box .box-footer.secondary:not(.alert) {
    background-color: #31383e;
}

.ct-label {
 color: #b2bac2;   
}

.degree-overview-progress-total {
    background: #31383e;
}

.header-nav-item-profile-chevron {
    fill: #f9fafa;
}

.button-secondary {
    color: #f9fafa !important;
    border: 2px solid #f9fafa !important;
}

.topic-filter a {
    box-shadow: 0 2px 0 rgba(0,0,0,0.06);
    border-radius: 4px;
    background: #384047;
    width: 100%;
    height: 80px;
    display: flex;
    align-items: center;
    transition: transform 0.2s;
}

.control-container .search-input input {
    margin: 0;
    padding-right: 0;
    padding-left: 20;
    width: 100%;
    background: #3f474f;
    border: 0;
}

.discussion-item-suggested {
    background: #2b3235;
}

.discussion-item {
    padding: 25px 0;
    border-bottom: 1px solid #55616c;
    position: relative;
}

.syllabi-show #syllabus-title h1, .syllabi-upcoming #syllabus-title h1 {
    color: #fff;
}

.project-stage .steps-list-item {
    border-top: 1px solid #424c54;
    padding: 25px 0 15px 0;
}

.degree .box-shaded {
    background: #3f474f;
    border-radius: 4px;
    padding: 15px;
}

.project-related-item {
    border-bottom: 1px solid #4d5761;
    display: block;
    padding: 20px 0;
}

.section-heading {
    padding: 30px 0 0;
    margin: 0 0 20px;
    border-top: 1px solid #576366;
}

hr.hr-on-gray {
    border-top: 1px solid #576366 !important;
}

.application-layout .pages-support-hero {
    background: #353c42;
}

.application-layout .pages-support-faq {
    box-shadow: 0 2px 0 rgba(0,0,0,0.06);
    background: #384047;
}

.control-container.attached {
    background-color: #40484a;
    box-shadow: 0 1px 0 0 #4b5658;
    border-radius: 4px 4px 0 0;
}

.control-container.attached .search .search-form .search-input input {
    border-radius: 100px;
    border: 2px solid #576366;
    margin: 0;
    padding: 11px 40px;
    color: #7b8b8e;
}

.avatar-white-bg .avatar-mask {
    fill: rgba(0,0,0,0.08);
}

.form-element {
    background: #3f474f;
    border: 2px solid #576366;
    font-weight: normal;
    color: #576366;
    line-height: 1;
    display: block;
    border-radius: 5px;
}

.discussion-commenting {
    width: 100%;
    position: relative;
    padding: 15px 0;
    margin: 15px 0 0;
    z-index: 2;
    border-top: 1px solid #55616c;
}

.discussion-answer-best {
    border: 2px solid #465058;
    border-width: 2px 0;

    position: relative;
    z-index: 1;
}

.discussion-answer-best .discussion-commenting {
    border-color: #55616c;
}

.discussion-answer-post {
    position: relative;
    border-top: 2px solid #414b54;
    padding: 30px 0 0 70px;
}

.discussion-answer-best .discussion-answer-voting .vote-up a {
    background: #2a3138;
}

.discussion-answer-voting a {
    color: #d4d9dd;
}

.comment {
    clear: both;
    border-top: 1px solid #55616c;
}

#asked-to-answer-banner {
    background: #31383e;
    border-bottom: 1px solid #48525a;
    color: #7b8b8e;
    font-size: 13px;
    line-height: 1.6;
    position: relative;
}
.discussion-answer {
    
    border-top: 2px solid #55616c;
    
}

.dropdown .dropdown-title {
    display: block;
    background: #30363c;
    font-size: 16px;
    line-height: 20px;
    position: relative;
    z-index: 1;
    border-bottom: 1px solid #55616c;
    border-radius: 5px 5px 0 0;
    margin: 0;
}

.dropdown>ul>li {
    border: 0 solid #55616c;
    border-width: 0 0 1px;
    font-weight: normal;
    margin: 0;
    transition: background-color 0.3s ease, border-color 0.3s ease;
}

.dropdown .dropdown-secondary {
    position: relative;
    display: block;
    padding: 15px 20px;
    font-size: 14px;
    line-height: 18px;
    font-weight: 500;
    color: #576366;
    transition: background-color 0.3s ease, color 0.3s ease;
    border-radius: 0 0 5px 5px;
    background: #30363c;
    border-top: 1px solid #55616c;
}

.dropdown:before {
  
    background: #30363b;
}

.dropdown>ul>li {

    background-color: #384047;
}

.header-nav-item-profile-dropdown-title-name {

    color: #b2bac2;

}

.dropdown>ul>li>a {

    color: #9ea1a2;
 
}

.sidebar #sidebar-nav li {
    border-bottom: 1px solid #3d454c;
    color: #7b8b8e;
    position: relative;
    transition: background-color 0.3s ease, color 0.3s ease;
}

.content-meta {
    background: #333a40;
    height: 220px;
    padding: 30px 30px 75px;
    position: relative;
    box-sizing: border-box;
}

.achievement-hero {
    background: #384047;

}



.degree-activity-current-step h3 {
    margin: 0 130px 3px 0;
    line-height: 1.3;
    color: #fff;
}
}
