css:
@-moz-document regexp("^(?=.*hasutsuki\\.com)(?=.*flame-kingdom).*$") {
/* Code to Style the Text and Scrollbar */
div[class="entry-content"]{
    background-color: #333333;
    border-bottom-color: #333333;
    font-family: Helvetica, Arial, sans-serif;
    line-height: 32px;
    color: #eeeeee !important;
    font-size: 22px;
    margin-bottom: unset;
    letter-spacing: unset;
    text-align: unset !important;

}
span{
    color: #eeeeee !important;
}
h1, h2, h3, h4, h5, h6, h7, h8, h9 {
    color: #eeeeee !important;
    background-color: #333333;
    font-family: Helvetica, Arial, sans-serif !important;
}
div[id="page"]{
    background-color: #333333;

}
hr {
    border-color: #555555 !important;
}

a {
    color: #c69c6d !important;
}

p {
    color: #eeeeee;
    font-size: 22px;
    margin-bottom: unset;
    letter-spacing: unset;
    text-align: unset !important;
}

html {
    scrollbar-color: #555555 #333333;
}

body {
    background-color: #333333;
    border-bottom-color: #333333;
    font-family: Helvetica, Arial, sans-serif;
    line-height: 32px;
}

.html::-webkit-scrollbar-track-piece {
    background-color: #333333;
}

.html::-webkit-scrollbar-thumb:vertical {
    height: 30px;
    background-color: #555555;
}

/* Code to center Novel text */
main[id="main"]{
	margin: unset;
}

.site {
    max-width: 700px !important;
    margin: 0 auto !important;
    padding: unset;
}

.entry-title {
    margin-top: unset !important;
 	width: unset !important;
    letter-spacing: unset;
}

div[id="content"] {
    width: 700px !important;
    margin: 0 auto !important;
}

div[class="entry-content"] {
 	margin-bottom: unset !important;
}

.entry-wrapper {
 	width: unset !important;   
}

/* Code to remove content from webpage except for the Novels text and navigation. */
a[class="skip-link screen-reader-text"] {
    display: none;
}

header[id="masthead"] {
    display: none;
}

div[class="sosimple-breadcrumbs"] {display: none;}

footer[id="colophon"] {
    display: none;
}

div[id="secondary"]{display: none;}
nav[class="navigation post-navigation"]{display: none;}
div[id="disqus_thread"]{display: none;}
div[class="sharedaddy sd-sharing-enabled"]{display: none;}
footer[class="entry-footer"]{display: none;}
div[class="entry-meta"]{display: none;}
}
