css:
@-moz-document url-prefix("https://lebunker.net/") {
.lebunkerNav {
    height: 60px;
    top: 0;
    right: 0;
    left: 0;
    background-color: #2e2e2e;
    background: #2e2e2e;
  padding: 10px 10px 10px 10px;
}
#mainNav {
    background: #3d5368;
    background: #2e2e2e;
}
.count-online-user {
    font-size: 0.6rem;
    color: white;
    font-family:'Roboto';
    position: fixed;
    right: 10px;
    z-index: 1000;
    top: 0px;
}
.menu {
    font-size: 1rem;
    background-color: #141414;
    text-align: center;
    padding-top: 0.4rem;
    padding-bottom: 0.4rem;
}
.menu a {
    color: #f1f1f1;
    text-decoration: none;
}
body {
    font-family:'Lato';
    background-color: #000;
}
.container-index .forum-list .thead-bunker {
    display: table-header-group !important;
    background-color: #2e2e2e;
}
.container-index .forum-list .post-bg2 {
    background-color: #222222;
}
.container-index .forum-list .post-bg1 {
    background-color: #141414;
}
.container-index .forum-list .post-bg2 a {
    color: #f1f1f1;
    text-decoration: none;
}
.container-index .forum-list .post-bg1 a {
    color: #f4f4f4;
    text-decoration: none;
}
.container-index .forum-list .post-bg1:hover {
    background-color: #000;
}
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    padding: 8px;
    line-height: 1.42857143;
    vertical-align: top;
    border-top: 1px solid transparent;
}
.table tr td, .table tr th {
    border-color: transparent !important;
}
.container-index .forum-list .post-bg2:hover {
    background-color: #000;
}
.post-form {
    background-color: #141414;
    border-radius: 5px;
    padding-left: 0px;
    margin-left: 0px;
    margin-right: 0px;
}
input {
    -webkit-appearance: textfield;
    background-color: #2e2e2e;
    -webkit-rtl-ordering: logical;
    cursor: text;
    padding: 1px;
    border-width: 2px;
    border-style: inset;
    border-color: initial;
    border-image: initial;
}
body {
    margin: 0;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #fff;
    text-align: left;
    background-color: #141414;
}
select {
    -webkit-appearance: menulist;
    box-sizing: border-box;
    align-items: center;
    white-space: pre;
    -webkit-rtl-ordering: logical;
    color: black;
    background-color: white;
    cursor: default;
    border-width: 1px;
    border-style: solid;
    border-color: initial;
    border-image: initial;
}
.footer .network {
    text-align: center;
    padding-top: 2rem;
    padding-bottom: 2rem;
    background-color: #141414;
}
.footer .footer-contact {
    background: #3d5368;
    background: #000;
  padding-bottom: 5rem;
    padding-top: 5rem;
    color: #fff;
}
.post-container {
    font-size: 1em;
    top: 0px;
    background-color: #141414;
    padding-left: 20px;
    padding-right: 20px;
}
.post-container .title-section h3 {
    color: #f5f5f5;
    padding-left: 10px;
    display: inline-block;
}
.breadcrum {
    background: #141414;
  padding: 15px 25px 15px 25px;
    font-family:'Roboto';
    color: white;
    border-radius: .25rem;
}
.card-body-post {
    background-color: #2e2e2e;
}
p {
    color: #fff;
}
.card-header-post {
    background: #000;
    color: white;
}
.card {
    position: relative;
    display: flex;
    flex-direction: column;
    min-width: 0;
    word-wrap: break-word;
    -webkit-box-shadow: -1px 3px 12px 0px rgba(0, 0, 0, 0.75);
    -moz-box-shadow: -1px 3px 12px 0px rgba(0, 0, 0, 0.75);
    box-shadow: -1px 3px 12px 0px rgba(0, 0, 0, 0.75);
    background-color: #fff;
    border: none;
    background-clip: border-box;
    border-radius: 0.25rem;
}
.likeunlike-posts {
    background-color: #141414;
}
.card-header {
    padding: 0.75rem 1.25rem;
    margin-bottom: 0;
    background-color: #141414;
    border-bottom: 1px solid rgba(0, 0, 0, 0.125);
}
.card-body {
    font-family:'Open Sans';
    background-color: #2e2e2e;
}
.likeunlike {
    padding-top: 10px;
    padding-bottom: 10px;
    background-color: #141414;
    padding-left: 10px;
    display: inline;
}
.post-container .info-cm-comment {
    margin-left: 5px;
    display: inline-block;
    color: #f6f6f6;
    font-size: 0.8rem;
}
.card-text-bunker {
    color: red !important;
    font-size: 1em;
}
.picture-container .pictureList .picture {
    padding: 0;
    background-color: #141414;
}
.picture-container .pictureList .picture .picture-info {
    margin-left: 0px;
    z-index: 1000;
    background-color: #141414;
    color: #f5f5f5;
    padding: 2px 6px 2px 6px;
    display: inline-block;
    width: 100%;
}
.picture-container .pictureList .picture header .header-sub-title {
    background-color: #2e2e2e;
    color: white;
    height: 45px;
    text-align: center;
    line-height: 2.5;
    z-index: 1000;
}
.form-btn {
    margin-top: 20px !important;
    background-color: #2e2e2e;
    border: 1px solid #2e2e2e;
    display: inline-block;
    cursor: pointer;
    color: #ffffff;
    font-family:'Roboto';
    padding: 6px 12px;
    text-decoration: none;
    width: 100%;
    font-size: 1rem !important;
    text-align: center;
}
.picture-container .pictureList .picture .picture-info {
    margin-left: 0px;
    margin-top: 15px;
    z-index: 1000;
    background-color: #141414;
    color: #f5f5f5;
    padding: 2px 6px 2px 6px;
    display: inline-block;
    width: 100%;
}
.upl-cat-container .uploadBtn {
    background-color: #141414;
    border: none;
    display: inline-block;
    cursor: pointer;
    color: #ffffff;
    font-family:'Roboto';
    padding: 12px 24px;
    text-decoration: none;
    width: 80%;
    font-size: 1rem;
    text-align: center;
}
.categorie-collapse .categorie-list .categorie-block {
    float: left;
    text-align: center;
    width: 50%;
    background-color: #141414;
    -webkit-box-shadow: inset -1px -7px 14px 0px rgba(0, 0, 0, 0.36);
    -moz-box-shadow: inset -1px -7px 14px 0px rgba(0, 0, 0, 0.36);
    box-shadow: inset -1px -7px 14px 0px rgba(0, 0, 0, 0.36);
    display: inline-block;
    height: 65px;
}
.notification-collapse {
    display: none;
    font-size: 0.7rem;
    font-family:'Open Sans';
    text-transform: none !important;
    position: absolute;
    z-index: 9999;
    height: auto;
    min-height: 60px;
    width: 320px;
    background-color: #141414;
    margin-left: -6px;
}
.notification-collapse .custom-separator {
    width: 50px;
    height: 50px;
    -webkit-transform: translateX(-50%) rotate(45deg);
    transform: translateX(-50%) rotate(45deg);
    margin-left: 36px;
    z-index: 0;
    position: absolute;
    background: #141414;
}
.notification-collapse .list-notif {
    position: relative;
    background-color: #2e2e2e;
    margin-top: 0px;
    cursor: pointer;
}
.mce-content-body div.mce-resizehandle {
    position: absolute;
    border: 1px solid black;
    box-sizing: content-box;
    background: #FFF;
    width: 7px;
    height: 7px;
    z-index: 10000
}

.mce-content-body .mce-resizehandle:hover {
    background: #2e2e2e
}

.mce-content-body img[data-mce-selected],
.mce-content-body hr[data-mce-selected] {
    outline: 1px solid black;
    resize: none
}

.mce-content-body .mce-clonedresizable {
    position: absolute;
    outline: 1px dashed black;
    opacity: .5;
    filter: alpha(opacity=50);
    z-index: 10000
}

.mce-content-body .mce-resize-helper {
    background: #555;
    background: rgba(0, 0, 0, 0.75);
    border-radius: 3px;
    border: 1px;
    color: white;
    display: none;
    font-family: sans-serif;
    font-size: 12px;
    white-space: nowrap;
    line-height: 14px;
    margin: 5px 10px;
    padding: 5px;
    position: absolute;
    z-index: 10001
}

.mce-container, .mce-container *, .mce-widget, .mce-widget *, .mce-reset {
    border: 0 !important;
    background: #2e2e2e;
}
}
