@-moz-document domain("web.whatsapp.com") {
/**
   * Everything you need to know is below:
   * https://github.com/vednoc/onyx
   * https://discord.gg/NpT8PzA
   */
:root {
    --dark: #272C35;
    --darker: #1F232A;
    --light: #D1D1D1;
    --lighter: #E9E9E9;
    --accent: #e7ffec;
    --icon: #E1E1E1;
    --shadow: rgba(0, 0, 0, 0.10);
    --mred: #A3525B;
    --mgreen: #70A352;
    --mblue: #527AA3;
    --hover-delay: 0.8s;
    --image: url(https://media.discordapp.net/attachments/310799529161129984/417408747812552715/zilesanamin.jpg);
    --emojiOpacity: 0.75;
    --version: "WhatsApp by Mew v1.4.8";
    --message: "Updated on 21st of July. Small update to address a few GitHub issues. ";
    --changes: "See the changelog at: https://github.com/vednoc/onyx/releases";
}

/* Body reset. */
body {
    font-family: font_name, 'Segoe UI', 'Helvetica Neue', 'Helvetica', 'Lucida Grande', 'Arial', 'Ubuntu', 'Cantarell', 'Fira Sans', sans-serif;
    background: none;
    background-color: var(--dark);
}

/* Scrollbar track. */
*::-webkit-scrollbar-track {
    background-color: var(--shadow);
}
/* Scrollbar thumb. */
*::-webkit-scrollbar-thumb {
    background-color: var(--accent);
}
* {
    scrollbar-width: thin;
    scrollbar-color: var(--accent) var(--shadow);
}

/* Highlight selection. */
::selection {
    color: var(--light) !important;
    background-color: var(--accent) !important;
}
::-moz-selection {
    color: var(--light) !important;
    background-color: var(--accent) !important;
}
/* Input placeholders. */
::placeholder {
    color: var(--lighter) !important;
}
::-moz-placeholder {
    color: var(--lighter) !important;
}
::-webkit-input-placeholder {
    color: var(--lighter) !important;
}

/* Progress. */
progress[value]::-webkit-progress-bar {
    background: var(--darker);
}
/* Value. */
progress[value]::-webkit-progress-value {
    background: var(--accent);
}
progress {
    background: var(--darker);
}
::-moz-progress-bar {
    background: var(--accent) !important;
}

/* App wrapper. */
#app .app {
    background: none;
    background-color: var(--dark);
}

/* Log-in page. */
.landing-wrapper::before {
    opacity: 0.6;
    background-color: var(--accent) !important;
}
.landing-window {
    border-radius: 4px !important;
    border: 1px solid var(--shadow);
    box-shadow: 0 8px 32px 0 var(--shadow) !important;
    background-color: var(--darker) !important;
}
.landing-main + div {
    background-color: var(--darker) !important;
}
.landing-main + div img:not(:hover) {
    opacity: 0.3;
}
/* Text color. */
.landing-main * {
    color: var(--light);
}
/* Reload QR code. */
._1MOym {
    background-color: var(--darker) !important;
}
._3PxOr rect {
    fill: var(--dark) !important;
}
/* Fix QR code. */
.landing-wrapper div[data-ref] {
    border-radius: 0;
    filter: contrast(150%) !important;
    outline: 4px solid white !important;
}
/* Loader. */
#startup svg > circle {
    stroke: var(--lighter) !important;
}
/* Logo colors. */
span._3PxOr svg path:nth-child(1) {
