css:
@-moz-document url-prefix("http://git.server.com/") {
/*Dark Stuff*/
/*dark-dark*/
.navbar-inner, .repository-header, .search input, .breadcrumb, .active a, .btn, .dropdown-menu li > a:hover, .search-query, .tree thead tr th, .source-header, .CodeMirror-gutter-text, .nav-tabs li a:hover, .network-header, .network-commit-overlay, .source-diff table tbody tr td, .commit-header {
    background-color: #292929 !important;
    background-image: none !important;
    color: #d5d5d5;
}
/*light-dark*/
.repository-body, html, body, .dropdown-menu, .tree tbody tr td, .table-striped tbody td, .network-graph, .source-diff table tbody tr td pre {
    background-color: #2f2f2f !important;
    color: #d5d5d5;
}

/*text*/
/*text general*/
.network-header, .cm-operator, .cm-variable {
    color: #d5d5d5 !important;
    background-image: none !important;
}
/*links*/
a, a:hover, .btn:hover {
    color: #e96001 !important;
}
/*code colors*/
.cm-def {
    color: #6866e5 !important;
}
.cm-string {
    color: #d41616 !important;
}
.cm-builtin {
    color: #ff00f8 !important;
}
.cm-keyword {
    color: #c25df8 !important;
}
.cm-attribute {
    color: #e3e251 !important;
}

/*Fixes*/
.brand, .name , .breadcrumb li a, .btn, .tree thead tr th, .network-header, .commit-header, .source-header, .active {
    text-shadow: none !important;
}
.breadcrumb {
    border: none;
    box-shadow: none;
}
}
