     margin-top: -37px;
}

.head_nums_cont_outer .nums, .head_nums_cont_outer .search_tool_conter{
    margin-left: 60px;
}

@media screen and (min-width: 2561px) {
    #content_left{
    	margin-left: 130px;
	}
}

/*搜索结果&相关搜索*/
.wrapper_new #rs, .wrapper_new .hint_common_restop {
    margin-left: 40px;
}
.c-container,
#rs{
    background-color: rgba(255,255,255,0.9);
    width: 540px;
    padding: 12px 15px 15px 15px;
    border-radius: 15px;
    box-shadow: rgba(0, 0, 0, 0.3) 1px 1px 5px 1px;
    transition: background-color 0.2s;
}
#rs{
    margin-left: 35px;
}
.c-container:hover,
#rs:hover{
    background-color: rgba(255,255,255,0.95);
}
/*c-border*/
.c-border{
    background-color: rgba(244,244,244,0.6);
}
/*宽版容器*/
.container_l{
    width: 100%;
}

#content_right{
    margin-top: 0px !important;
    border-style: none;
}
#content_right table{
    background-color: rgba(248,248,248,0.9) !important;
    padding: 25px 16px 0px 17px;
    border-radius: 0 0 15px 15px;
}
#content_right table tbody tr td table{
    background-color: transparent !important;
    padding: 0;
}
/*跳页*/
.wrapper_new .container_new~#page .page-inner {
    padding: 14px 0 14px 40px;
}
#page{
    background-color: transparent !important;
    margin-left: 35px;
    padding-left: 0px;
    width: 555px;
}
#page a,
#page strong{
    background-color: rgba(255,255,255,0.9) !important;
    height: auto;
    border-radius: 18px;
    border: 1px solid rgb(225, 226, 227);
    box-shadow: rgba(0, 0, 0, 0.3) 1px 1px 2px 0;
}
#page strong{
    border-color: rgb(51, 136, 255);
}
#page a:hover{
    border-color: rgb(51, 136, 255);
}
#page .pc{
    border-style: none !important;
}
/*屏蔽无用元素及广告*/
#page .fk,
#result_logo,
#con-at,
.res_top_banner,/*网络环境被劫持提示（根据需要屏蔽）*/
.hint_right_middle,
.headBlock,
.ec-figcaption,
#foot,
#content_left [class="result c-container"]{/*广告*/
    display: none !important;
}
#content_left [style*="display:block !important;visibility:visible !important"]{/*推广*/
    margin: 0 !important;
    width: 0 !important;
    height: 0 !important;
    overflow: hidden !important;
}
}

@-moz-document regexp(".*^(?!.*(pan|yun)).*.baidu.com(.*)") {
/*滚动条*/
::-webkit-scrollbar{
    margin-right: -10px !important;
    width: 6px;
    height: 6px;
    background-color: #F5F5F5;
}
::-webkit-scrollbar-track{
    -webkit-box-shadow: 0 0 3px rgba(0,0,0,0.3) inset;
    background-color: #F5F5F5;
}
::-webkit-scrollbar-thumb{
    border-radius: 3px;
    -webkit-box-shadow: 0 0 3px rgba(0,0,0,.3) inset;
    background-color: #999;
}
::-webkit-scrollbar-thumb:hover{
    background-color: #777;
}
::-webkit-scrollbar-thumb:active{
    background-color: #555;
}
}

