/* Atualização */

.row {
    margin-left: -15px;
    margin-right: -16px;
}
.container {
    width: 986px;
}
.entry {
    background: #E6E6E6;
    margin-bottom: 15px;
    box-shadow: 0 2px 2px 0 rgba(0,0,0,.14), 0 1px 5px 0 rgba(0,0,0,.12), 0 3px 1px -2px rgba(0,0,0,.2);
}
.pagination>li>a {
    position: relative;
    float: left;
    padding: 6px 12px;
    line-height: 1.42857143;
    text-decoration: none;
    color: #337ab7;
    background-color: #0f0f0f;
    border: 1px solid #0a0a0a;
    margin-left: 1px;
}
blockquote {
    margin: 1em -0px 1.5em -0px;
    padding: 1em;
    color: #666;
    background: #e6e6e6;
    font-style: italic;
    border: 2px solid #0a0a0a;
}
.single-post div.post {
    border-bottom: 3px solid #0a0a0a;
}
.profile-card-centered {
    text-align: left;
    position: relative;
    border-bottom: 0px;
}
.profile-image img {
    border-radius: 50%;
    border: 3px solid #f4f4f4;
    box-sizing: border-box;
}

/* Disqus */

.entry, #comment-wrap {
    border-radius: 3px;
    box-shadow: 0px 50px 0px rgb(11, 11, 11) !important;
}
#comment-wrap {
    padding: 1px;
    background: #0b0b0b;
    margin-bottom: 15px;
}
