css:
@-moz-document domain("opennet.ru") {
html {
  font-size: 16px;
}
BODY {
	background-color: #fff;
	max-width: 1100px;
}

BODY > div {
  padding: 0 !important;
}

/* news item */
table.thdr2 {
	background: transparent;
	border-left: 0;
	border-top: 0;
	padding-left: 0;
}
/* article main */
table.ttxt2 {
	margin-bottom: 5px;
	background: transparent;
	border-bottom: 0;
	border-left: 0;
	text-align: left;
}

/* article text */
.chtext {
	background: transparent;
}
.chtext img {
  border-width: 0 !important;
}
/* article share */
table.ttxt2 tbody tr:nth-child(2) {
	display: none;
}
/* article similar */
table.ttxt2 tbody tr:nth-child(3) {
	display: none;
}
table.ttxt2 tbody tr:nth-child(4) {
	display: none;
}
table.ttxt2 tbody tr:nth-child(5) {
	display: none;
}
table.ttxt2 tbody tr:nth-child(6) {
	display: none;
}
table.ttxt2 tbody tr:nth-child(7) {
	display: none;
}
#lenta_nav {
	display: none;
}
/* comments header */
table.thdr {
	display: none;
}

/* article comment */
td.ctxt {
	display: none;
}

/* post comments form */
table.ttxt2 form {
	display: none;
}

/* sidebar blocks */
#as1 table.tlist {
	background: #f7f7f7;
	border-bottom: 0;
	border-left: 0;
}
#as1 .ttxt,
#as1 .t_lenta_wrapper .t_lenta {
	background: #f7f7f7;
	border-bottom: 0;
	border-left: 0;
}
.t_lenta_wrapper {
	border-bottom: 0;
	border-left: 0;
}
/* header login */
body > form > aside > div {
	display: none;
}
body > form > aside > table > tbody > tr > td {
	background: transparent !important;
}

.ttxt select {
  font-size: 14px !important;
  width: 100% !important;
  background-color: #fff !important;
  color: #222 !important;
}

table.ttxt, table.tlist {
  background: #fff;
  border-left: 0;
}
table.ttxt tr, table.tlist tr {
  background: #fff;
}
td.tdelim {
  padding: 0;
  height: 0;
  font-size: 0;
  background: transparent;
}
td.i {
  padding: 0;
  border-left: 0;
  text-indent: 0;
}

table.tlist tr td, td.tlist {
  font: 1rem verdana, arial;
}

#tabs_top {
  background-color: transparent;
  border-left: 0;
  margin-bottom: 1rem;
  margin-top: 5px;
}

div.tab, div.tab-selected {
  float: left;
  color: #222;
  font: normal 1rem Arial, sans-serif;
  text-decoration: none;
  text-align: center;
  border-right: 0;
  border-top: 0;
  border-bottom: 0;
  width: auto;
  padding-top: 5px;
  padding-bottom: 5px;
  padding-left: 5px;
  padding-right: 5px;
  background-color: transparent;
  cursor: pointer;
}

div.tab-null {
  font: normal 1rem Verdana, sans-serif;
  border-right: 0;
  border-top: 0;
  border-bottom: 0;
  padding-top: 0;
  padding-bottom: 0;
  background-color: transparent;
}

td.tmenu {
  padding: 3px 1px 3px 1px;
  font: 1rem verdana,arial;
  color: #707088;
}
}
