css:
@-moz-document domain("wikipedia.org") {
:root {

    /* https://github.com/chriskempson/tomorrow-theme#tomorrow-night */
    --codecolor-def-background: #1d1f21;
    --codecolor-def-foreground: #c5c8c6;
    --codecolor-def-comment:    #969896;
    --codecolor-def-red:        #cc6666;
    --codecolor-def-orange:     #de935f;
    --codecolor-def-yellow:     #f0c674;
    --codecolor-def-green:      #b5bd68;
    --codecolor-def-aqua:       #8abeb7;
    --codecolor-def-blue:       #81a2be;
    --codecolor-def-purple:     #b294bb;

    --codecolor-background:       var(--codecolor-def-background);
    --codecolor-foreground:       var(--codecolor-def-foreground);
    --codecolor-comment:          var(--codecolor-def-comment);
    --codecolor-punctuation:      var(--codecolor-def-foreground);
    --codecolor-operator:         var(--codecolor-def-aqua);
    --codecolor-keyword:          var(--codecolor-def-purple);
    --codecolor-keyword-operator: var(--codecolor-def-purple);
    --codecolor-keyword-type:     var(--codecolor-def-purple);
    --codecolor-keyword-constant: var(--codecolor-def-orange);
    --codecolor-classname:        var(--codecolor-def-yellow);
    --codecolor-name:             var(--codecolor-def-yellow);
    --codecolor-name-member:      var(--codecolor-def-foreground);
    --codecolor-instance-var:     var(--codecolor-def-red);
    --codecolor-special-name:     var(--codecolor-def-red);
    --codecolor-decorator:        var(--codecolor-def-red);
    --codecolor-symbol:           var(--codecolor-def-green);
    --codecolor-string:           var(--codecolor-def-green);
    --codecolor-string-escape:    var(--codecolor-def-orange);
    --codecolor-string-interp:    var(--codecolor-def-orange);
    --codecolor-number:           var(--codecolor-def-orange);
    --codecolor-function:         var(--codecolor-def-blue);
    --codecolor-special-funcdecl: var(--codecolor-def-blue);
    --codecolor-error:            var(--codecolor-def-green);
    --codecolor-preprocessor:     var(--codecolor-def-red);
    --codecolor-preprocessor-val: var(--codecolor-def-green);
    --codecolor-console-prompt:   var(--codecolor-def-comment);
    --codecolor-console-output:   var(--codecolor-def-blue);
}

.mw-highlight,
pre {
    font-family: 'Fira Mono', Consolas, monospace;
    background: var(--codecolor-background) !important;
    color: var(--codecolor-foreground) !important;
}
.mw-highlight * {
    font-weight: normal !important;
}
.mw-highlight span.c,
.mw-highlight span.c1,
.mw-highlight span.cm,
.mw-highlight span.sd {
    color: var(--codecolor-comment);
}
.mw-highlight span.k,
.mw-highlight span.kd,
.mw-highlight span.kr,
.mw-highlight span.kn {
    color: var(--codecolor-keyword);
}
.mw-highlight span.ow {
    color: var(--codecolor-keyword-operator);
}
.mw-highlight span.kt {
    color: var(--codecolor-keyword-type);
}
.mw-highlight span.kc,
.mw-highlight span.kp {
    color: var(--codecolor-keyword-constant);
}
.mw-highlight span.nc {
    color: var(--codecolor-classname);
}
.mw-highlight span.nn {
    color: var(--codecolor-foreground);
}
.mw-highlight span.nv,
.mw-highlight span.no,
.mw-highlight span.ni {
    color: var(--codecolor-name);
}
.mw-highlight span.vi {
    color: var(--codecolor-instance-var);
}
.mw-highlight span.na {
    color: var(--codecolor-name-member);
}
.mw-highlight span.nd {
    color: var(--codecolor-decorator);
}
.mw-highlight span.p {
    color: var(--codecolor-punctuation);
}
.mw-highlight span.nb,
.mw-highlight span.bp,
.mw-highlight span.vm {
    color: var(--codecolor-special-name);
}
.mw-highlight span.fm {
    color: var(--codecolor-special-function);
}
.mw-highlight span.nf {
    color: var(--codecolor-function);
}
.mw-highlight span.o {
    color: var(--codecolor-operator);
}
.mw-highlight span.ne,
.mw-highlight span.nl {
    color: var(--codecolor-error);
}
.mw-highlight span.ss {
    color: var(--codecolor-symbol);
}
.mw-highlight span.s,
.mw-highlight span.s1,
.mw-highlight span.s2 {
    color: var(--codecolor-string);
}
.mw-highlight span.se {
    color: var(--codecolor-string-escape);
}
.mw-highlight span.si {
    color: var(--codecolor-string-interp);
}
.mw-highlight span.mi {
    color: var(--codecolor-number);
}
.mw-highlight span.gp {
    color: var(--codecolor-console-prompt);
}
.mw-highlight span.go {
    color: var(--codecolor-console-output);
}
.mw-highlight span.cp {
    color: var(--codecolor-preprocessor);
}
.mw-highlight span.cpf {
    color: var(--codecolor-preprocessor-val);
}
}
