css:
@-moz-document url-prefix("https://ed.team/"), domain("ed.team") {
:root {
    --color-body-bg: hsl(0, 0%, 5%);
    --color-border: hsl(180, 5%, 10%);
    --deep-black: hsl(0, 0%, 0%);
    --text-color: hsl(0, 0%, 100%);;
}

@media screen and (min-width: 1024px) {
  [class*=scroll],
  .scroll,
  body {
    --thumb-color: var(--dark-color);
    --thumb-color-hover: #b8baba;
    --track-color: var(--color-body-bg);
    --scroll-radius: calc(var(--scroll-size) / 2);
    --display: block; 
    }
}

h1,
h2,
h3,
h4,
h5,
.t1,
.t2,
.t3,
.t4,
.t5,
.dark-color, 
.color-dark {
    color: var(--color-first);
}
.blog-article__title a{
   color: var(--text-color);
}

.article-header {
    background-color: var(--color-body-bg);
}

.main-header {
    background-color: var(--color-body-bg);
}

.card,
.card__data {
    background-color: var(--color-body-bg)
}

.s-shadow-bottom,
.card:hover,
.featured-publication__item:hover .lg-shadow-bottom {
    box-shadow: 0 0.5rem 1rem -0.5rem #2f2f2f;
}

.s-bg-white {
    background-color: var(--deep-black);
}

.publication:not(.publication--main):not(.publication--fixed) {
    background-color: var(--deep-black);
}

.create-publication,
.foro-sidebar,
.agenda,
.s-bg-grey,
.featured-publication__item,
.comment,
.form-editor,
[type="text"], [type="email"], 
[type="password"], 
[type="number"], 
[type="date"], 
[type="search"], 
[type="tel"], 
select, 
textarea{
    background-color: var(--deep-black);
}
}
