css:
@-moz-document domain("snakify.org") {
body {
    color: #aaa;
    background-color: #222;
}

#MathJax_Message {
    background-color: #222;
    border: 1px solid #666;
    color: #aaa;
}

html,
body {
    color: #aaa;
}

/* sidebar */
.navbar {
    background: linear-gradient(to right, #0c0c0c 0%, #0e6341 99%);
    box-shadow: 0 0 1px -1px rgba(34, 226, 124, 0.6), 0 0 2px -2px rgba(34, 226, 124, 0.3);
}

.sidebar {
    background-color: #111 !important;
    border-right: 1px solid #666;
}

.nav-sidebar {
    background: #111;
}

.nav-sidebar > .active > a,
.nav-sidebar > .active > a:hover,
.nav-sidebar > .active > a:focus {
    color: #90ffff;
    background-color: #1c3245;
}

.lesson_sections .problems_link li a.active {
    background: #2b3c4b;
    color: #ccc;
}

.lesson_sections li a:hover,
.lesson_sections li a:focus {
    background: rgba(190, 255, 255, 0.1);
}

/* main area */
.nav-pills > li.active > a,
.nav-pills > li.active > a:focus,
.nav-pills > li.active > a:hover {
    color: #d89400;
    background-color: #111;
}

.nav > li > a:focus,
.nav > li > a:hover {
    background-color: #333;
}

.btn-primary {
    color: #d89400;
    background-color: #111;
    border-color: #666;
}

.btn-primary:hover {
    color: #d89400;
    background-color: #222;
    border-color: #666;
}

.btn-default {
    color: #aaa;
    background-color: #333;
    border-color: #666;
}

.btn-default:hover {
    color: #aaa;
    background-color: #555;
    border-color: #666;
}

hr {
    border-top: 1px solid #444;
}

code {
    color: #d6a800;
    background-color: #111;
}

.problem {
    color: #ffffff !important;
    background-color: #00542d !important;
}

element {
    background-color: #222;
    box-shadow: 0 0 1px 0px #0cb500, 0 0 2px 0px #12FF01;
}

.snakify-facts {
    background-color: #333;
}

.snakify-alumni-card {
    background-color: #333;
}

button.snakify-button-start {
    background-color: #0d4527;
    color: #bdffb5;
}

button.snakify-button-start {
    background-color: #0d4527;
    color: #bdffb5;
}

.table > thead > tr > th {
    border-bottom: 1px solid #666;
}

.table > tbody > tr > td,
.table > tbody > tr > th,
.table > tfoot > tr > td,
.table > tfoot > tr > th,
.table > thead > tr > td,
.table > thead > tr > th {
    border-top: 1px solid #666;
}

table {
    border-color: #555;
}

td,
th {
    padding: 6px;
    border-color: #555;
}

/* visualizer */
.visualizer_code_toppanel,
.visualizer_code_bottompanel {
    background: #333;
}

.visualizer_code_toppanel button,
.visualizer_code_bottompanel button {
    background: #333 !important;
}

pre {
    display: block;
    padding: 9.5px;
    margin: 0 0 10px;
    font-size: 13px;
    line-height: 1.42857143;
    color: #aaa;
    word-break: break-all;
    word-wrap: break-word;
    background-color: #111;
    border: 1px solid #666;
    border-radius: 4px;
}

.ace-tm {
    background-color: #222;
    color: #ccc;
}

.ace-tm .ace_gutter {
    background: #111;
    color: #505050;
}
.ace-tm .ace_gutter-active-line {
    background-color: #222;
}

.ace-tm .ace_storage,
.ace-tm .ace_keyword {
    color: #ffc600;
}

.ace-tm .ace_support.ace_function {
    color: rgb(103, 213, 0);
}

.ace-tm .ace_constant.ace_numeric {
    color: rgb(0, 198, 205);
}

.ace-tm .ace_keyword.ace_operator {
    color: rgb(255, 76, 17);
}

.ace-tm .ace_string {
    color: rgb(110, 178, 255);
}

.ace-tm .ace_comment {
    color: rgb(108, 149, 133);
}

.ace-tm .ace_marker-layer .ace_selection {
    background: #111;
}

/* visualizer 2 */
button,
input,
optgroup,
select,
textarea {
    margin: 0;
    font: inherit;
    color: inherit;
    background-color: #333;
}

.ace-tomorrow {
    background-color: #222;
    color: #ccc;
}

.ace-tomorrow .ace_gutter {
    background: #111;
    color: #666;
}
.ace-tomorrow .ace_gutter-active-line {
    background-color: #2f2f2f;
}

.ace-tomorrow .ace_marker-layer .ace_selection {
    background: #111;
}

.intro {
    background-color: #111;
}

.ace_active-line {
    background: #333 !important;
}


/* footer */
footer {
    background: #111;
    border-top: 1px solid #444;
    padding: 1em;
    font-size: 75%;
}
}
