css:
@-moz-document url-prefix("http://www.neopets.com/") {
#ban {
    display:none;
}
.sidebar {
    background-color:#454753;
}
.activepetinfo {
    background-color:#1b1c1f;
    color:white;
}
.neofriend {
    background-color:#1b1c1f;
    color:white;
}
.activePetInfo TD {
    background-color:transparent;
    color:white;
}
.activepet {
    background-color:#1b1c1f;
    border:1px solid #2f3138;
}
a {
    color:red;
}
.contentModuleContent {
    background-color:#2f3138;
}
.content {
    background-color:#2f3138;
    color:#fff!important;
}
body {
    background-color:#2f3138;
    color:white;
}
text {
    color:white;
}
table {
    color:white;
    background-color:transparent;
}
a:link {
    color:#6899F4;
}
