    color:#6899F4;
}
table .inventory td {
    color:white;
    border:1px solid #2f3138;
    border-radius:10px;
}
.sidebarTable {
    border: 0 solid #fff;
}
.sidebarmodule {
    border:0;
}
.mid-content table {
    color:black;
}
.content tr td {
    background-color:transparent!important;
}
.content tr td div {
    background-color:transparent!important;
}
.content tr {
    background-color:transparent!important;
}
#gr-main {
    background-color:white!important;
    color:black;
}
