    color:black;
}
#gr-header {
    background-color:white!important;
    color:black;
}
#gr-category-list {
    background-color:white!important;
    color:black;
}
#gr-ctp {
    background-color:white!important;
    color:black
}
.desc_inner {
    color:black;
}
.content {
    color:black;
}
.randomEvent {
    color:black!important;
}
.stock-table {
    color:black;
}
.frame {
    border:1px solid #000!important;
}
.contentmodule {
    border:1px solid #000;
}
#prizePopup {
    color:#000!important;
}
#prizesContainer {
    color:#000;
}
