css:
@-moz-document regexp("https?://user.qzone.qq.com/(.*)") {

.background-container {

    background:url("https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1556431839308&di=dfdb457dcc0a370bda212ab1f8aa7f90&imgtype=0&src=http%3A%2F%2Fb-ssl.duitang.com%2Fuploads%2Fitem%2F201802%2F22%2F20180222145241_WaWKe.jpeg") !important;

    background-size: contain !important;

    background-position:center;

    /*background-size: cover right !important;*/

    /* background-size:100% 100%;*/

}

.-single, .s-s {

    border-radius: 10px;

}

#feed_friend_list {

    border-radius: 10px;

}

li, .fn-feed-control-v2, .mod-side-nav-message {

    border-radius:20px;

    opacity:0.27;

    color:black;

    font-size:16px;

}

li {

    display: inline-block;

}

.mod-side-nav-message {

    position:fixed;

    right:15px;

    width:150px;

}

li:hover {

    opacity:0.98;

}

.layout-head .head-info, .layout-head .head-description a, .layout-head .head-name .user-name, .layout-head .head-detail-name .user-name, .layout-head .qz-progress-bar .progress-bar-info {

    color:#fff;

}

.fn-feed-control-v2:hover {

    opacity:0.98;

}

.mod-side-nav:hover {

    opacity:0.98;

}

.qz-grid, .more-toggle, .more-toggle-hide {

    border-radius:20px !important;

    opacity:0.7;

}

/*屏蔽*/

#tb_vip_li, .vip-setting, .usic-container, .ead-info, .head-title, .ead-description, .description-text, .ead-detail-info, .earfix, #QM_Mood_Poster_Container, .user-vip-info, .head-detail-info, .layout-nav-inner, .head-info, .head-detail, .layout-head, .layout-nav, .col-main-sidebar, #site_hot_btn, .hot-msg, .aMyFriends, .mod-side-nav-recently-used {

    display: none !important;

}
}
