/*-------------------------------------
-------------- CREDITs ---------------
---------------------------------------
Tons of CSS for basic dark template lifted from:
---------------------------------------
IGN - Dark Red/Blue Theme
by Mboxer511
https://userstyles.org/styles/153560/ign-dark-red-blue-theme
-------------------------
IGN - Minimal Dark
by Dante Miyamoto
https://userstyles.org/styles/166896/ign-minimal-dark
--------------------------------------*/

@-moz-document domain("ign.com") {
	/* Header */

	#header-container.spine:after {
		background: #1a1a1a;
	}

	#header-container #ign-logo {
		background: #1e1e1e !important;
	}

	#header-container #ign-logo:after {
		background: url(http://oystatic.ignimgs.com/src/core/img/widgets/global/page/header/global-header-sprite.png)
			#1e1e1e 15px -104px no-repeat !important;
	}

	#header-container #browse-button {
		background: #1e1e1e
			url(http://oystatic.ignimgs.com/src/core/img/widgets/global/page/header/global-header-sprite.png)
			97px 13px no-repeat !important;
	}

	#header-container #browse-social {
		background-color: #1e1e1e;
	}

	#header-container #search-input {
		background-color: #202020 !important;
		border: 1px solid #323232 !important;
		color: #acacac !important;
	}

	#header-container .search-container {
		background-color: #1a1a1a;
	}

	#header-container #search-button {
		-webkit-filter: invert(80%);
		filter: invert(80%);
	}

	#ign-shows.ign-shows,
	#header-container .popular-links a,
	#header-container .user-tools.logged-out .ignHeader-authentication a,
	#global-comments .user-tools.logged-out .ignHeader-authentication a,
	#spine-container.horizontal .title {
		color: #00adee !important;
	}

	#spine-container.horizontal .trending .title:after {
		background-color: #2e2e2e;
	}

	#header-nav.fixed:after {
		background: #1a1a1a;
	}

	/* Footer */

	#ignFooter-container,
	.ignFooter-topRow,
	.ignFooter-bottomRow {
		background: #1a1a1a;
	}

	#ignFooter-container,
	#ignFooter-container p,
	#ignFooter-container a,
	#ignFooter-container a:link,
	#ignFooter-container a:visited {
		color: #9b9b9b;
	}

	/* Deals */

	.bottomItem:nth-child(2),
	.interruptsContainer.interruptFull,
	#livefyre-comment .fyre-help,
	.side_medrec,
	.chess,
	article > .ign-ad-300x250,
	.ad-placement.ad-leaderboard,
	.taboola-container,
	.ign-ad-s300x250,
	aside.ign-ad.ign-ad-300x250.no-hero.has-ad {
		display: none;
	}

	/* Login/Register Page */

	.ignsignin-mainContainer {
		background-color: #111;
		border-top: 5px solid #00adee;
		color: #a0a0a0;
	}

	.ignsignin-changeForm {
		background-color: #111111;
	}

	.regHeader {
		background: #111111 !important;
	}

	/* Base */

	ul.contentNav {
		border-top: 1px solid #000;
		background: #161616;
	}

	.contentNav li a:hover,
	.contentNav li.active a {
		background: #313131;
	}

	.article-headline {
		color: #474748;
	}

	body {
		background-color: #161616;
		color: #c5c5c5;
	}

	.shell {
		background: #1c1c1c;
		-webkit-box-shadow: none !important;
		box-shadow: none !important;
	}

	.evo-coverStories .fuseNav a .text {
		border-left: 1px solid #2e2e2e;
	}

	.evo-coverStories .fuseNav .fuse {
		background-color: #2e2e2e;
	}

	.evo-coverStories a {
		color: #c5c5c5;
	}

	.cvr-highlights {
		background: #242424;
	}

	.evo-coverStories .bottom-bumper {
		background-color: #242424;
	}

	.followBox-header {
		color: #c5c5c5;
	}

	.extra-storyItem .story-url,
	.extra-storyItem .story-url:visited,
	.extra-storyItem .spotlight-title {
		color: #c5c5c5;
	}

	#promo_breakingnews_img_container img {
		border-radius: 4px;
	}

	.blogrollContainer .ign-blogrollMainFilters a.selected {
		color: #c5c5c5;
		font-size: 13px;
		border: 1px solid #262626;
		border-bottom: 1px solid #262626;
	}

	.ign-blogrollMainFilters {
		border-bottom: 1px solid #262626;
	}

	.blogrollContainer a:link,
	.blogrollContainer a:visited {
		color: #00adee;
	}

	.ign-blogrollFilters li a.selected {
		color: #c5c5c5;
	}

	.blogrollContainer {
		color: #4c4c4c;
	}

	.listElmnt {
		border-bottom: 1px dashed #262626;
	}

	.interruptFixBottom {
		background-color: #1c1c1c;
		border-top: 1px solid #1c1c1c;
	}

	#blogrollInterruptDeals {
		background-color: #1c1c1c;
		border: solid #262626;
		border-width: 1px 0;
	}

	#blogrollInterruptDeals .listElmnt-blogItem {
		border-bottom: 1px dashed #262626;
	}

	.trendingwikis-widget {
		background-color: #1c1c1c;
	}

	.trendingwikis-row {
		border-bottom: 1px dashed #262626;
	}

	.trendingwikis-row:first-child {
		border-top: 1px dashed #262626;
	}

	.blogrollContainer button {
		background-color: #161616;
		background: -moz-linear-gradient(top, #212121 0, #161616 100%);
		background: -webkit-gradient(
			linear,
			left top,
			left bottom,
			color-stop(0%, #212121),
			color-stop(100%, #161616)
		);
	}

	.right-col-module {
		background-color: #1c1c1c;
	}

	.subHeaderSectionContainer {
		border-top: none !important;
		background: none !important;
	}

	.topgames-module .first {
		background: #262626 !important;
	}

	.topgames-module .column-game {
		border-bottom: 1px dotted #262626;
		background: #161616;
	}

	.followBox {
		display: none;
	}

	.topgames-module .game-title {
		font-size: 11px;
		color: #00adee;
	}

	.topgames-module .column-game .rating {
		color: #fff;
		background: #00adee;
	}

	.topgames-module .view-all-lnk {
		color: #808080;
		background-color: #161616;
		background-image: none !important;
	}

