		> div#browse-button {
		background: #00adee
			url(http://oystatic.ignimgs.com/src/core/img/widgets/global/page/header/global-header-sprite.png)
			6px 7px no-repeat !important;
	}

	section.ign-grid.ign-grid-stripe.clear-fix
		> header#ign-header
		> div#header-container.no-spine
		> div#header-nav.no-spine.watchread
		> div.browse
		> div.search-container {
		background-color: transparent;
	}

	#header-container #header-nav {
		background-color: #1a1a1a;
	}

	.ign-grid {
		background: #1a1a1a;
	}

	.article-publish-date {
		color: #6f6f6f;
	}

	article.review .article-headline strong {
		color: #3a3a3a;
	}

	.ign-content .article-share-count-container .icon-sharrow {
		background-color: #3888c9;
	}

	#ign-shows.ign-shows:hover .ign-shows-label {
		color: #00adee;
	}

