css:
@-moz-document domain("canvas.eee.uci.edu"), domain("login.uci.edu") {
body {
    background-image: url(https://cdn.kulopo.com/pic/bg.png);
    background-position: center bottom;
    background-size: cover;
    background-attachment: fixed;
    background-repeat: no-repeat;
}
.skin_normal .wrap1 {
    background-color: rgba(0, 0, 0, 0);
}
html{
	background: rgba(0, 0, 0, 0);
}
.with-right-side #right-side-wrapper{
	display: none;
}
.section {
    background: rgba(245,245,245,0.5);
}
}

@-moz-document url-prefix("https://www.ics.uci.edu/~thornton"), url-prefix("http://www.ics.uci.edu/~thornton") {
body {
    width:960px;
    margin:0 auto;
    text-align:left
}
body {
    background-image: url(https://cdn.kulopo.com/pic/bg.png);
    background-position: center bottom;
    background-size: cover;
    background-attachment: fixed;
    background-repeat: no-repeat;
}

}

@-moz-document regexp("https?://www.ics.uci.edu/~pattis/.+\\.txt") {
@media screen and (min-width: 600px) {
    body{
        margin: 0 -webkit-calc(30vw) auto;
        width: 50vw;
        font-size: 16;
    }
}
}
