additional_info:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.4channel.org")
{
  form table tbody>tr:nth-of-type(2) td[align='right'],
#footer,
.recaptchatable #recaptcha_reload_btn,
.recaptchatable #recaptcha_whatsthis_btn,
.recaptchatable #recaptcha_switch_audio_btn,
.recaptchatable #recaptcha_switch_img_btn,
.recaptchatable td:last-of-type,
.rules,
hr,
th
  {
    display: none;
  }

  html, body
  {
    background: url(http://i.imgur.com/8r4TO.png)bottom right no-repeat fixed, url bottom right no-repeat fixed, url(http://i.imgur.com/zFp0L.png) fixed !important;
  }

  .postblock
  {
    border: 1px #8d8377 solid !important;
    background: #fef0d2 !important;
    -moz-border-radius: 5px !important;
  }

  .op
  {
    padding-top: 20px !important;
    max-width: 60% !important;
  }

  .postarea
  {
/*[[padisplay]]*/
/*[[classicpost]]*/;
  }

  #header
  {
    position: fixed !important;
    top: 0px !important;
    border-bottom: 3px #8d8377 solid !important;
    border-right: 3px #8d8377 solid !important;
    border-left: 3px #8d8377 solid !important;
    background: #fef0d2 !important;
    padding: 0 5px !important;
    -moz-border-radius-bottomright: 10px !important;
    -moz-border-radius-bottomleft: 10px !important;
    border-bottom-right-radius: 10px !important;
    border-bottom-left-radius: 10px !important;
    z-index: 1 !important;
  }

  #header span
  {
    display: inline !important;
  }

  a
  {
    color: #6f695D !important;
  }

  #imgControls
  {
    background: url(http://i.imgur.com/NJzLd.png), #fef0d2 !important;
    border: 3px #8d8377 solid !important;
    position: fixed !important;
    right: -130px !important;
    bottom: 130px !important;
    color: #8D8375 !important;
    padding: 1px 5px !important;
    width: 210px !important;
    -moz-border-radius-topleft: 10px !important;
    -moz-border-radius-bottomleft: 10px !important;
    border-top-left-radius: 10px !important;
    border-bottom-left-radius: 10px !important;
  }

  #imgControls:hover
  {
    right: 0px !important;
  }

  a:hover
  {
    color: #000 !important;
  }

  .commentpostername
  {
    color: #8D8375 !important;
  }

  .filetitle
  {
    color: #000 !important;
  }

  .reply:not(dialog), td.replyhl
  {
    margin: 10px 0 10px  !important;
    background: url(http://i.imgur.com/NJzLd.png) fixed, #fef0d2 !important;
    border: 3px #8d8377 solid !important;
    height: auto !important;
    color: #8D8375 !important;
    font-size: 12pt !important;
    -moz-border-radius: 10px !important;
    border-radius: 10px !important;
/*[[expadding]]*/;
  }

  td.replyhl
  {
    border: 6px #8d8377 solid !important;
  }

  #updater
  {
    background: #fef0d2 !important;
    height: 20px !important;
    padding: 0 5px !important;
    -moz-border-radius: 0px !important;
    border-radius: 10px !important;
    -moz-border-radius-topleft: 10px !important;
    -moz-border-radius-bottomleft: 10px !important;
    border-top-left-radius: 10px !important;
    border-bottom-left-radius: 10px !important;
  }

  #updater:hover
  {
    height: 180px !important;
  }

  .op .postername
  {
    color: #8D8375 !important;
  }

  .postertrip
  {
    color: #8D8375 !important;
    padding: 0 5px 0 0 !important;
  }

  .deletebuttons
  {
    background: url(http://i.imgur.com/NJzLd.png), #fef0d2 !important;
    border: 3px #8d8377 solid !important;
    position: fixed !important;
    right: -360px !important;
    bottom: 162px !important;
    color: #8D8375 !important;
    padding: 0 5px !important;
    -moz-border-radius-topleft: 10px !important;
    -moz-border-radius-bottomleft: 10px !important;
    border-top-left-radius: 10px !important;
    border-bottom-left-radius: 10px !important;
  }

  .deletebuttons:hover
  {
    right: 0px !important;
  }

  .inputtext
  {
    background: #fff8e9 !important;
  }

  .pages
  {
    position: relative !important;
    left: 36% !important;
    bottom: -22px !important;
    background: url(http://i.imgur.com/NJzLd.png), #fef0d2 !important;
    border: 3px #8d8377 solid !important;
    height: auto !important;
    color: #8D8375 !important;
    font-size: 12pt !important;
    -moz-border-radius: 10px !important;
    border-radius: 10px !important;
    padding: 2px 5px 10px 5px !important;
  }

  [class="pagelist desktop"]
  {
    background: url(http://i.imgur.com/NJzLd.png), #fef0d2 !important;
    border: 3px #8d8377 solid !important;
    height: auto !important;
    color: #8D8375 !important;
    font-size: 12pt !important;
    -moz-border-radius: 10px !important;
    border-radius: 10px !important;
    padding: 2px 5px 10px 5px !important;
  }

  table
  {
    margin: 3px 0 0 0 !important;
  }

  .logo
  {
/*[[logoname]]*/;
  }
}

