css:
@-moz-document url-prefix("https://finobe.com/") {
.navbar-light.bg-faded {
 background: #fff;
 background: linear-gradient(90deg, #fff, #fff);
 border-bottom: 0;
}

.nav-scroller.navbar-light.bg-faded {
 background: #7474bf;
 background: linear-gradient(90deg, #ffffff, #ffffff);
}

.navbar-light.bg-faded {
 background-color: #fff;
 border-bottom: 1px solid #e6e6e6;
 border-radius: 0;
 box-shadow: 0 0 black;
 box-shadow: 0px 0.2px black;
}

.nav-scroller-inner > a.nav-link,
.navbar-light .navbar-nav .active > .nav-link,
.navbar-light .navbar-nav .nav-link,
.navbar-light .navbar-nav .nav-link.active,
.navbar-light .navbar-nav .nav-link.show,
.navbar-light .navbar-nav .show > .nav-link {
 color: hsla(0, 0%, 0%, 0.75);
}

.nav-scroller-inner > a.nav-link:hover,
.navbar-light .navbar-brand,
.navbar-light .navbar-brand:focus,
.navbar-light .navbar-brand:hover,
.navbar-light .navbar-nav .nav-link:focus,
.navbar-light .navbar-nav .nav-link:hover {
 color: hsla(100, 0%, 0%, 0.85);
}

.nav-scroller.navbar-light.bg-faded {
 background: #fff;
 background: linear-gradient(90deg, #fff, #fff);
 box-shadow: 0 0.2px black;
}

.navbar-brand img {
 content: url("https://finobe.com/img/BUSY.png");
 width: 25px;
 height: 25px;
 vertical-align: middle;
}

.btn-success {
 color: #fff;
 background-color: #039be5;
 border-color: #039be5;
}
}

@-moz-document url-prefix("https://finobe.com/forum/hom") {
.forum-section-info {
 width: 100%;
 background-color: #ffffff00;
}
}
