css:
@-moz-document domain("privatebin.net") {
body {
    background-color: #655E57;
    font-size: 12px;
    font-family: monospace;
    line-height: 1.3;
    color: #B58989;
}
img {
    height: 20px;
    width: 20px;
    margin: 0px;
    padding: 0px 0px;
}
.form-control {
    background-color: #655E57;
    color: #ffe2c3;
    border: 0px solid #ccc;
    font-size: 12px;
    line-height: 1.3;
    padding: 10px 8px;
    height: 12px;
    box-shadow: none;
}
.nav {
    background-color: rgb(65, 5e, 57);
    font-size: 12px;
    font-family: monospace;
    line-height: 1.5;
    color: #B58989;
    margin: 0px;
    padding: 0px 0px;
}
.nav-tabs > li > a, .nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav > li > a:hover, .nav > li > a:focus {
    color: #B58989;
    background-color: #655E57;
    border: 0px solid #655E57;
    cursor: pointer;
}
.nav-tabs > li.active > a:hover, .nav-tabs > li > a:hover {
    color: #B58989;
    background-color: #655E57;
    border: 0px solid #655E57;
    cursor: pointer;
}
.nav > li > a {
    padding: 2px 10px;
}
li {
    background-color: #655E57;
    color: #ffe2c3;
    border: 0x solid #655E57;
    margin: 0px;
    padding: 0px 0px;
}
.navbar-nav > li > a {
    text-shadow: 0 0px 0 rgba(255,255,255,.25);
    margin: 0px;
    padding: 5px 6px;
}
.navbar-default .navbar-nav > li > a, .navbar-default .navbar-nav > li > a:focus, .navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:focus {
    background-image: linear-gradient(to bottom,#655E57 0,#655E57 200%);
    background-color: #655E57;
    color: #B58989;
}
.navbar-default .navbar-nav > li > a:hover {
    background-color: #655E57;
    color: #B58989;
}
.navbar-nav > li > .dropdown-menu, .dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover, .dropdown-menu > li > a {
    background-image: linear-gradient(to bottom,#655E57 0,#655E57 200%);
    background-color: #655E57;
    color: #ffe2c3;
    margin: 0px;
    padding: 2px 6px;
    box-shadow: none;
}
.nav-tabs {
    margin: 0px;
    padding: 1px 0px;
    border: 0px;
    box-shadow: none;
}
.navbar-btn, .btn-primary:focus, .btn-primary:hover, .btn-default:focus, .btn-default:hover {
    background-color: #655E57;
    color: #ffe2c3;
    font-size: 12px;
    line-height: 1.3;
    margin: 6px;
    border: 0px solid transparent;
    padding: 2px 6px;
    text-shadow: 0 0px 0 rgba(255,255,255,.25);
    box-shadow: none;
    border-radius: 0px;
    background-image: linear-gradient(to bottom,#655E57 0,#655E57 120%);
}
.navbar-text {
    line-height: 1.5;
    height: 10px;
    margin: 8px;
    padding: 0px 0px;
}
.navbar-default {
    border-color: #655E57;
    box-shadow: none;
}
.navbar-default .navbar-text {
    color: #B58989;
}
div {
    background-color: #655E57;
    margin: 0px;
    padding: 0px 0px;
    border: 0x solid #655E57;
}
span {
    line-height: 2.08;
    margin: 0px;
    padding: 0px 0px;
}
input {
    font-size: 12px;
    line-height: 1.3;
    margin: 0px;
    border: 0px solid transparent;
    padding: 0px 0px;
}
pre {
    background-color: #655E57;
    color: #ffe2c3;
    border: 0px;
    font-size: 12px;
    margin: 0px;
    padding: 12px 0px;
}
a {
    background-color: #655E57;
    color: #ffe2c3;
}
h4, h4 small {
    color: #B58989;
    font-size: 12px;
}
}
