css:
@-moz-document domain("bangumi.tv") {
.day {
    overflow: auto;
}
.today {
    background: #FFFFaa;
}

.tinyMode a.epBtnUnknown:link
, .tinyMode a.epBtnUnknown:active
, .tinyMode a.epBtnUnknown:hover
, .tinyMode a.epBtnUnknown:visited
, a.epBtnUnknown:link
, a.epBtnUnknown:active
, a.epBtnUnknown:hover
, a.epBtnUnknown:visited
{
    background-color: #9d00ff42;
    color: #2f0080;
    outline: 1px solid #c290ee00;
    border-color: #9932cd29;
}

.tinyMode a.epBtnAir:link
, .tinyMode a.epBtnAir:active
, .tinyMode a.epBtnAir:hover
, .tinyMode a.epBtnAir:visited
, a.epBtnAir:link
, a.epBtnAir:active
, a.epBtnAir:hover
, a.epBtnAir:visited
{
    background-color: #00ff007a;
    color: green;
    outline: 1px solid #90ee9045;
    border-color: #32cd32;
}

.tinyMode a.epBtnAirNewDay2:link
, .tinyMode a.epBtnAirNewDay2:active
, .tinyMode a.epBtnAirNewDay2:hover
, .tinyMode a.epBtnAirNewDay2:visited
, a.epBtnAirNewDay2:link
, a.epBtnAirNewDay2:active
, a.epBtnAirNewDay2:hover
, a.epBtnAirNewDay2:visited
{
    background: #beffbd;
    color: limegreen;
    outline: 0;
    border-color: lightgreen;
}

.tinyMode a.epBtnAirNewDay1:link
, .tinyMode a.epBtnAirNewDay1:active
, .tinyMode a.epBtnAirNewDay1:hover
, .tinyMode a.epBtnAirNewDay1:visited
, a.epBtnAirNewDay1:link
, a.epBtnAirNewDay1:active
, a.epBtnAirNewDay1:hover
, a.epBtnAirNewDay1:visited
{
    background: white;
    color: limegreen;
    outline: 0;
    border-color: lightgreen;
}

.tinyMode a.epBtnToday:link
, .tinyMode a.epBtnToday:active
, .tinyMode a.epBtnToday:hover
, .tinyMode a.epBtnToday:visited
, a.epBtnToday:link
, a.epBtnToday:active
, a.epBtnToday:hover
, a.epBtnToday:visited
{
    background: white;
    border-color: gray;
    color: black;
}


}
