css:
@-moz-document domain("web.hellotalk.com") {
/****** Main page ******/

.mainChat {
  background-color: #222;
}

/**** Left column ****/

/* Profile box */
.selfProfile-box[data-v-4de239fe] {
  background-color: #222;
}

.headPortrait-box > span[data-v-4de239fe] {
  color: #aaa;
}

/* Search box */
.userSearch-box[data-v-4de239fe] {
  background-color: #222;
}

.userSearch-box .placehold-box[data-v-4de239fe] {
  background-color: #282828;
}

/* Space below Search */
.userList-box[data-v-4de239fe] {
  background-color: #222;
}

.mouseSelect[data-v-5303a51f]:hover {
  background-color: #333;
}

/* Selected user */
.mouseSelect.select[data-v-5303a51f] {
  background-color: #333;
}

.userName[data-v-5303a51f] {
  color: #aaa;
}

.userInfo-box[data-v-5303a51f] {
  border-bottom-color: #333;
}


/**** Right column ****/

/* 'No chats selected' */
.placeholdMsgSelectionChild[data-v-25555d86] {
  opacity: 0.3;
}

.placeholdMsgSelection[data-v-25555d86] {
  color: #aaa;
}

/* User name box */
.mainMsgBoxHead[data-v-52eee3f0] {
  background-color: #282828;
}

.username[data-v-52eee3f0] {
  color: #999;
}

/*** Chat Box ***/

.mainMsgSelection[data-v-25555d86] {
  background-color: #222;
}

/* My text - without translate */
.sendBox .textWrapper[data-v-295bd8e2] {
  background-color: #333;
}

.textWrapper pre[data-v-295bd8e2] {
  color: #aaa;
}

/* My text - with translate */
.sendBox .translateWrapper[data-v-14eba4f5] {
  background-color: #333;
}

.sendBox .translateWrapper i[data-v-14eba4f5] {
  background-color: #666;
}

.translateWrapper pre[data-v-14eba4f5] {
  color: #aaa;
}

/* Other user text - without translate */
.textWrapper[data-v-295bd8e2] {
  background-color: #252a2e;
  border-color: #333;
}

/* translate icon */
.receiveBox .trans-icon[data-v-295bd8e2] {
  filter: invert(80%);
}

/* Other user text - with translate */
.translateWrapper[data-v-14eba4f5] {
  background-color: #252a2e;
}

.translateWrapper pre[data-v-14eba4f5] {
  color: #aaa;
}

.translateWrapper i[data-v-14eba4f5] {
  background-color: #666;
}


/* 'Reply', 'Correction', 'Translate' Dropdown */
.el-dropdown-menu {
  background-color: #222;
  border-color: #333;
}

.el-dropdown-menu__item {
  color: #aaa;
}

.el-dropdown-menu__item--divided {
  border-top-color: #333;
}

.el-dropdown-menu__item--divided::before {
  background-color: #222;
}

.el-popper[x-placement^="bottom"] .popper__arrow {
  border-bottom-color: #333;
}

.el-popper[x-placement^="bottom"] .popper__arrow::after {
  border-bottom-color: #222;
}

.el-dropdown-menu__item:focus, .el-dropdown-menu__item:not(.is-disabled):hover {
  background-color: #333;
}

/** Correction Box **/

/* My Text */

.sendBox .correctionWrapper[data-v-9dffd5de] {
  background-color: #333;
  color: #aaa;
}

/* Other user Text */

.correctionWrapper[data-v-9dffd5de] {
  background-color: #252a2e;
  border-color: #333;
}

.correctionWrapper p[data-v-9dffd5de] {
  color: #aaa;
}

/** Reply Box (with original msg above) **/

/* My Text */

.sender.replayWrapper[data-v-0f9ca6cf] {
  background-color: #333;
  border-bottom-color: #666;
}

pre[data-v-3e56e77c] {
  color: #aaa;
}

/* Other user Text */

.replayWrapper[data-v-0f9ca6cf] {
  background-color: #252a2e;
  border-bottom-color: #666;
}

/** Audio Messages **/

/* My audio */
.sendBox .voiceWrapper[data-v-97f6bcde] {
  background-color: #333;
}

/* Other user audio */
.voiceWrapper[data-v-97f6bcde] {
  background-color: #252a2e;
}

.voiceTime[data-v-97f6bcde] {
  color: #999;
}

/*** Correct sentences Pop-up Dialog ***/

.el-dialog {
  background-color: #282828;
}

.el-dialog__title {
  color: #aaa;
}

p[data-v-469285ae] {
  color: #aaa;
}

.mainCorrectionInput[data-v-469285ae] {
  color: #446CCF; /* #6E7F80; */
}

.correctionItem[data-v-469285ae] {
  border-bottom-color: #666;
}

.mainCorrectionBox[data-v-2519be76] {
  border-top-color: #666;
  border-bottom-color: #666;
}

/* Box containing correction */
.correctionItem .correctionWrapper[data-v-9dffd5de] {
  background-color: #282828 !important;
}

.el-dialog__body {
  color: #aaa;
}

/* 'Notes' box */
textarea[data-v-2519be76] {
  border-color: #666;
  color: #aaa;
}

.el-checkbox__inner {
  background-color: #222;
  border-color: #666;
}


/** Writing Box **/
.mainMsgInput[data-v-0a6729a4] {
  background-color: #282828;
}

.editWrapper[data-v-0a6729a4] {
  background-color: #282828;
  color: #aaa;
}

/** Emoji Pop-up Pane **/

.emojiPicker[data-v-2cd8b4e8] {
  background-color: #282828;
}

/* Navigation bar at bottom */
.expressionNavBar[data-v-2cd8b4e8] {
  background-color: #282828;
}

.expressionNavBar ul li.activeBar[data-v-2cd8b4e8] {
  background-color: #333;
}

.expressionWrapper > ul[data-v-2cd8b4e8]::after {
  background-color: #444;
}

/** Reply Pop-up Pane **/

.sendReplyPreview[data-v-73dd203e] {
  background-color: #333;
  border-bottom-color: #666;
}

.sendReplyPreviewWrapper p[data-v-73dd203e], .sendReplyPreviewWrapper span[data-v-73dd203e] {
  color: #aaa;
}

/*** Messages ***/

.el-message {
  border-color: #666;
}

.el-message--error {
  background-color: #333;
  border-color: #666;
}

.el-message--warning {
  background-color: #333;
  border-color: #666;
}

.el-message--success {
  background-color: #333;
  border-color: #666;
}

#login_box[data-v-27fbaf8c] {
  background-color: #222;
}

/**** 'Chat Settings' sliding column ****/

.mianProfile[data-v-1dedf9c7] {
  border-left-color: #666;
}

.mainPersonProfile[data-v-6e7f7f89] {
  background-color: #222;
}

/* Header */
.profileHead[data-v-6e7f7f89] {
  background-color: #222;
  border-bottom-color: #666;
}

.btnGroup h3[data-v-6e7f7f89] {
  color: #aaa;
}

.learnBox li > span[data-v-6e7f7f89] {
  color: #aaa;
}

.learnBox > i[data-v-6e7f7f89] {
  color: #aaa;
}

.learnDepth[data-v-6e7f7f89] {
  background-color: #333;
}

/* Middle region */
.userinfoBox[data-v-6e7f7f89] {
  background-color: #222;
  border-bottom-color: #666;
}

.username > span[data-v-6e7f7f89] {
  color: #aaa;
}

.selfIntroduce[data-v-6e7f7f89] {
  background-color: #222;
}

.selfIntroduce p[data-v-6e7f7f89], .selfIntroduce h3[data-v-6e7f7f89] {
  color: #aaa;
}

.noticeBtn[data-v-6e7f7f89] {
  background-color: #222;
  border-top-color: #222;
}

.noticeBtn span[data-v-6e7f7f89] {
  color: #aaa;
}

/* Footer */
.controlBtnGroup[data-v-6e7f7f89] {
  background-color: #222;
}

.controlBtnGroup ul li[data-v-6e7f7f89]::after {
  background-color: #666;
}


/****** QR Code Scan page ******/

#login_box[data-v-bcc8ef22] {
  background-color: #222;
}
}
