css:
@namespace url(http://www.w3.org/1999/xhtml);

/*text&background*/
 html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer {
    color: white !important;
    background: #191919 !important;
}
select, button {
    background: #191919 !important;
}
div, span, a {
    background-color: #191919 !important;
}
select, input, span, code, textarea, pre, label, div, dd {
    color: #ffffff !important;
}
/*links*/
 a {
    color: #3296c8 !important;
}
a:visited {
    color: #9558d6 !important;
}
/*highlight*/
 textarea, pre, input, code {
    background: #191919 !important;
}
