css:
@-moz-document url-prefix("http://www.roblox.com/"), url-prefix("https://www.roblox.com/"), url-prefix("https://forum.roblox.com/"), url-prefix("http://forum.roblox.com/"), url-prefix("http://web.roblox.com/"), url-prefix("https://web.roblox.com/")
{
  html
  {
    background-color: rgba(0, 0, 0, 0.7);
  }

  body, .content
  {
    background: transparent !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("https://cdn.discordapp.com/attachments/559197490327453718/572730242595160064/ezgif-2-c1d66691f3c4.png") !important;
 /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  .game-main-content, .section #horizontal-tabs, .section, .game-instances, .developer-store, .container-list, #horizontal-tabs, .rbx-item-card, .rbx-btn-control-sm, #Body, .tab-content, .tab-container+div>div, #mid-column .StandardBox, .GroupWallPane .AlternatingItemTemplateOdd, .GroupWallPane, .SquareTabGray, .AjaxCommentsContainer .Post, .AjaxCommentsContainer .PostBottom, .container-footer, .SmallCatalogItemView:hover .CatalogItemInner, .SmallCatalogItemView:hover .CatalogItemInner, .input, #keywordTextbox, .alert-info
  {
    background-color: rgba(255, 255, 255, 0.60) !important;
  }

  .badge-row .badge-data-container p, .section-row, .nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus, .rbx-text-overflow, .rbx-item-card .rbx-caption p, div#testingSitePanelWrapper, #BodyWrapper, body, #content, .profile-avatar-left, .tab-content-container, .rbx-para-overflow, .GroupWallPane .AlternatingItemTemplateEven, #left-column .StandardBox, #SearchControls, .GroupControlsBox, .SmallCatalogItemView, .StandardPanelContainer .StandardPanelWhite, .SquareTabGray.selected, .SquareTabGray.selected:hover, .SquareTabGray.ui-tabs-selected, .SquareTabGray.ui-tabs-selected:hover, .SquareTabGray.selected a:hover, .SquareTabGray.ui-tabs-selected a:hover, .SquareTabGray a:hover, .CatalogItemInner, .CatalogItemInner:hover, #vorlias-nav, a:hover, #categoriesForKeyword, .btn-control.btn-control-large, #SortMain, #SortAggregation, .rbx-tab .rbx-tab-heading
  {
    background-color: rgba(255, 255, 255, 0) !important;
  }

  .badge-row .badge-data-container p:after, .rbx-text-overflow:after, .rbx-item-card .rbx-caption p:after, #Footer.footer-container, .rbx-para-overflow:after, .AjaxCommentsContainer .Post, .AjaxCommentsContainer .PostBottom, .WhiteSquareTabsContainer, .slide-switcher .profile-slide-item .profile-slide-item-right .slide-item-description.games:after, .slide-switcher .profile-slide-item .profile-slide-item-right .slide-item-stats .list-item .slide-item-my-rank.games:after, .slide-switcher .profile-slide-item .profile-slide-item-right h2.games:after, .slide-switcher .profile-slide-item .profile-slide-item-right .slide-item-stats .list-item .slide-item-my-rank.groups:after, .slide-switcher .profile-slide-item .profile-slide-item-right .slide-item-description.groups:after, .slide-switcher .profile-slide-item .profile-slide-item-right h2.groups:after, .card .card-item .card-text-notes:after, .blog-news .news .news-link:after, .GroupListImageContainer img
  {
    background-image: linear-gradient(to right,rgba(255,255,255,0) 0,#fff 1000%) !important;
  }

  .rbx-header
  {
    background-color: rgba(0, 0, 0, 0.7) !important;
  }

  #navigation
  {
    background-color: rgba(0, 0, 0, 0.7) !important;
    padding: 5px !important;
  }

  .vorlias-nav-link
  {
    font-weight: 400 !important;
  }

  .favorite-button-container span, .game-calls-to-action .voting-panel .upvote span, .game-calls-to-action .voting-panel .downvote span, .rbx-link, .rbx-link-sm, .rbx-link-xs, .vlist .list-item .list-body .list-content a, .rbx-link:link, .rbx-link-sm:link, .rbx-link-xs:link, .vlist .list-item .list-body .list-content a:link, .rbx-link:visited, .rbx-link-sm:visited, .rbx-link-xs:visited, .vlist .list-item .list-body .list-content a:visited, .rbx-link:active, .rbx-link-sm:active, .rbx-link-xs:active, .vlist .list-item .list-body .list-content a:active, .game-main-content .game-calls-to-action .game-creator span, #build-page .aside-text, #build-page .totals-label, #build-page .details-table td.item-date, .GroupOwner, .MyRank, .rbx-text-notes, .stat-label, #build-page a.place-inactive, span.inactive-image, #build-page a.place-inactive:hover, .slide-switcher .profile-slide-item .profile-slide-item-right .slide-item-stats .list-item .slide-item-stat-title, #GroupTitle div, .messageDivider.read .wrappedText span, span.messageDate.read a, .text-footer, .text-footer-nav, .text-footer-nav:link, .text-footer-nav:visited, .text-footer-nav:active, .text-link, .vlist .list-item .list-body .list-content a, .text-link:link, .vlist .list-item .list-body .list-content a:link, .text-link:visited, .vlist .list-item .list-body .list-content a:visited, .text-link:active, .vlist .list-item .list-body .list-content a:active, div#game-context-menu
  {
    color: #000000 !important;
  }

  .rbx-link:hover, .rbx-link-sm:hover, .rbx-link-xs:hover, .vlist .list-item .list-body .list-content a:hover, .rbx-link:focus, .rbx-link-sm:focus, .rbx-link-xs:focus, .vlist .list-item .list-body .list-content a:focus
  {
    color: #00a2ff !important;
    text-decoration: none;
  }

  .rbx-btn-primary-lg, .btn-primary-lg
  {
    background-color: rgba(0, 0, 0, 0.4) !important;
    color: #FFFFFF !important;
  }

  .rbx-btn-primary-lg:hover, .rbx-btn-primary-lg:focus
  {
    background-color: rgba(0, 0, 0, 0.7) !important;
  }

  .img_ad,.ad-annotations,#AdvertisingLeaderboard,.ad,.ad *,#google_image_div,#aw0, .leaderboard-abp, .Ads_WideSkyscraper, .ads-pane, .profile-ads-container, .messages-ad-skyscraper, #BrowseContainer > div:nth-child(4)
  {
    display: block !important;
  }

  .home-header-content h1 a, .container-list h3, h2, h1, h1, h1 a, h1 a:visited, h1 a:active, h1 a:link, #wrap > div.container-main > div > div.row.page-content.ng-scope > div.rbx-tabs-horizontal > div.friends-content > h3, .content .pager li>span, #ctl00_ctl00_LabelAnnouncement > span > font > span, #ctl00_LabelAnnouncement > span > font > span, #wrap > div.container-main > div.alert-info > span > font > span, .alert-info a:link, .alert-info a:visited, .alert-info a:hover, .alert-info a:active, .alert-info .alert-link:link, .alert-info .alert-link:visited, .alert-info .alert-link:hover, .alert-info .alert-link:active
  {
    text-shadow: -1px -1px 0 #000,
    1px -1px 0 #000,
    -1px 1px 0 #000,
    1px 1px 0 #000;
    color: white !important;
  }

  #ctl00_ctl00_LabelAnnouncement > span > font > span, #ctl00_LabelAnnouncement > span > font > span, #wrap > div.container-main > div.alert-info > span > font > span, .alert-info a:link, .alert-info a:visited, .alert-info a:hover, .alert-info a:active, .alert-info .alert-link:link, .alert-info .alert-link:visited, .alert-info .alert-link:hover, .alert-info .alert-link:active
  {
    font-size: 20px;
  }

  .footer-note:after, .Legalese:after
  {
    visibility: visible !important;
    position: absolute !important;
    font-weight: bold !important;
    content: " Theme created by DrKaric." !important;
  }

  #navigation > ul > li.rbx-lead:before
  {
    display: block;
    content: "Theme by DrKaric";
    color: #191919;
  }

  #StudioWidget, #CommunityWidget, #my-recommended-games > ul > li:nth-child(6)
  {
    display: none;
  }

  span.vorlias-notification
  {
    background: rgba(255, 0, 8, 0.43) !important;
  }

  .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading, .rbx-tabs-horizontal .rbx-tab-heading:hover, .rbx-tabs-horizontal .rbx-tab-heading:focus, .rbx-tabs-horizontal .rbx-tab-heading:active
  {
    box-shadow: 0 -4px 0 0 rgba(0, 0, 0, 0.7) inset;
  }

  .rbx-btn-secondary-sm:hover, .rbx-btn-secondary-xs:hover, .chat-main .chat-header, .dialog-container .dialog-header, .dialog-container .dialog-message-container .dialog-triangle:before, .dialog-container .dialog-message-container .dialog-message
  {
    background-color: rgba(0, 0, 0, 0.7);
  }

  .rbx-btn-secondary-sm, .rbx-btn-secondary-xs
  {
    background-color: rgba(0, 0, 0, 0.7);
    border-color: rgba(0, 0, 0, 0.7);
  }

  .btn-neutral, .btn-neutral:link, .btn-neutral:active, .btn-neutral:visited
  {
    background-image: url(https://cdn140.picsart.com/267058625005201.jpg?r1024x1024) !important;
    background-color: #0F0 !important;
    border-color: rgb(11, 161, 0) !important;
  }

  #wrap > div.container-main > div > div.row.page-content.ng-scope > h1, #game-detail-page, #BodyWrapper, .profile-container, .container-main
  {
    padding-top: 40px;
  }

  .profile-accoutrements-container .profile-accoutrements-slider .accoutrement-item a, .accoutrement-image, .profile-avatar-right, .accoutrement-item, #groups-switcher, .slide-switcher.groups, .slide-switcher.games,
.slide-switcher .profile-slide-item .profile-slide-item-right.games, .slide-switcher .profile-slide-item .profile-slide-item-right .slide-item-stats .list-item .slide-item-my-rank.games, .slide-switcher .profile-slide-item .profile-slide-item-right h2.games, .slide-switcher .profile-slide-item .profile-slide-item-right .slide-item-description.groups, .slide-switcher .profile-slide-item .profile-slide-item-right.groups, .slide-switcher .profile-slide-item .profile-slide-item-right .slide-item-stats .list-item .slide-item-my-rank.groups, .slide-switcher .profile-slide-item .profile-slide-item-right h2.groups, .slide-switcher .profile-slide-item .profile-slide-item-right .slide-item-description.games, .profile-avatar-right .profile-avatar-mask, .card .card-item .card-text-notes, .GroupListImageContainer img, .dialog-container .dialog-message-container .dialog-triangle:before, .table-header th, gear-button-wrapper, .gear-button-wrapper, #build-page .separator,.separator, #build-page .gear-button-wrapper, .blog-news .news .news-link, .game .game-item .rbx-votes .vote-bar .voting-container .mask .segment, .btn-control.btn-control-large, .select, .roblox-message-large-btn
  {
    background: none !important;
  }

  *
  {
    border: none !important;
  }

  .alert-info
  {
    margin-top: 40px !important;
  }

  .avatar .avatar-card-link, .avatar .avatar-card-image, .avatar-card-fullbody .avatar-card-link, .avatar-card-fullbody .avatar-card-image, #GroupsPeoplePane_Clan .roblox-avatar-image img, .avatar.card-plain, .avatar-card-container.card-plain
  {
    border-radius: 0% !important;
  }

  .avatar-card-container, .avatar:hover, .avatar-card-container:hover, .avatar
  {
    background: none !important;
    box-shadow: none !important;
  }

  .col-xs-12
  {
    width: 102.7% !important;
  }
    /* Remove ads from the loading place screen for NBC users - credit to EpikYummeh for base code.*/
  #videoPrerollPanel
  {
    background-color: rgba(255, 255, 255, 0.60) !important;
    height: auto !important;
    border: none !important;
  }

  #videoPrerollPanel *
  {
    color: white !important;
  }

  #videoPrerollLoadingDiv
  {
    padding-top: 0px !important;
    padding-bottom: 17px;
  }

  #videoPrerollLoadingBar
  {
    border-color: #050505 !important;
  }

  #videoPrerollLoadingBarCompleted
  {
    background-color: rgba(255, 255, 255, 0.60) !important;
  }

  #videoPrerollTitleDiv, #videoPrerollMainDiv, #videoPrerollTitleData,
        #videoPrerollJoinBC, #videoPrerollCompanionAd
  {
    display: none !important;
    color: white !important;
  }
}
