css:
@-moz-document domain("app.nlziet.nl") {
.video-page__wrapper {
    max-width: none !important;
    
}
.video-page__player-wrapper, .video-page__details-wrapper {
    width:100% !important;
    float: none !important;   
}

.video-page__player-details-wrapper {
    height: auto !important;
}

/* aspect ratio hack */
.video-page__player-details-wrapper:before {
    content: "";
    display: block;
    width:100%;
    padding-top: 56.25%;
}

.video-page__player-wrapper {
    position: absolute !important;
    top:0;
    left:0;
}

.video-page__player-details-container {
    margin-top: 0 !important;
}
}
