css:
@-moz-document domain("w3schools.com") {

/* --------- BACKGROUNDS --------- */
 body, #page, .left, #leftcolumn a, #topnavTut, .lamp td, .boxDemo, .example, #menu a, .w3-white, .top, .w3-example .w3-code, table.browserref th, table.browserref tr:nth-child(odd), .w3-table-all tr:nth-child(odd), .w3-code, #textareaCode, .textareawrapper {
    background-color: #222 !important;
}
#topnav, .container {
    background: #1d1d1d !important;
}
.w3-light-grey, .left, table.browserref tr:nth-child(even), table.browserref tr.fixzebra, .w3-table-all tr:nth-child(even) {
    background-color:#191919 !important;
    color:#FFF;
}
.w3-example {
    background-color:#161616 !important;
    color:#FFF;
}
/* ------------ TEXT ------------ */
 body, .left, .lamp td, .boxDemo, .example, .w3-white, .top, .w3-example .w3-code, .w3-example, .w3-btn {
    color:#FFF !important;
}
.pythoncolor {
    color:#FFF !important;
}
.w3-light-grey, h2 {
    color:#FFF;
}
a {
    color:#FFF;
}
p {
    color:#FFF;
}
/* ---------- BORDERS ---------- */
 hr {
    border-color: #000 !important;
}
table, th, td, .w3-bordered tr, .w3-table-all tr {
    border-color: #121212 !important;
}
.w3-border {
    border-bottom-color: #121212 !important;
    border-top-color: #121212 !important;
    border-right-color: #121212 !important;
}
}
