css:
@-moz-document domain("bilibili.com")
{
  :root
  {
    --m-bgimg: url(https://i.loli.net/2019/05/01/5cc9954c4e5fd.jpg);
    --m-bgcolor: #2a2a2e;
    /*[[useMask]]*/
    --m-darkest: rgba(0, 0, 0, .85);
    --m-darkest-solid: rgb(0, 0, 0);
    --m-dark: rgba(34, 34, 34, .85);
    --m-dark-solid: rgb(34, 34, 34);
    --m-gray: rgba(60, 60, 60, .85);
    --m-gray-solid: rgb(60, 60, 60);
    --m-gray2: rgba(130, 130, 130, .7);
    --m-gray2-solid: rgb(130, 130, 130);
    --accent-blue: #f26dc8;
    --accent-pink: #f25d8e;
    --accent-yellow: #faa63e;
    --accent-blue-light: hsla(195, 100%, 42%,.6);
    --accent-pink-light: hsla(340, 85%, 66%,.6);
    --accent-yellow-light: hsla(33, 95%, 61%,.6);
    /*鼠标滑过容器边框大小*/
    --content-border-size: 2px;
    /*鼠标滑过容器边框色*/
    --content-border-hover: #ff6030;
    /*字体颜色*/
    --m-font-color: #fdfdfd;
    /*字体副色*/
    --m-font-subcolor:#d361dd;
    --m-font-subcolor2: #f271d6;
    --content-radius: /*[[setContentRadius]]*/px;
    --shadow-dark-color: rgba(0, 0, 0, .5);
    --shadow-color: rgba(0, 0, 0, .3);
    --shadow15: 0 4px 15px -2px var(--shadow-dark-color);
    --shadow40: 0 8px 40px var(--shadow-dark-color);
    --shadow60: 0 20px 60px var(--shadow-dark-color);
    --shadow80: 0 20px 80px var(--shadow-dark-color);
  }

@keyframes showInfo
  {
    0% {
        opacity: 0;
    transform: translate(0, 40%);
  }
}

@keyframes shock
{
  0%
  {
    transform: translate(-10%, 0px);
  }

  25%
  {
    transform: translate(10%, 0px);
  }

  50%
  {
    transform: translate(0px, 10%);
  }

  75%
  {
    transform: translate(0px, -10%);
  }
}

@keyframes showVideo
{
  from
  {
    opacity: 0;
    transform: scale(.8);
  }
}

@keyframes floatDown
{
  from
  {
    opacity: 0;
    margin-top: -20%;
  }

  30%
  {
    opacity: 0;
  }
}

@keyframes floatUp
{
  from
  {
    opacity: 0;
    margin-top: 50%;
  }

  10%
  {
    opacity: 0;
  }
} 
/***************滚动条调整****************/
.ps__rail-y,
.mCSB_draggerRail,
.ps__scrollbar-y-rail
{
  background-color: #b2304e!important;
}

::-webkit-scrollbar,
.custom-scrollbar::-webkit-scrollbar
{
  width: 6px;
  height: 6px;
}

::-webkit-scrollbar-track-piece,
::-webkit-scrollbar-corner,
.custom-scrollbar::-webkit-scrollbar-track-piece
{
  
  background-color:  #b2304e;

}

::-webkit-scrollbar-thumb,
.custom-scrollbar::-webkit-scrollbar-thumb
{
  background-color: hsla(0, 0%, 100%, .7);
  border-radius: 10px;
  border: none;
}

::-webkit-scrollbar-thumb:hover,
::-webkit-scrollbar-thumb:active
{
  background-color: hsla(0, 0%, 100%, .85);
  width:8px;
  -webkit-transition:width 0.25s;
}
}

@-moz-document domain("search.bilibili.com"), domain("space.bilibili.com"), domain("message.bilibili.com"), regexp("^https?://www\\.bilibili\\.com/?((index\\.html)|(\\?spm_id_from.*))?"), regexp("^https?://www\\.bilibili\\.com/(v/)?(?!game/match|audio|blackboard|read|mango|documentary/discovery|blackroom|bangumi/(media|big)|medialist|tag|topic|newlist|video/online).*"), regexp("^https?://t\\.bilibili\\.com/?(\\?tab=\\d*)?"), regexp("^https?://elec\\.bilibili\\.com/plugin_v3/elec-plugin.html.*")
{
/***************全局****************/
  html,
.bili-header-m > .nav-menu
  {
    background-image: url(https://i.loli.net/2019/05/01/5cc9954c4e5fd.jpg)!important;
    background-attachment: fixed!important;
    background-size: cover!important;
    background-repeat: no-repeat!important;
    background-position: center!important;
  }

  html,
.bili-header-m > .nav-menu
  {
    background-image: url(https://i.loli.net/2019/05/01/5cc9954c4e5fd.jpg)!important;
    background-color:transparent!important;
  }

/*背景遮罩*/
  body
  {
    background-color: var(--m-mask-color)!important;
  }

  .bili-wrapper,
.bangumi-index-wrapper,
.main-inner,
.cinema-home-wrapper,
.container-body,
.cinema-documentary-wrapper
  {
    width: 1230px!important;
  }

@media screen and (max-width: 1400px)
  {
    .bili-wrapper,
    .cinema-home-wrapper,
    .bangumi-index-wrapper,
    .main-inner,
    .container-body,
    .cinema-documentary-wrapper {
        width: 1045px!important;
  }
}

#app,
.bili-header-m
{
  background-color: transparent;
}

.nav-con .mobile,
#fixed_app_download,
#elevator-mobile-app,
.gif-menu,
.mobile-link-l
{
  display: none!important;
}

.t,
.txt,
.tit,
.title,
span.info,
.item,
.info,
.text,
.detail a,
.operation,
h3,
h4.name,
.social a,
.user-name a,
.headline .name,
.title .name,
.info .user .name:not([class*=vip]),
a.username:not([class*=vip]),
.name,
.ri-title,
.idc-uname,
.tag-list a:hover,
.batch,
.video-desc:not(:hover),
.user-count,
.item-title,
.more:not(:hover),
.video-item-biref:not(:hover) .biref-title-line2,
.video-item-biref:not(:hover) .biref-title,
.season-title,
.bangumi-title,
.filter-title,
.filter-item:not(.on),
.sort-item:not(.on) span,
.block-title,
.b-head .b-head-t,
.footer a
{
  transition: border .25s, color .25s!important;
  color: var(--m-font-color)!important;
}

.state,
.tag-list a,
.type,
.bangumi-label,
.uname span,
.pub-info,
.filter-name,
span.name
{
  color: var(--m-font-subcolor)!important;
}

.desc,
.user-name,
span.username:not(.is-vip),
.item-a:not(:hover),
a.name:not([class*=vip]),
.view-more
{
  transition: .25s;
  color: var(--m-font-subcolor2)!important;
}

.is-vip,
.vip,
.vip-red-name,
.this-is-vip
{
  color: var(--accent-pink)!important;
}

a.title:hover,
a.t:hover,
.detail a:hover,
.user-name a:hover,
span.username:not(.is-vip):hover,
.user-name:hover,
.headline a.name:hover,
.more:hover,
.operation:hover,
.spread-module:hover p.t,
.rank-item:hover .ri-title,
.u-info .name .username:hover,
.uname,
.bangumi-item:hover .bangumi-title,
.footer a:hover,
.season-title:hover
{
  transition: color .25s!important;
  color: var(--accent-blue)!important;
}


/*----------------------
        网页顶部
------------------------*/
.nav-mask
{
  background-color: rgba(0, 0, 0, .6) !important;
}

.bili-header-m > .nav-menu
{
  box-shadow: 0 10px 40px var(--shadow-color);
}

.bili-header-m .nav-menu .nav-con .nav-item
{
  transition: .25s;
}

.bili-header-m .nav-menu .nav-con .nav-item a.t
{
  color: var(--m-font-color)!important;
}

.bili-header-m .nav-menu .nav-con .nav-item .i-frame
{
  background: transparent!important;
  filter: drop-shadow(0 15px 20px rgba(0, 0, 0, .8));
}

.nav-con .dd-bubble,
.bili-header-m .nav-menu .up-load .up-nav/*投稿*/
,
.bili-header-m .i_menu_login/*登录*/
{
  border: none;
  background: var(--m-darkest)!important;
  transition: .25s;
  box-shadow: var(--shadow60)!important;
}

/*未登录状态的个人介绍*/
.bili-header-m .i_menu_login .reg
{
  color: var(--m-font-color);
}

.bili-header-m .i_menu_login .tip
{
  color: var(--m-font-subcolor)!important;
}

/*个人介绍*/
.bili-header-m .profile-info .i-face .face
{
  border: none!important;
}

.bili-header-m .profile-m.dd-bubble a,
.bili-header-m .profile-m.dd-bubble .header-u-info,
.bili-header-m .profile-m.dd-bubble .member-menu a,
.bili-header-m .profile-m.dd-bubble .member-bottom a
{
  color: var(--m-font-color);
  transition: .25s;
}

.bili-header-m .profile-m.dd-bubble .member-menu
{
  border-color: var(--m-gray2-solid);
}

.bili-header-m .profile-m .btns-profile .ver .tips
{
  border: none;
  background: var(--m-darkest);
  color: var(--m-font-color);
}

  
.bili-header-m .profile-m .btns-profile .num-tip,
.bili-header-m .profile-m .grade .bar
{
  background: var(--m-darkest-solid)!important;
}

.header-u-info .grade .desc-tips
{
  background: var(--m-darkest)!important;
}

.header-u-info .grade .desc-tips div
{
  color: var(--m-font-subcolor);
}

.header-u-info .grade .desc-tips div strong
{
  color: var(--m-font-color)!important;
}

/*调整个人中心，投稿管理，直播中心，b币钱包，订单中心*/
.nav-con .profile-m.dd-bubble .member-menu a
{
  transition: .25s!important;
}

.nav-con .profile-m.dd-bubble .member-menu a:hover
{
  filter: drop-shadow(0 8px 10px var(--accent-blue));
  color: var(--accent-blue)!important;
}

.nav-con .profile-m.dd-bubble .member-menu a:not(:hover) i
{
  filter: brightness(200%)!important;
}

.nav-con .profile-m.dd-bubble .member-bottom
{
  background: var(--m-gray)!important;
}

#i_menu_become_vip
{
  background: var(--m-darkest);
}

#i_menu_become_vip .recommand .title,
#i_menu_become_vip .recommand .item .recommand-link
{
  color: var(--m-font-color);
  transition: .25s;
}

#i_menu_become_vip .recommand .item .recommand-link:hover
{
  color: var(--accent-blue);
}

#i_menu_become_vip .recommand .renew-btn button
{
  transition: .25s;
  filter: none;
}

#i_menu_become_vip .recommand .renew-btn button:hover
{
  background: var(--accent-pink);
}

/*稍后再看，收藏夹，历史列表修改*/
.bili-header-m .mini-wnd-nav .list.history li
{
  transition: .25s;
}

.bili-header-m .mini-wnd-nav .list.history li:hover
{
  background: var(--m-gray);
}

.bili-header-m .mini-wnd-nav .list.history li.timeline
{
  border-color: var(--m-gray2-solid);
  color: var(--m-font-subcolor)!important;
}

.bili-header-m .mini-wnd-nav .list.history li a
{
  color: var(--m-font-color);
}

.bili-header-m .mini-wnd-nav .list.history li a:hover
{
  color: var(--accent-blue);
}

/*稍后再看，收藏夹，历史按钮调整*/
.bili-header-m .mini-wnd-nav .read-more
{
  transition: .25s;
  border: none;
  background: var(--accent-blue);
  color: var(--m-font-color);
}

.bili-header-m .mini-wnd-nav .read-more:hover
{
  background: var(--accent-pink);
}

.bili-header-m .mini-wnd-nav .read-more i
{
  transition: .25s;
  filter: brightness(500%);
}

.bili-header-m .mini-wnd-nav .read-more:hover i
{
  margin-left: 20px;
}

.bili-header-m .mini-wnd-nav .list.history li.timeline .date
{
  background: var(--m-darkest);
}

/*投稿*/
.stardust-video .nav-menu .up-load
{
  margin-right: 20px;
}

.nav-menu .up-load .u-link
{
  background: var(--accent-blue)!important;
  box-shadow: 0 0 20px var(--accent-blue-light);
  transition: .6s;
}

.nav-menu .up-load .u-link:hover
{
  box-shadow: 0 0 60px var(--accent-blue-light);
}

.bili-header-m .nav-menu .up-load .up-nav
{
  background-color: transparent!important;
}

.bili-header-m .nav-menu .up-load .up-nav li
{
  background: var(--m-darkest);
}

.bili-header-m .nav-menu .up-load .up-nav li:hover
{
  background: var(--m-gray);
}

.bili-header-m .nav-menu .up-load .up-nav li a
{
  color: var(--accent-blue);
}

.bili-header-m .nav-menu .up-load .up-nav li i
{
  filter: hue-rotate(230deg) brightness(112%);
}

/*通知与动态数量*/
.bili-header-m .nav-menu .nav-con .nav-item .t .num
{
  box-shadow: 0 0 20px var(--accent-pink);
  transition: .25s;
}

.bili-header-m .nav-menu .nav-con .nav-item .t:hover .num
{
  animation: .3s shock infinite alternate;
  box-shadow: 0 0 25px 2px var(--accent-pink);
}



/*----------------------
        网页主体
------------------------*/
/*Logo效果*/
.head-logo
{
  transition: .25s !important;
  background-position: top !important;
}


/*[[logoEffect]]*/
/*搜索框*/
.bili-header-m .search
{
  background-color: rgba(30, 25, 2,.6);
  transition: .25s;
  padding-left: 26px;
}

.bili-header-m .search:hover
{
  background-color: rgba(30, 25, 2, .5);
}

/*排行榜*/
.bili-header-m .search .link-ranking
{
  width: 24px;
  overflow: hidden;
  left: 5px;
}

.bili-header-m .search .link-ranking span
{
  filter: contrast(0) brightness(2);
  transition: .3s ease-out;
}

.bili-header-m .search .link-ranking:hover span
{
  filter: hue-rotate(225deg) brightness(1.2) drop-shadow(0 2px 4px var(--accent-blue));
  transform: scale(.9) translate(-1px, -3px);
}

.bili-header-m .search .link-ranking,
.search .searchform
{
  background-color: transparent!important;
}

.bili-header-m .search .link-ranking:hover
{
  background-color: var(--m-gray);
}

.search .search-keyword
{
  background-color: transparent;
  border-radius: 4px;
}

.search .searchform input.search-keyword
{
  color: var(--m-font-color);
}

.search .searchform input.search-keyword::placeholder,
.nav-search-box .nav-search .nav-search-keyword::placeholder
{
  color: transparent;
}

.bili-header-m .search button.search-submit
{
  border-radius: 0 5px 5px 0!important;
  filter: contrast(0) brightness(2);
  transition: transform .3s ease-out;
}

.bili-header-m .search button.search-submit:hover
{
  filter: hue-rotate(225deg) brightness(1.2) drop-shadow(0 2px 4px var(--accent-blue));
  transform: scale(.9) rotate(-10deg) translate(-1px, -1px);
}

/*导航栏搜索框*/
#nav_searchform
{
  border-color: transparent!important;
  background-color: var(--m-gray)!important;
  transition: .25s;
}


#nav_searchform:hover
{
  border-color: var(--accent-blue)!important;
}

#nav_searchform input
{
  color: var(--m-font-color)!important;
}

#banner_link .bilibili-suggest
{
  width: 296px;
  right: 26px;
}

.bilibili-suggest,
.suggest-wrap
{
  animation: .5s showInfo;
  background: var(--m-darkest)!important;
  border-color: transparent!important;
  box-shadow: var(--shadow60)!important;
  transition: .25s;
}

.bilibili-suggest .b-line span
{
  color: var(--m-font-subcolor)!important;
  background: var(--m-darkest-solid)!important;
}

.bilibili-suggest .suggest-item a,
.suggest-wrap .vt-text,
.suggest-wrap .keyword-wrap .keyword
{
  color: var(--m-font-color)!important;
}

.bilibili-suggest .suggest-item:hover,
.bilibili-suggest .suggest-item.focus,
.suggest-wrap .vt-text:hover,
.suggest-wrap .vt-text.focus
{
  background: var(--m-gray)!important;
}

.bilibili-suggest .suggest-item .cancel,
.suggest-wrap .history-li .clear
{
  filter: brightness(200%)!important;
}

.bilibili-suggest .suggest-item .cancel,
.suggest-wrap .history-li .clear
{
  transition: transform 3s!important;
}

.bilibili-suggest .suggest-item:hover .cancel,
.suggest-wrap .history-li:hover .clear
{
  transform: rotate(1800deg)!important;
}

.bilibili-suggest .suggest-item .cancel:hover,
.suggest-wrap .history-li .clear:hover
{
  filter: brightness(100%)!important;
}

/*搜索*/
.search-wrap
{
  width: 556px;
}

.search-wrap .search-logo
{
  background-image: url(https://i.loli.net/2019/01/25/5c4afa94e490d.png) !important;
  background-position: left bottom !important;
  background-size: 80px;
  background-repeat: no-repeat;
  width: 80px!important;
}

.search-wrap .fixed-top
{
  background-color: rgba(30, 25, 2,.6);
  box-shadow: 0 10px 40px var(--shadow-color);
}

.search-wrap .search-button
{
  position: relative;
  left: 20px;
}

.search-wrap .search-button span
{
  display: none;
}

#search-keyword
{
  transition: .25s;
  background-color: var(--m-gray);
  color: var(--m-font-color);
  border-color: transparent;
}

#search-keyword:focus
{
  border-color: var(--accent-blue)!important;
  outline: none;
}

.search-block .input-wrap
{
  background: transparent!important;
}

/*热门关键字*/
.suggest-wrap .horizontal .hz-text
{
  transition: .2s;
  border-color: var(--m-gray);
  color: var(--m-font-subcolor);
}

.suggest-wrap .title span
{
  color: var(--m-font-subcolor)!important;
  background-color: var(--m-darkest-solid);
}

/*回到顶层*/
.btn_gotop,
.go-top-m .go-top
{
  margin-top: 20px;
  background-color: var(--m-gray)!important;
  border: none!important;
  transition: background-color .25s;
}

.btn_gotop:hover,
.go-top-m .go-top:hover
{
  background-color: var(--accent-pink)!important;
  animation: .1s shock ease-out infinite;
  box-shadow: 0 0 40px var(--accent-pink-light);
}

/*分区导航栏*/
#banner_link + .bili-wrapper
{
  width: 100%!important;
  margin-bottom: 20px!important;
  background-color: var(--m-darkest)!important;
}

#primary_menu
{
  width: 1230px;
  border: none !important;
}

#primary_menu .nav-menu
{
  padding: 0 30px;
}

.primary-menu .nav-menu > li
{
  width: 53.7px;
}

@media screen and (max-width: 1400px)
{
  #primary_menu
  {
    width: 1045px!important;
  }

  #primary_menu .nav-menu
  {
    padding: 0 30px!important;
  }

  .primary-menu .nav-menu > li.home
  {
    margin-right: 8px!important;
  }

  .primary-menu .nav-menu > li
  {
    width: 50.7px;
  }
}

@media screen and (min-width: 1400px)
{
  .primary-menu .nav-menu > li.home
  {
    margin-right: 20px!important;
  }
}

#primary_menu .nav-menu li .num-wrap span
{
  transition: .25s;
}

#primary_menu .nav-menu li:hover .num-wrap span
{
  filter: drop-shadow(0 2px 4px var(--accent-blue-light));
}

#primary_menu .nav-menu > li:not(.on) .nav-name,
#primary_menu .nav-menu .side-nav .side-link span
{
  color: var(--m-font-color)!important;
}

#primary_menu .nav-menu > li,
#primary_menu .nav-menu .side-nav .side-link
{
  transition: .25s;
}

#primary_menu .nav-menu > li:hover,
#primary_menu .nav-menu .side-nav .side-link:hover
{
  transform: scale(1.1);
}

#primary_menu .nav-menu .side-nav .square-wrap .square-field,
#primary_menu .nav-menu > li .nav-live .live-field
{
  border-color: var(--m-gray2);
}

@media screen and (max-width: 1680px)
{
  #primary_menu > .nav-menu > .side-nav:nth-last-child(2) .sub-nav
  {
    left: -300px;
  }

  #primary_menu > .nav-menu > .side-nav:nth-last-child(3) .sub-nav
  {
    left: -330px;
  }
}

/*分区导航栏图标颜色调整*/
#primary_menu .nav-menu li.home a
{
  background: url(data:image/png;base64,) no-repeat center 5px;
}

#primary_menu .nav-menu li a div > span
{
  background-color: var(--accent-blue)!important;
}

#primary_menu .nav-menu .sub-nav/*分区导航栏二级菜单*/
{
  background-color: var(--m-darkest);
  box-shadow: var(--shadow40);
  border-radius: var(--content-radius);
}

#primary_menu .nav-menu .sub-nav span
{
  color: var(--m-font-color);
}

.primary-menu .nav-menu .sub-nav li:hover
{
  background: var(--m-gray);
}

#primary_menu .nav-menu li:hover .sub-nav
{
  animation: .25s showInfo;
}

#primary_menu > .nav-menu > .side-nav
{
  margin-left: 5px;
}


/*子导航栏，如动画分区的不同类型*/
#subnav,
.bangumi-home-crumb,
.bread-crumb,
.bangumi-index-crumb
{
  padding: 20px 30px;
  margin-bottom: 40px;
  background-color: var(--m-darkest);
  border-radius: var(--content-radius);
  transition: .25s;
  box-shadow: 0 0 0 0 var(--shadow15);
}

#subnav:hover,
.bangumi-home-crumb:hover,
.bread-crumb:hover,
.bangumi-index-crumb:hover
{
  box-shadow: 0 0 0 var(--content-border-size) var(--content-border-hover),
    var(--shadow15);
}

/*放映厅导航栏*/
.cinema-home-crumb ul li:not(.on) a:not(:hover)
{
  color: var(--m-font-color);
}

.bangumi-home-crumb
{
  padding: 20px;
}

.sub-nav-m ul li,
.bread-crumb ul li,
.bangumi-index-crumb ul li
{
  margin: 10px;
}

.bangumi-home-crumb ul li
{
  margin: 0 20px 0 0!important;
}

.bangumi-home-crumb .discovery-entry
{
  top: 20px;
  right: 20px;
}

.bread-crumb ul li a,
.bangumi-index-crumb ul li a
{
  color: var(--m-font-color)!important;
}

.sub-nav-m ul li a,
.bangumi-home-crumb ul li a,
.bread-crumb ul li,
.bangumi-index-crumb ul li
{
  padding: 0 20px 0 20px!important;
  border-radius: 5px;
  color: var(--m-font-color)!important;
  background-color: var(--accent-blue);
  transition: .3s;
  box-shadow: 0 4px 10px var(--accent-blue-light);
}

.sub-nav-m ul li.on a,
.sub-nav-m ul li a:hover,
.bangumi-home-crumb ul li.on a,
.bangumi-home-crumb ul li a:hover,
.bread-crumb ul li:hover,
.bread-crumb ul li.on,
.bangumi-index-crumb ul li:hover,
.bangumi-index-crumb ul li.on
{
  background-color: var(--accent-pink);
  box-shadow: 0 6px 20px -4px var(--accent-pink-light);
}

.sub-nav-m .tips
{
  position: absolute;
  top: 60px!important;
  background-color: var(--m-darkest);
  color: var(--m-font-color);
  border-color: transparent;
  box-shadow: 0 8px 40px var(--shadow-dark-color);
}

/*番剧导视*/
.bangumi-home-crumb .discovery-entry
{
  filter: grayscale(1) invert(1) brightness(2);
}

/*去除板块广告*/
.gg-floor-module [data-loc-id]
{
  display: none;
}

/*板块轮播图*/
.carousel-module
{
  margin: auto!important;
}

.carousel-wrapper,
.carousel-module
{
  box-shadow: var(--shadow15);
}

/*板块厂牌推荐*/
.block-left.block-brand .pgc-brand-tab
{
  filter: invert(1) hue-rotate(180deg);
}

/*各板块容器调整*/
.chief-recommend-module,
.zone-wrap-module.report-wrap-module.report-scroll-module,
.popularize-module,
.special-recommend-module,
.channel-m .report-wrap-module.report-scroll-module:not(#recent_hot),
.tag-list-wrp/*标签列表*/
,
.tag-list-wrp + div,
.block-area-top,
.block-area,
.bangumi-index-body/*番剧索引*/
,
.rank-body .rank-list .rank-item/*排行榜列表*/
,
.watch-later-list .list-box .av-item/*稍后再看*/
,
.area-module,
.list-custom-wrp,
#app .timeline-container
{
  margin-top: 0px;
  margin-bottom: 40px;
  padding: 30px!important;
  background: var(--m-darkest);
  border-radius: var(--content-radius);
  transition: .25s;
  box-shadow: 0 0 0 0,
    var(--shadow15);
}

.chief-recommend-module:hover,
.zone-wrap-module.report-wrap-module.report-scroll-module:hover,
.popularize-module:hover,
.special-recommend-module:hover,
.channel-m .report-wrap-module.report-scroll-module:hover:not(#recent_hot),
.tag-list-wrp:hover,
.tag-list-wrp + div:hover,
.block-area-top:hover,
.block-area:hover,
.bangumi-index-body:hover,
.rank-body .rank-list .rank-item:hover,
.watch-later-list .list-box .av-item:hover,
.area-module:hover,
#app .timeline-container:hover
{
  box-shadow: 0 0 0 var(--content-border-size) var(--content-border-hover),
    var(--shadow15);
}

@media screen and (max-width: 1400px)
{
  .block-area-top
  {
    width: 982px!important;
  }

  .block-area-top .hot-recom-module
  {
    width: 340px!important;
  }

  .video-item-biref:nth-of-type(2),
    .video-item-biref:nth-of-type(4)
  {
    margin-right: 0;
  }

  .video-item-biref:nth-of-type(3n)
  {
    margin-right: 20px!important;
  }
}

.block-area
{
  width: auto!important;
}

.bangumi-index-body
{
  width: 1170px!important;
  margin-bottom: 40px;
}

@media screen and (max-width: 1400px)
{
  .bangumi-index-body
  {
    width: 980px!important;
  }
}

.filter-wrapper
{
  float: left;
}

.block-area-top
{
  overflow: visible;
  margin-right: 0;
}

.video-item-biref:nth-of-type(3n)
{
  margin-right: 0;
}

.block-area-top .hot-recom-module
{
  width: 520px;
}

/*视频容器*/
.game-groom-m
{
  background-color: var(--m-darkest);
  border-color: var(--m-gray);
}

.game-groom-m .num,
.game-groom-m .title
{
  transition: .3s!important;
  background-color: var(--m-darkest-solid);
}

.game-groom-m a:hover .title
{
  color: var(--accent-blue)!important;
}

.spread-module,
.groom-module
{
  animation: 1s showVideo;
  background-color: var(--m-darkest);
}

.groom-module *
{
  transition: .25s;
}

.groom-module:hover img
{
  filter: blur(6px);
  transform: scale(1.2);
}

.groom-module:hover .card-mark
{
  background-color: rgba(0, 0, 0, .5);
}

/*排行列表*/
.rank-list .rank-item
{
  animation: 1s floatDown;
}

/*排行排名*/
.rank-item.highlight .ri-num,
/*番剧最新话*/
.card-timing-module .r-text .update.on a,
/*番剧最新话*/
.timeline-box .timeline-item.new p.num a
{
  background-color: var(--accent-blue)!important;
}

.rank-item .ri-num,
.card-timing-module .r-text .update a,
.timeline-box .timeline-item p.num a
{
  background-color: var(--m-gray)!important;
}

/*番剧时间表、新番时间表*/
.timeline-container
{
  margin-top: 40px!important;
}

.timeline-header
{
  box-shadow: none;
  margin: 0 30px 30px 30px;
  background-color: var(--m-darkest-solid);
  box-shadow: 0 10px 10px var(--m-darkest-solid);
}

.timeline-header .tl-weeks
{
  box-shadow: inset 0 -33px 22px 12px var(--m-darkest);
}

.tl-head .tl-day:not(.today)
{
  background-color: var(--m-darkest-solid);
}

.tl-head .tl-day.today
{
  filter: invert(1) hue-rotate(180deg);
}

.timeline-wrapper
{
  background-color: transparent!important;
  box-shadow: none;
}

li.season-item .season-body .season-title span.follow
{
  background-color: transparent;
}

.season-timeline .season-group .group-time
{
  color: var(--m-font-color);
}

.season-timeline .season-group.is-published .group-time
{
  color: var(--m-font-subcolor);
}

/*最新动态、视频热度排序*/
.vl-hd,
.vd-list li,
.vd-list .l-item,
.vd-list .r,
.vd-list li .l-r
{
  border-color: var(--m-gray)!important;
}

.vd-list li .l-item
{
  background-color: transparent;
}

.vd-list li *
{
  transition: color .25s, transform .25s, opacity .25s!important;
}

.vd-list li:hover img
{
  transform: scale(1.2);
}

.vd-list li.l3:hover .title,
.mod-3 li:hover .r .title
{
  color: var(--accent-blue)!important;
}

/*标签栏调整*/
.bili-tab-item,
.tab-list li,
.season-item,
.be-tab,
.vl-hd #tab_list_type .tab-i,
.tab-item
{
  transition: .25s;
  color: var(--m-font-color)!important;
  border-color: transparent!important;
}

.bili-tab-item:hover,
.bili-tab-item.on,
.tab-list li:hover,
.tab-list li.on,
.season-item:hover,
.season-item.on,
.vl-hd #tab_list_type .tab-i.on,
.vl-hd #tab_list_type .tab-i:hover,
.tab-item.active,
.tab-item:hover
{
  color: var(--accent-blue)!important;
}

.bili-tab-item.on,
.tab-list li.on,
.season-item.on
{
  border-color: var(--accent-blue)!important;
}

/*按钮调整*/
/*蓝色滑动发光按钮*/
.read-push/*动态，换一换*/
,
.change-push,
.submit-move,
.attention-btn,
.random-change
{
  transition: .4s!important;
  background-color: var(--accent-blue)!important;
  color: var(--m-font-color)!important;
  border: none!important;
}

.read-push:hover,
.change-push:hover,
.submit-move:hover,
.attention-btn:hover,
.random-change:hover
{
  color: var(--m-font-color)!important;
  box-shadow: 0 4px 30px var(--accent-blue-light);
}

.default,
.back-to-info
{
  background-color: transparent!important;
  border-color: var(--m-font-color)!important;
  color: var(--m-font-color)!important;
}

.default:hover,
.back-to-info:hover
{
  border-color: var(--accent-blue)!important;
  color: var(--accent-blue)!important;
}

/*更多*/
.link-more,
.new-stat-more,
.follow-more
{
  transition: .4s!important;
  background-color: var(--accent-pink)!important;
  color: var(--m-font-color)!important;
  border: none!important;
}

.link-more:hover,
.new-stat-more:hover,
.follow-more:hover
{
  color: var(--m-font-color)!important;
  box-shadow: 0 4px 30px var(--accent-pink-light);
}

.read-push .icon,
.change-push .icon,
.link-more .icon,
.new-stat-more i,
.back-to-info:not(:hover),
.follow-more i,
.random-change i
{
  filter: brightness(3);
}

/*蓝色发光按钮*/
a.like,
.b-gz,
.primary,
.btn-submit,
.btn-follow.cinema
{
  box-shadow: 0 2px 20px var(--accent-blue-light);
  filter: none!important;
}

a.like:hover,
.b-gz:hover,
.primary:hover,
.btn-submit:hover,
.btn-follow.cinema:hover
{
  box-shadow: 0 2px 30px var(--accent-blue-light);
}

.b-cd,
.btn-follow:not(.cinema),
.following-charge-btn
{
  transition: .25s;
  color: var(--m-font-color)!important;
  background-color: var(--accent-pink)!important;
  border: none!important;
  box-shadow: 0 4px 20px var(--accent-pink-light);
  filter: none!important;
}

.b-cd:hover,
.btn-follow:not(.cinema):hover,
.following-charge-btn:hover
{
  box-shadow: 0 4px 30px 2px var(--accent-pink-light);
}

/*已追番、已关注*/
.btn-follow.followed,
.btn-follow.followed:hover,
.btn.followed .b-gz,
.ghost,
.btn.followed .b-gz,
a.liked,
.following
{
  background-color: var(--m-gray)!important;
  box-shadow: none!important;
  border-color: transparent!important;
}

/*禁用*/
.btn.btn-disabled,
.disable
{
  background-color: var(--m-gray2)!important;
  box-shadow: none!important;
  border-color: transparent!important;
  opacity: .4;
}

.follow-btn.followed,
.ygz,
.qxgz,
.d-btn
{
  background-color: var(--m-gray)!important;
  color: var(--m-font-subcolor)!important;
}

/*白色边框通透按钮*/
/*发送消息*/
.btn-box .message,
.btn-cancel
{
  background: transparent!important;
  color: var(--m-font-color)!important;
}

.btn-box .message:hover,
.btn-cancel:hover
{
  border-color: var(--accent-blue)!important;
  color: var(--accent-blue)!important;
}

/*红色边框通透按钮*/
.charge-btn.not-follow-charge-btn
{
  background-color: transparent!important;
}

.charge-btn.not-follow-charge-btn:hover
{
  background-color: var(--accent-pink)!important;
}

/*蓝色色边框通透按钮*/
.s-btn
{
  background-color: transparent!important;
}

.s-btn:hover
{
  background-color: var(--accent-blue)!important;
}

/*跳页按钮*/
.paging-box-big a,
.page-item button,
.pagelistbox a.p,
.bangumi-pagelistbox .p
{
  color: var(--m-font-color)!important;
  background-color: var(--m-gray)!important;
  border: none!important;
}

.paging-box-big a:hover,
.pages .active button,
.pages button:hover,
.pagelistbox a:hover,
.pagelistbox a.active,
.bangumi-pagelistbox a:hover,
.bangumi-pagelistbox a.active
{
  background-color: var(--accent-blue)!important;
}

.paging-box-big .dian,
.pages strong,
.pagelistbox strong
{
  color: var(--m-font-color)!important;
  background-color: transparent!important;
  border: none!important;
}

.paging-box-big input,
.pager .jump-pager input[type=text],
.be-pager-options-elevator input,
.pagelistbox input,
.bangumi-pagelistbox input
{
  background-color: var(--m-gray)!important;
  border-color: var(--m-gray)!important;
  color: var(--m-font-color)!important;
  transition: .25s;
}

.paging-box-big input:focus,
.pager .jump-pager input[type=text]:focus,
.be-pager-options-elevator input:focus,
.pagelistbox input:focus,
.bangumi-pagelistbox input:focus
{
  border-color: var(--accent-blue)!important;
}

/*新跳页按钮*/
.be-pager li:not(.be-pager-item-active) a
{
  color: var(--m-font-color);
}

.be-pager li:not(.be-pager-item-active)
{
  background-color: transparent;
}

.be-pager li:not(.be-pager-item-active):hover a
{
  color: var(--accent-blue);
}

/*全部、收起按钮*/
.timeline-toggle-btn,
.b-toggle-btn
{
  background: none!important;
}

.timeline-toggle-btn:not(:hover) span,
.b-toggle-btn:not(:hover) span
{
  color: var(--m-font-subcolor);
}

/*标签按钮*/
.zone-title .headline .tags li a:not(:hover)
{
  color: var(--m-font-color);
}

/*弹幕数，播放数，视频类型背景*/
.spread-module p.t + p.num,
.card-live-module p.t + p.num
{
  background-color: var(--m-darkest-solid)!important;
}

/*音频标题*/
.audio-list .song-list .song-li .content .text
{
  filter: invert(1) hue-rotate(180deg);
}


/* 下拉列表调整 */
.rank-dropdown,
.dropdown-list .dropdown-item,
.vl-hd-sub .b-slt .list
{
  background-color: var(--m-gray-solid)!important;
  color: var(--m-font-color)!important;
  border-color: var(--m-gray-solid)!important;
  transition: .25s;
}

.dropdown-list .dropdown-item:hover,
.b-slt .list li:hover
{
  background-color: var(--m-gray2-solid)!important;
}

/*新下拉列表*/
#select_list_order,
.be-dropdown-menu,
.opera-list,
.more-panel,
.dropdown-list
{
  background-color: var(--m-gray-solid)!important;
  border-color: var(--m-gray-solid)!important;
  box-shadow: 0 10px 40px var(--shadow-dark-color)!important;
  border-radius: 0 0 4px 4px;
}

.more-panel::after
{
  background-color: var(--m-gray-solid)!important;
  border: none!important;
}

.be-dropdown-menu .be-dropdown-item,
.opera-list li,
.more-panel p
{
  color: var(--m-font-color);
}

.opera-list li:hover
{
  color: var(--accent-blue);
  background-color: var(--m-gray2-solid)!important;
}

.be-dropdown-menu .be-dropdown-item:hover
{
  background-color: var(--accent-blue);
  color: var(--m-font-color);
}

/*查看更多*/
.more-link
{
  background-color: var(--m-gray)!important;
  color: var(--m-font-color)!important;
  border-color: var(--m-gray)!important;
}

.more-link:hover
{
  border-color: var(--m-gray2)!important;
  background-color: var(--m-gray2)!important;
}

/*输入框调整*/
.be-textarea_inner,
.textarea,
.vl-hd-sub .date-select
{
  transition: .25s;
  background-color: var(--m-gray)!important;
  border-color: transparent!important;
  color: var(--m-font-color)!important;
}

.be-textarea_inner:hover,
.be-textarea_inner:focus,
.textarea:hover,
.textarea:focus
{
  background-color: transparent!important;
  border-color: var(--accent-blue)!important;
}

/*悬停显示视频信息*/
.video-info-module,
.bangumi-info-module
{
  border-radius: var(--content-radius);
  background-color: var(--m-darkest);
  border-color: var(--accent-blue);
  box-shadow: var(--shadow80)!important;
}

.video-info-module .v-title
{
  color: var(--m-font-color);
}

.video-info-module .v-preview,
.video-info-module .v-data
{
  border-color: var(--m-gray);
}

.video-info-module p,
.video-info-module .name
{
  color: var(--m-font-subcolor)!important;
}

/*侧边栏*/
.fav-sidenav,
.contribution-sidenav,
.follow-sidenav
{
  border: none!important;
}

.nav-container
{
  border-color: var(--m-gray)!important;
}

.fav-item:not(.cur):hover,
.contribution-sidenav li:not(.cur):hover,
.follow-item:not(.cur):hover
{
  background-color: var(--m-gray)!important;
}

/*用户卡片信息*/
.userinfo-wrapper
{
  border: none!important;
  box-shadow: 0 20px 80px var(--shadow-dark-color)!important;
  border-radius: var(--content-radius);
}

.user-card-m,
.user-card,
.userinfo-content,
#id-card
{
  border: 1px solid var(--accent-blue)!important;
  background-color: var(--m-darkest)!important;
  margin-top: -20px;
  overflow: hidden!important;
  box-shadow: var(--shadow60)!important;
  border-radius: var(--content-radius);
}

.user-card-m div:first-of-type,
.user-card div:first-of-type,
.userinfo-content div:first-of-type,
#id-card div:first-of-type
{
  border-radius: 0px!important;
}

/*去除头像边框*/
.user-card-m a:first-of-type,
.user-card a:first-of-type,
.userinfo-content a:first-of-type,
#id-card .idc-avatar
{
  border-color: transparent!important;
  background: none;
}

#id-card .idc-meta-item
{
  color: var(--m-font-color);
}

.user-card-m iframe
{
  display: none;
}

/*通知框调整*/
@keyframes diglogPulse
{
  0%
  {
    transform: scale(0);
  }

  40%
  {
    transform: scale(1.1);
  }
}

@keyframes diglogPulse2
{
  0%
  {
    opacity: 0;
    transform: translate(0, -100%);
  }
}

.follow-dialog-window,
#pin-layer,
.modal-wrapper,
.collection-m,
#elec-modal .content,
.comment-bilibili-con,
.extension-wrapper,
.bili-dialog .con,
.sponsor-pay-content
{
  border: 1px solid var(--accent-blue);
  border-radius: var(--content-radius)!important;
  background-color: var(--m-darkest)!important;
  box-shadow: var(--shadow80);
}

.follow-dialog-window,
#pin-layer,
.modal-wrapper,
.collection-m,
#elec-modal .content,
.comment-bilibili-con,
.extension-wrapper,
.bili-dialog .con
{
  animation: diglogPulse .6s;
}

.modal-wrapper,
.follow-dialog-window
{
  animation: diglogPulse2 .6s;
}

.collection-m .title .close,
.modal-close:not(:hover),
.btn-close
{
  filter: brightness(2);
}

.modal-header-close
{
  filter: invert(1) hue-rotate(180deg);
}

.modal-title
{
  border-bottom: 1px solid #FFF!important;
}

.modal-title,
.modal-title p:first-child,
.modal-body,
.modal-body li:not(.disabled) span,
.follow-dialog-window label,
.collection-m label:not(:hover),
.comment-bilibili-con .con label,
.bili-dialog .con,
.sponsor-pay-dialog-v2 li:not(.active) span,
.sponsor-pay-dialog-v2 .sponsor-hint,
.sponsor-pay-dialog-v2 .sponsor-agree a:not(:hover)
{
  color: var(--m-font-color)!important;
}

.modal-title p:nth-child(2),
.comment-bilibili-con .reason-box dl dt,
.sponsor-pay-dialog-v2 .sponsor-point,
.sponsor-pay-dialog-v2 .sp-pay-index .sponsor-agree
{
  color: var(--m-font-subcolor)!important;
}

.modal-body li:hover
{
  background-color: var(--m-gray)!important;
}

