css:
@-moz-document domain(zhihu.com)
{

/* 2020-09-11: version 0.28 */
:root {
    --bgcolor: #F9F9E9;
    --lightcolor: #FFFFEF;
    --darkcolor: #E9E9D9;
    --hovercolor: #D9EFEF;
}

body,
.Entry-body,
.WhiteBg-body,
.GreyBg-body,
.LightGreyBg-body,
.Topstory-body--new {
    background-color: var(--darkcolor);
    background-image: linear-gradient(rgba(0, 0, 0, 0.2) 30%, rgba(0, 0, 0, 0.8) 70%), url("https://open.saintic.com/api/bingPic/?picSize=1");
    background-size: 100% 100%;
    background-attachment: fixed;
}

.Topstory--new .Topstory-container {
    background-color: rgba(249, 249, 233, 0.6);
    margin: 0 auto;
    width: 1120px;
    max-width: 1120px;
}

.GlobalLeftSideBar {
    position: relative;
    width: 160px !important;
    max-height: none;
    top: 0px;
}

.LeftMenu {
    padding: 0;
}

.Topstory-mainColumn {
    margin-left: 0px !important;
}

.GlobalSideBar--new {
    position: relative;
    width: 280px;
    max-height: none;
    left: 0px;
    right: 0px;
    top: 0px;
    margin-left: 8px;
    padding-left: 8px !important;
    background-color: var(--bgcolor);
}


.Post-content {
    max-width: 1080px;
    margin: auto;
}

.VerificationForm-container,
.VerificationManagement-container,
.zu-main {
    background-color: var(--bgcolor);
    padding: 25px 30px 50px;
}

.WriteIndexLayout-main,
.PinDetail {
    padding: 30px 50px;
    background-color: var(--bgcolor);
}

.Card,
.AppHeader,
.QuestionHeader,
.ColumnPageHeader,
.ColumnHeader,
.Column-ArticleList,
.RichContent-actions,
.RichContent-actions.is-fixed,
.Post-content,
.HotList,
.AnswerForm-footer,
.Editable-toolbar,
.Modal-inner,
.Popover-content,
.SettingsMain-mainColumn,
.ExpandableField,
.SettingsFAQ,
.AnswerForm-fullscreenScroller,
.AnswerForm-fullscreenContent .Editable-toolbar,
.SearchTabs,
.Notifications-footer,
.Messages-footer,
.Modal-modal-wf58,
.QuestionHeader-footer,
.QuestionWaiting-types,
.Notifications-Section-header,
.NotificationList,
.Creator-sideColumn,
.AnalyticsWork-Tabs,
.Title-main-1ldU,
.iframeLive-iframe_live-WojO,
.iframeLive-certified-2IOp,
.iframeLive-description-2C6O,
.Profile-lightList,
.Profile-footerOperations,
.PubIndex-CategoriesHeader,
.Pub-reader-app-header,
.Pub-reader-bottom-bar,
.Pub-web-reader .reader-chapter-content,
.module,
.roundtable-list .visits,
.menu-nav li.active,
.menu-nav li,
.feeds.loose .item,
.module .module-header,
.global-footer,
.comments-pagination,
.Topic-bar,
.css-1qefhqu,
.ClubSliderList,
.ExploreSpecialCard {
    background-color: var(--bgcolor);
}

.Input-wrapper.is-focus,
.Input-wrapper.Input-wrapper--grey.is-focus,
.CommentEditorV2-inputWrap,
.HoverCard,
.FeedbackForm-inputBox-15yJ:focus,
.AnalyticsCount,
.AnalyticsWork-CreatorSection,
.ToolsCopyright-input,
.public-DraftEditor-content {
    background-color: var(--lightcolor);
}

.Input-wrapper,
.Input-wrapper.Input-wrapper--grey,
.CommentsV2-withPagination,
.CommentsV2-footer,
.Topbar,
.ztext pre,
.ztext blockquote,
.PushNotifications-newItem,
.Messages-newItem,
.FeedbackForm-inputBox-15yJ,
.Pub-web-reader .reader-container,
.Pub-reader-app-header .reader-nav,
.Comments-footer,
.CommentList {
    background-color: var(--darkcolor);
}

.GlobalSideBar-navLink:hover,
.Messages-item:hover,
.menu-nav li:hover,
.roundtable-profile .description:hover {
    background-color: var(--hovercolor);
}

.HotItem,
.ContentItem-actions,
.ProfileHeader-wrapper,
.AnalyticsWork,
.Recommendations-Main,
.ztext code,
.feeds .item-actions,
.KfeCollection-PcCollegeCard-root,
.KfeCollection-PcCollegeCard-wrapper {
    background: none;
}

.Pc-feedAd-container,
.Pc-card {
    display: none;
}

}
