css:
@-moz-document domain("filmdb.pl") {
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px #242424;
    background-color: #323232;
}
::-webkit-scrollbar {
    width: 13px;
    background-color: #242424;
}
::-webkit-scrollbar-thumb {
    -webkit-box-shadow: inset 0 0 3px #242424;
    background-color: #454545;
}
body {
    background-color: #252525;
    font-family: arial;
}
#aswift_2 {
    visibility: hidden !important;
    height: 0px !important;
}
#header {
    background-color: #101010;
}
header {
    background-color: #101010;
}
#logo {
    background-image: url(http://rybka.ct8.pl/1/logodb.png);
}
#header_right {
    background-color: #303030;
    border-color: #202020;
    border-radius: 5px;
}
#search_field {
    background-color: #303030;
    color: white;
}
.font2,
.font1,
.video_title,
#inne_zrodla_linki,
.no-decoration,
h2,
h4,
.no-mobile {
    color: white !important;
}
.login_button,
.button2 {
    background-color: #303030;
    color: white;
    border-radius: 5px;
}
.header_menu_item {
    color: #b1b1b1;
}
.adbox {
    height: 0;
}
.main_btn {
    background-color: #454545;
    border: 2px solid #202020;
}
.orange {
    background-color: #454545;
}
.main_btn:hover {
    background-color: #303030;
}
.popularne_figura {
    box-shadow: 0px 0px 0px 0px;
    background-color: #303030;
    border-radius: 5px;
    border: solid 1px #202020;
    color: white;
}
.popularne_figure_img {
    border-radius: 5px;
}
#footer {
    background-color: #101010;
}
#podstrona_prawy_box {
    border: 0;
}
#wybor_sezonu {
    background-color: #303030;
    border: 1px solid #101010;
    border-radius: 5px;
}
a {
    color: #b0b0b0 !important;
}
a.epizod_href:visited {
    color: #fff !important;
}
#comment_area {
    visibility: hidden;
    height: 0;
}
#filmdb_szukaj_video {
    background-color: #303030 !important;
    color: white;
    border: 1px solid #101010 !important;
}
#inne_zrodla > div:nth-child(1) {
    background-color: #303030 !important;
    color: white;
    border: 1px solid #101010;
    border-radius: 5px;
}
.fbooster_zrodlo {
    color: #707070 !important;
}
#search_options {
background-color: #252525 !important;
}
#search_options > form > select.font2.select1 {
    background-color: #323232;
    color: #b0b0b0 !important;
    border-color: black;
}
#search_options > form > .select1, .select2 {
    background-color: #323232;
    border-color: black;
    color: #b0b0b0;
    border-radius: 4px;
}
#search_response {
    box-shadow: rgba(0, 0, 0, 0.3) 5px 5px 10px 10px !important;
    background-color: #373737;
    border-radius:4px;
}
#search_response > a > div {
    padding-top: 8px;
    padding-bottom: 8px;
}
#search_response > a > div:hover {
    background-color: #404040;
}
}
