css:
@-moz-document url-prefix("https://informatics.msk.ru") {
input{
  position: relative;
  display: block;
  color: black;
  font-weight: bold;  
  font-size: 14px !important;
  font-family: "montserrat";
  text-decoration: none;
  margin: 5px 0;
  border: 2px solid #E03C85;
  padding: 14px 60px;
  text-transform: uppercase;
  overflow: hidden;
  transition: 1s all ease;
  background: white;	
}
input:hover{
	background: #E03C85;
    color: white;
    border: 2px solid white;
}
input:-internal-autofill-previewed, input:-internal-autofill-selected, textarea:-internal-autofill-previewed, textarea:-internal-autofill-selected, select:-internal-autofill-previewed, select:-internal-autofill-selected{
	background-color: #ff7675 !important;
}


.header{
	background-color: #E03C85;
	border-color: #E03C85 !important; 	
}

h2 {
	background-color: #E03C85;
    border-color: #E03C85 !important; 	
}

th.header, td.header, div.header {
    background-color: #E03C85;
}

#header-home, #header {
    background-color: #E03C85;
    border-color: #E03C85 !important;
    
}

#header-home{
	height: 5px;
    background-color: #e03c8500;
    border-color: #e03c8500 !important;
}

hr {
    display: none;
}

h1.headermain {
    background-color: transparent;
   	display: none;
    height: 30px;
}

#left-column{
	display:none;
}

.block_news_items{
    display:none;

}

#layout-table #middle-column {
    vertical-align: top;
    padding-left: 40px;
    padding-right: 40px;
    padding-top: 40px;
}

.sideblock .content{
	border-color: #E03C85; 
    background-color: white;
    border-width: 5px;
}

a img {
    display: none;
    border: none;
}

.icon-collapse-top:before {
    display: none;
}

.generalbox {
    border-color: #E03C85;
    background-color: #FFFFFF;
    border-width:7px;
}

#right-column{
	padding-top: 40px;
    padding-right: 40px;
}

.logininfo{
	padding-right: 40px;
    font-size: 20px;
}

a:visited {
    color: #EF8729;
}

a:link {
    color: #EF8729;
}

a.show_all_button{
    display:none;
}

[class^="icon-"]{
	display:none;
}

.tree li::after{
	border-top: 2px solid #69C9DD;
}

.tree li::before{
	border-left: 2px solid #69C9DD;
}

.tree li a.node {
	border: 2px solid #69C9DD;
    color: #ff7900;
}

div.navbar {
    display: none;
}

b{
	display: none;
}
}

@-moz-document url-prefix("https://informatics.msk.ru") {
html, p, li, h1, h2, a, input, i, b, td {font-family : franklin gothic medium !important;} body {font-family: "franklin gothic medium" !important;}
}

@-moz-document url-prefix("https://informatics.msk.ru/course"), url-prefix("https://informatics.msk.ru/mod") {
h2, h1, h3 {
    background-color: white;
    border-color: #E03C85 !important;
    border-width: 5px;
    padding: 10px;
}

#course-view .section.main .side, #course-view .section.main .content, #course-view .section td.right {
    background-color: white;
    border-color: #E03C85 !important;
    border-width: 5px;
}

h1{
	color: #ff7900;
}

h3{
	 border-width: 2px;
}

#header{
    height: 5px;
    background-color: #e03c8500;
    border-color: #e03c8500 !important;
}

.navigation ul{
	display:none;
}

img.bigicon {
	display:none;
}

.generalbox{
	margin-left: 30px;
    padding: 19px;
}
}

@-moz-document url-prefix("https://informatics.msk.ru/mod/statements") {
.statements_chapter_title {
    display: none !important;
}

h1{
    display:none;
}

b{
	display: block;
}

table{
	border-color: #69C9DD;
    border-width: 3px;
}
input{
 	display: none !important;
}

.combobox_button{
	display: none !important;
}

div.combobox_container {
    display: none !important;
}

ul{
	border-color: #E03C85 !important;
    border-width: 5px;
}

ul, menu, dir {
    display: block;
    list-style-type: none;
    
}

.statements_summary{
    border-style:solid;
	border-color: #E03C85 !important;
    border-width: 3px;
    padding:10px;
}
}
