css:
@-moz-document domain(linkhay.com) {
    body {
    background: #23262b none;
    color: #f6ffff;
    -webkit-transition: all 1s ease-in-out;
    -moz-transition: all 1s ease-in-out;
    -o-transition: all 1s ease-in-out;
    transition: all 1s ease-in-out;
}
a {
    color: #f6ffff;
}
.V2-vote-box {
    -webkit-transition: all 0.2s ease-in-out;
    -moz-transition: all 0.2s ease-in-out;
    -o-transition: all 0.2s ease-in-out;
    transition: all 0.2s ease-in-out;
}
.V2-vote-box:hover {
    transform: scale3d(1.2, 1.2, 1);
}
a:hover,
a:focus,
a:active {
    color: #e85e41 !important;
}
.V2-header {
    background: #262a2e;
    border-bottom: 1px solid #494b4c;
    box-shadow: 0 0 2px #222323;
}
.menu-bar > li > a {
    color: #f6ffff;
}
.V2-header .header-right .user-menu .user-info .user-name > a {
    color: #f6ffff;
}
.V2-two-cols-layout {
    background: #1f1f1f;
    box-shadow: 0 0 10px #1b1b1b;
}
.V2-link-stream > h2 .tab {
    color: #eaeaea;
}
.V2-link-item .link-info .info,
.V2-link-report,
.V2-link-sensitive a,
.V2-link-item .link-info .info .set-sensitive-box {
    color: #eaeaea;
}
.V2-link-item .V2-vote-box {
    border-radius: 200px;
    border: 1px solid #a5a5a5;
}
.V2-vote-box .counter,
.V2-link-item .link-info .info .comments,
.V2-link-item .link-info .info .V2-reshare > span:first-child,
.V2-link-stream > h2 .tab.selected {
    font-weight: 700;
    color: #03799c;
}
.V2-link-item .link-info .title div {
    color: #b2daff;
    font-style: italic;
}
.V2-link-item .link-info .info .user-link span {
    color: #e3e3e3;
}
.V2-old-style-sidebar-box {
    background: #1f1f1f;
    border: 1px solid #454e52;
}
.V2-old-style-sidebar-box > .box-tile {
    border-bottom: 1px solid #505050;
}
.top-token li + li {
    border-top: 1px solid #4a4a4a;
}
.V2-comments.old-style .V2-comment-item {
    background: #2d2d2c;
}
.V2-comments.old-style .V2-comment-body {
    color: white;
}
.V2-comments.old-style .V2-comment-vote,
.V2-comment-vote-icon,
.V2-comments.old-style .V2-comment-reply-btn {
    background: #2a2a4ed9;
}
}
