css:
@-moz-document domain("bandcamp.com") {
/*

Bandcamp: Stick Track List to Player
https://userstyles.org/styles/123397/

Document rules means
- "anything on bandcamp.com domain and subdomains" (first)
- "anything on other domains and subdomains served via http or https" (second).

It is for good categorisation at userstyles.org.

*/
/*
	move merchandising down, so playlist or track description moves up below player
	*/
#centerWrapper #pgBd #trackInfoInner
{
	display: flex;
	flex-direction: column;
}

#centerWrapper #pgBd #trackInfoInner > .tralbumCommands
{
	order: 1;
}

/*
	move upcoming shows down, so discography moves up below band info
	*/
#centerWrapper #pgBd #rightColumn
{
	display: flex;
	flex-direction: column;
}

#centerWrapper #pgBd #rightColumn > #showography
{
	order: 1;
}

/*
	make modals less modal
	*/
/* * OFF for now /
	.ui-widget-overlay {
		display: none;
	}
	.ui-dialog.ui-widget.ui-widget-content.ui-corner-all.nu-dialog.no-title {
		position: fixed !important;
	    top: 0 !important;
	    right: 0 !important;
	    bottom: auto !important;
	    left: auto !important;
	}
	/* */
}

@-moz-document domain("bandcamp.com") {
svg, .inline_player .nextbutton, .inline_player .prevbutton
{
	filter: invert(100%);
}

a
{
	color: #da5 !important;
}

button, .trackYear
{
	color: #ac6 !important;
}

div#collection-container.collection-container, div.home,#hub .item .info,#hub ol.tabs.fixed
{
	background: #000 !important;
}

span, div.area_text, div.sort_controls, div.text
{
	color: #ccc !important;
}

div#pgBd.yui-skin-sam, div.collection-item-details-container, div#dlg0_h.hd, div.blogunit-details-section, DIV#propOpenWrapper,#hub .hub-component:nth-child(2n)
{
	background: #262626 !important;
}

div.collection-item-artist, h1
{
	color: #ccc !important;
}

div.collection-item-title, h2, div.message, DIV.track_number.secondaryText
{
	color: #FFF !important;
}

h3
{
	color: #FFED80 !important;
}

DIV.tralbumData.tralbum-credits
{
	color: #ccc !important;
}

li, DIV.tralbumData.tralbum-about, DIV#license.info, DIV.tralbumData.tralbum-feed
{
	color: #806300 !important;
}

button.sc-button.sc-button-small.sc-button-responsive.sc-button-addtoset
{
	color: black !important;
}

div.collection-item-gallery-container, div#fan-suggestions.dotted-section.mine, div.collection-stats.dotted-section.mine, div.bcweekly-bd
{
	background: #222222 !important;
}

p
{
	color: #aaa !important;
}

div.sound__soundActions
{
	background: transparent !important;
}

button.sc-button.sc-button-small.sc-button-responsive.sc-button-addtoset
{
	color: #111111 !important;
}

div.ft.fakeFt
{
	background: #555555 !important;
}

div.bd.footerless
{
	background: #999999 !important;
}

.walkthrough ol
{
	background-color: #373737;
}

.walkthrough .button
{
	background: #262626;
	border: #262626;
}

.fan-banner.empty.owner
{
	background-color: #373737;
}

#menubar, #pgFt, .menubar-outer
{
	background-color: #26423b !important;
	border-bottom: dotted #000 1px !important;
}

#menubar-wrapper
{
	background-color: #000;
	border-bottom: dotted #000 1px !important;
}

#menubar input#search-field
{
	margin: 0;
	height: 21px;
	line-height: 21px;
	width: 222px;
	font-family: "Helvetica Neue", Arial, sans-serif;
	color: #fff;
	font-size: 13px;
	padding: 0 21px 0 3px;
	-webkit-user-select: text;
	text-align: center;
	background-color: #282828;
	border: 1px solid #282828;
	outline: none;
	border-radius: 3px;
}

#menubar input#search-field.focused
{
	background-color: #282828;
	border: 1px solid #282828;
}

.fan-bio .edit-profile a
{
	border: 1px solid #373737;
	border-radius: 5px;
	outline: none;
	background: #373737;
	color: #aaa;
	font-weight: 500;
	padding: 5px 9px;
	font-size: 11px;
	line-height: 15px;
	text-transform: uppercase;
	display: inline-block;
}

.grids
{
	color: #fff;
	margin: 0 0 100px 0;
}

.recommendations-container
{
	background-color: #373737;
	border-top: dotted #373737 1px;
}

.fan-container .top.editing
{
	border-bottom: 1px solid #2a2a2a;
	background-color: rgb(25, 25, 25);
}

.ui-dialog.nu-dialog .ui-dialog-titlebar
{
	padding: 15px 20px 12px;
	background-color: #282828;
	border-bottom: 1px solid #282828;
}

.ui-widget-content
{
	border: 1px solid #373;
	background: #373737;
}

.notable, .bcdaily, .email-intake, .discover, .app-promo-desktop
{
	background-color: #262626;
}

.bcdaily .bcdaily-story
{
	min-height: 280px;
	background: #373737;
}

.notable-item
{
	background-color: #373737;
}

.item-page
{
	background: #373737;
	border: 1px solid #373737;
}

.follow-fan-btn
{
	background-color: #373737;
	border: 1px solid #373737;
}

.spotlight-name, .spotlight-location, .spotlight-button, .spotlight-bio, .spotlight-link
{
	color: #fff;
}

.aotd-large
{
	background: #373737;
}

.factoid-title
{
	color: #46C5D5;
}

#autocomplete-results.autocompleted
{
	background: #262626;
	border: 1px solid #262626;
	color: white;
}

.searchwidget.keyboard-focus input[type=text]:focus
{
	background: #262626;
	box-shadow: 0 0;
}

.hd
{
	background-color: #000;
}

.discover-detail-inner
{
	background-color: #373737;
}

body.wordpress
{
	background: #262626;
}

.wordpress .sidebar .textwidget
{
	color: #fff;
}

.wordpress h1 a
{
	display: block;
	height: 60px;
	background-size: 242px 28px;
	background-position: 24.6% 50%;
}

p
{
	color: #ffffff !important;
}

.wordpress #content
{
	color: #ffffff;
}

#dash-container .follow-fan, #dash-container .follow-band, #dash-container .follow-discover
{
	border: 1px solid #373737;
	background: linear-gradient(to bottom, #373737 0%, #373737 100%);
}

html
{
	background: #1E1E1E !important;
}

#stories-vm .story-innards
{
	background-color: #373737;
}

.pane
{
	color: #c7c7c7;
}

#settings-menubar
{
	border-right: 1px solid #383838;
}

#settings-menubar li
{
	border-left: 1px solid #383838;
	border-bottom: 1px solid #383838;
	border-top: 1px solid #383838;
}

.share_dialog.ui-dialog .ui-dialog-content
{
	background-color: #262626;
}

.share_dialog .section_head
{
	color: #fff;
}

.buy-dlg
{
	color: #ffffff;
}

#menubar > ul > li .logo
{
	background: url('https://www.dropbox.com/s/8s7km8r329l7qy7/bandcamp-logo-gray.png?dl=1') 0 0 no-repeat;
	background-size: contain;
	height: 20px;
	margin-top: 15px;
	width: 85px;
}

.hd-logo
{
	background: transparent url('https://www.dropbox.com/s/8s7km8r329l7qy7/bandcamp-logo-gray.png?dl=1') no-repeat;
	background-size: 100%;
	margin-top: 24px;
	height: 25px;
	width: 156px;
}

.wordpress h1 a
{
	display: block;
	text-indent: -999em;
	background: url('https://www.dropbox.com/s/mx80o2eenp43l0o/bandcamp-daily-retina-dark-theme.png?dl=1') no-repeat;
	height: 60px;
	background-size: 242px 28px;
	background-position: 24.6% 50%;
}

#pgBd
{
	color: #fff;
}

.download-bottom-area
{
	border-top: none;
	background: none;
}

.download .formats-container
{
	border: 1px solid #373737;
	background-color: #373737;
}

.download .formats
{
	list-style: none;
	color: #888;
	padding: 0;
	background-color: #373737;
	width: 170px;
	z-index: 2;
	cursor: default;
}

.download .formats li:hover
{
	background-color: #262626;
}

::-webkit-scrollbar, ::-webkit-scrollbar-corner
{
	background: #000;
	width: 14px;
	height: 14px;
}

::-webkit-scrollbar-button
{
	display: none;
}

::-webkit-scrollbar-thumb
{
	background: #333;
}

::-webkit-scrollbar-thumb:not(:active):hover
{
	background: #383838;
}

::-webkit-scrollbar-thumb:active, ::-webkit-scrollbar-thumb:hover:active
{
	background: #5c5c5c;
}
}
