css:
@-moz-document domain(w3schools.com) {

/* Main pages */
#mainLeaderboard,
#tryitLeaderboard,
.top,
.topnav,
#skyscraper,
#footer > div:last-of-type,
#footer > br {
	display: none;
}
#leftmenuinner {
    padding-top: 0 !important;
}
body {
    color: #999;
}

.w3-row {
    color: #999 !important;
    background-color: #222 !important;
}
.w3-main,
.w3-light-grey,
.w3-hover-light-grey:hover,
.w3-light-gray,
.w3-hover-light-gray:hover {
    color: #767676!important;
    background-color: #3a3a3a!important;
}
.w3-dark-grey,
.w3-hover-dark-grey:hover,
.w3-dark-gray,
.w3-hover-dark-gray:hover {
    background-color: #282828!important;
}
.w3-text-dark-grey,
.w3-hover-text-dark-grey:hover,
.w3-text-dark-gray,
.w3-hover-text-dark-gray:hover {
    color: #999 !important;
}

.w3-text-white,
.w3-hover-text-white:hover {
    color: #52ae51 !important;
}
html[lang] h1, 
html[lang] h2, 
html[lang] h3, 
html[lang] h4, 
html[lang] h5, 
html[lang] h6 {
    color: #999;
}
#sidenav a:hover,
#sidenav a:focus,
#sidenav a.active {
    color: #aaa;
    background-color: #222;
}
.w3-white,
.w3-hover-white:hover {
    color: #888!important;
    background-color: #333!important;
}
.w3-border,
.w3-border-left {
    border: none !important;
}

.w3-btn,
.w3-button,
.w3-btn:link,
.w3-btn:visited,
.w3-green {
    color: #52ae51 !important;
    background-color: #202020 !important;
    box-shadow: none !important;
}
.w3-btn:hover,
.w3-button:hover,
.w3-btn:link:hover,
.w3-btn:visited:hover,
.w3-hover-green:hover,
.w3-green:hover,
.w3-bar .w3-bar-item:hover {
    color: #6aca69 !important;
    background-color: #181818 !important;
    box-shadow: none !important;
}
.nextprev a:link,
.nextprev a:visited,
.nextprev a.w3-right,
.nextprev a.w3-left {
    color: #49a448;
    background-color: #262626;
    border: none;
}
.nextprev a:hover,
.nextprev a.w3-right:hover,
.nextprev a.w3-left:hover {
    color: #5cbd5b;
    background-color: #202020;
}
a.btnplayit,
a.btnplayit:link,
a.btnplayit:visited {
	color: #222;
    background-color: #52ae51;
}

.w3-example {
    color: #999 !important;
    background-color: #282828 !important;
	box-shadow: none !important;
}
.w3-code {
    background-color: #1a1a1a;
    border-left: 4px solid #4CAF50;
}
.w3-codespan {
    color: #14b977;
    background-color: #191919;
}
.sqlHigh > *,
.htmlHigh > *,
.cssHigh > *,
.jsHigh > * {
    filter: invert(100%);
}

hr {
    border-top: 2px solid #222;
}

.w3-table-all {
    border: none;
}
table.browserref:not(.w3-table-all) th {
    filter: invert(100%);
    -webkit-filter: invert(100%);
    background-color: #e5e5e5;
}
table.browserref th,
table.browserref tr:nth-child(odd),
.w3-table-all tr:nth-child(odd),
.tabler tbody tr:nth-child(odd) {
    color: #999;
    background-color: #1e1e1e;
    border: 1px solid #111;
}
table.browserref td,
.w3-table-all tr:nth-child(even),
.tabler tbody tr:nth-child(even) {
    background-color: #222;
    border: 1px solid #111;
}
.w3-bordered tr, .w3-table-all tr {
    border: none !important;
}
div[style*="img_bg_transparent.gif"],
div[style*="img_bg_transparent.gif"] > * {
    filter: invert(100%);
    -webkit-filter: invert(100%);
}
div[style*="background-color:rgba(255,"] {
    color: #eee;
}

/* Special rare elements */
.searchinput {
    color: #888;
    background-color: #303030;
    border: none;
}
.howto-container,
button.accordion,
button.accordion.activeacc,
button.accordion.active {
    color: #888;
    background-color: #2e2e2e;
}
button.accordion:hover {
    color: #999;
    background-color: #2a2a2a;
}
#belowtopnav {
	padding-top: 0 !important;
}
div.panel {
    background-color: #3e3e3e;
}
.tab button,
button.tablink,
ul.tab li a {
    color: #999;
}
.tab,
button.tablink,
ul.tab {
    border: 1px solid #222;
    background-color: #282828;
}
.tab button.active,
.tab button:hover,
button.tablink:hover,
ul.tab li a:hover,
ul.tab li a:focus,
ul.tab li .active {
    color: #aaa;
    background-color: #222;
}
.tabcontent {
    border-color: #222;
}
.tabcontent h1 {
    color: #eee;
}
ul.topnav2 li a {
    color: #aaa;
}
ul.topnav2 li a:hover {
    color: #aaa;
    background-color: #282828;
}
.w3-info,
.w3-note,
.w3-warning,
.w3-pale-blue,
.w3-sand,
.w3-boxmodel .margin,
.w3-boxmodel .padding,
.w3-boxmodel .content {
    color: #999 !important;
    background-color: #262626 !important;
}
.w3-boxmodel .border {
    color: #999 !important;
    background-color: #1e1e1e !important;
}

[style*="border:1px"] {
    border: 1px solid #222 !important;
}
[style*="border-top:none"] {
    border-top: none !important;
}
[style*="border-right:none"] {
    border-right: none !important;
}
[style*="border-bottom:none"] {
    border-bottom: none !important;
}
[style*="border-left:none"] {
    border-left: none !important;
}

/* Selected text */
::selection {
    color: #e9e9e9;
    background-color: rgba(0, 161, 79, 0.8)
}
::-moz-selection {
    color: white;
    background-color: rgba(0, 161, 79, 0.8)
}

/* Scrollbar */
::-webkit-scrollbar {
	width: 10px!important;
	height: 10px!important;
}
::-webkit-scrollbar-button {
	width: 0px!important;
	height: 0px!important;
}
::-webkit-scrollbar-thumb {
	background: #4b4b4b!important;
	border: none!important;
	border-radius: 0px!important;
}
::-webkit-scrollbar-thumb:hover {
	background: #555!important;
}
::-webkit-scrollbar-thumb:active {
	background: #5a5a5a!important;
}
::-webkit-scrollbar-track {
	background: #2f2f2f!important;
	border: none!important;
	border-radius: 0px!important;
}
::-webkit-scrollbar-track:hover {
	background: #262626!important;
}
::-webkit-scrollbar-track:active {
	background: #262626!important;
}
::-webkit-scrollbar-corner {
	background: transparent!important;
}
}

@-moz-document regexp("https://www.w3schools.com/.*/tryit.asp.*") {

/* Exercises pages */
html[lang="en-US"],
html[lang="en-US"] > body {
	height: 100%;
	overflow: hidden;
}
#container,
.trytopnav {
    position: unset;
    background-color: #202020;
}
#container {
    height: calc(100vh - 40px);
}
textarea,
#textareaCode {
	color: #888;
    background-color: #1a1a1a;
}
#textareawrapper {
    filter: invert(100%);
    -webkit-filter: invert(100%);
}


::-webkit-scrollbar-thumb {
	background: #bbb!important;
}
::-webkit-scrollbar-thumb:hover {
	background: #b0b0b0!important;
}
::-webkit-scrollbar-thumb:active {
	background: #a6a6a6!important;
}
::-webkit-scrollbar-track {
	background: #dbdbdb!important;
}
::-webkit-scrollbar-track:hover {
	background: #d2d2d2!important;
}
::-webkit-scrollbar-track:active {
	background: #d2d2d2!important;
}
}
