css:
@-moz-document regexp("^(?=.*lightnovelbastion\\.com)(?=.*death-mage).*$") {
/* Code to Style the Text and Scrollbar */
h1, h2, h3, h4, h5, h6, h7, h8, h9 {
    color: #eeeeee !important;
    font-family: Helvetica, Arial, sans-serif !important;
}
hr {
    border-color: #555555 !important;
}
select {
 	background-color: #555555 !important;
    color: #eeeeee;
}
a {
    color: #c69c6d !important;
}
.nav-links > * a {
 	background-color: #555555 !important;
}
blockquote {
 	background-color: #555555;
    color: #eeeeee;
    letter-spacing: unset;
    text-align: unset !important;
    border: unset;
}
b, strong {
    font-weight: unset !important;   
}
p,del {
    color: #eeeeee;
    font-size: 22px;
    margin-bottom: unset;
    letter-spacing: unset;
    text-align: unset !important;
}
.site-content,.entry-content,.entry-header, body {
    background: #333333 !important;
}
html {
    scrollbar-color: #555555 #333333;
}
.html::-webkit-scrollbar-track-piece {
    background-color: #333333;
}
.html::-webkit-scrollbar-thumb:vertical {
    height: 30px;
    background-color: #555555;
}
/* Code to center Novel text */
.entry-header {
	margin-top: 50px;
}
div[class="container"] {
    max-width: 700px !important;
    margin: 0 auto !important;
}
div[id="content"] {
    margin: 0 auto !important;
    padding: unset !important;
}
.hentry {
    margin: unset !important;
    padding: unset !important;
}
header[class="entry-header"] {
    margin: unset !important;
    padding: unset !important;
    padding-top: 30px !important;
    max-width: unset !important;
    hyphens: none;
}
div[class="entry-content"] {
    margin: unset !important;
    padding: unset !important;
    max-width: unset !important;
    hyphens: none;
}
.content-area{
    margin: unset !important;
    padding: unset !important;
}
/* Code to remove content from webpage except for the Novels text and navigation. */
 div[class="lnbad-tag lnbad-tag-6"] {
    display: none;
}
div[id="adb-container"] {
    display: none;
}
div[class="wp-manga-section"] {
    display: none;
}
div[class="go-to-top active"] {
    display: none;
}
div[id="cookie-notice"] {
    display: none
}
div[id="wp-a11y-speak-polite"] {
    display:none;
}
div[id="wp-a11y-speak-assertive"] {
    display: none;
}
header[class="site-header"] {
    display:none;
}
div[class="c-sidebar c-top-sidebar wp-manga"] {
    display: none;
}
footer[class="site-footer"] {
    display: none;
}
div[id="cookie-notice"] {
 	display: none !important;   
}
div[class="row "] {
 	display: none;   
}
div[class="lnbad-tag lnbad-tag-11"] {
 	display: none;   
}
div[class="ad c-ads custom-code body-bottom-ads"] {
 	display: none;   
}
div[class="entry-header_wrap"] {
 	display: none;   
}
div[class="lnbad-tag lnbad-tag-1"] {
 	display: none;   
}
}
