css:
@-moz-document domain("bloxtopia.xyz") {
.box {
    background-color: #444 !important;
    box-shadow: 0 2px 3px rgb(10, 10, 10), 0 0 0 1px rgba(10,10,10,.1) !important;
    color: #ffffff !important;
}

.title {
    color: #ffffff !important;
}

body {
    background-color: #2d2d2d !important;
}

.navbar.has-shadow {
    box-shadow: 0 2px 0 0 #1f1f1f !important;
    background-color: #232323 !important;
}

.navbar-link.is-active, .navbar-link:hover, a.navbar-item.is-active, a.navbar-item:hover {
    background-color: #272727 !important;
    color: #ffffff !important;
}

.navbar-link.is-active, .navbar-link, a.navbar-item.is-active, a.navbar-item {
    color: #ffffff !important;
}

.footer {
    background-color: #1b1b1b !important;
}

html {
    background-color: #171717 !important;
}

.menu-list a.is-active {
    background-color: #ffffff !important;
    color: #000 !important;
}

.menu-list a {
    color: #ffffff !important;
}

.menu-list a:hover {
    color: #000 !important;
}

.button.is-info[disabled], fieldset[disabled] .button.is-info {
    background-color: #ffffff !important;
    color: black !important;
}

.button.is-info {
    background-color: #ffffff !important;
    color: #000 !important;
}

p {
    color: white !important;
}

a:hover {
    color: #ffffff !important;
}
a {
    color: #cacaca !important;
}

.navbar-link:not(.is-arrowless)::after {
    border-color: #ffffff !important;
}

.navbar-dropdown {
    background-color: #000 !important;
    border-top: 2px solid #292929 !important;
    box-shadow: 0 8px 8px rgba(10, 10, 10, 0) !important;
}

.navbar-item.is-active .navbar-dropdown, .navbar-item.is-hoverable:hover .navbar-dropdown {
    background-color: #383838 !important;
}

.navbar-divider {
    background-color: #ffffff !important;
}

.navbar-item.has-dropdown:hover .navbar-link {
    background-color: #1f1f1f !important;
}

.textarea {
    background-color: #212121 !important;
    border-color: #dbdbdb !important;
	color: #ffffff !important;
}

.subtitle {
    color: #ffffff !important;
}

.dropdown-content {
    background-color: #4a4a4a !important;
}

a.dropdown-item:hover, button.dropdown-item:hover {
    background-color: #3a3a3ad1 !important;
}

strong {
    color: #ffffff !important;
}

.modal-card-foot, .modal-card-head {
    background-color: #444444 !important;
}

.modal-card-body {
    background-color: #2f2f2f !important;
}

.textarea::placeholder {
	color: #ffffff !important;
}

.input {
    background-color: #212121 !important;
    color: #ffffff !important;
}

.input::placeholder {
    background-color: #212121 !important;
    color: #ffffff !important;
}

.label {
    color: #ffffff !important;
}

.table {
    background-color: #2d2d2d !important;
    color: #ffffff !important;
}

.table thead th {
    color: #ffffff !important;
}

h1 {
    color: #ffffff !important;
}

center {
	color: #ffffff !important;
}
}
