css:
@-moz-document url-prefix("https://sites.google.com") {
#sites-app-title > span:nth-child(1) > .sites-site-name, #sites-revision-info, .sites-ccc-nav > div > div > div > div > div > div > a[role="button"] {
   color: white !important;
}
.sites-ccc-nav > div > div, #collaborator-bar-container {
   background: #333333 !important;
}
#sites-app-title, #page-btns {
   background: unset;
}
.sites-ccc-nav > div > div > div > div > div > div > a[role="button"]>span {
   filter: invert(1);
}

/* fix the Bar Title Hover behavior */
.sites-site-name:hover, .sites-site-name:focus {
   background-color: #555 !important;
}
.sites-collaborator-bar-back-arrow {
   filter: invert(1);
}
}
