css:
@-moz-document domain("forum.snahp.it") {
.logo {
    background: #2ebb00 url(https://i.imgur.com/MIVgqIl.png);
    border-color: #000;
    box-shadow: 0 5px 15px 5px rgba(0,0,0,.2);
}

.headerbar {
    color: #eee;
    background: url(https://i.imgur.com/3TWD7l5.png);
}

.wrap {
    background-color: #000;
    border-color: #1d1d1d;
    box-shadow: 0 0 3px 1px #000;
}

.navbar {
    background-color: #1d1d1d;
}

.navbar a {
    color: #2ebb00;
}

.navbar-breadcrumbs a {
    color: #2ebb00;
}

.search-header {
    border-color: #1d1d1d;
    background-color: #000;
}

.button {
    border-color: #000;
    background-color: #1d1d1d;
    color: #000;
}

.button .icon:before {
    padding-right: 0;
    color: #2ebb00;
}

.button:hover, .button:focus {
    border-color: #373737;
    background-color: #373737;
    color: #bc2a4d;
}

.forabg {
    background-color: #1d1d1d;
}

.forumbg .header a, .forabg .header a, th a {
    color: #fff;
}

li.header dt, li.header dd {
    color: #fff;       
}

a {
    color: #2ebb00;
}

li.row {
    border-bottom-color: #565656;
}

h3 {
    border-bottom-color: #565656;
}

ul.linklist>li.rightside, p.rightside, a.rightside {
    float: right;
    margin-right: 0;
    margin-left: 7px;
    text-align: right;
    color: white;
}

.copyright {
    color: #fff;
    border-color: transparent;
}

.copyright a {
    text-decoration: underline;
    color: #2ebb00;
}

.icon.icon-blue, a:hover .icon.icon-blue {
    color: #1663a6;
}

.icon.icon-red, a:hover .icon.icon-red {
    color: #b5284a;
}

.forum_read {
    background-image: url(https://i.imgur.com/HLjb5Sn.png);
}

.dropdown .dropdown-contents {
    background: #000;
    border-color: #1d1d1d;
    box-shadow: 0 3px 5px #000;
}

.navbar .dropdown-contents a {
    color: #2ebb00;
}

.fa-caret-up:before {
    content: "\f0d8";
    color: #2ebb00;
}

.dropdown li.separator {
    border-color: #565656;
}

.dropdown .pointer {
    border-color: #565656 transparent;
}

.dropdown .dropdown-contents {
    background: #000;
    border-color: #565656;
    box-shadow: 0 3px 5px #000;
}

.dropdown-extended .header {
    background-color: #000;
    color: #eee;
    border-bottom-color: #565656;
}

.dropdown-extended ul li {
    border-top-color: #565656;
}

.dropdown-extended .footer {
    border-top: 1px solid #565656;
}

.phpbb_alert {
    background-color: #000;
    border-color: #1d1d1d;
}

.forum_link {
    background-image: url(https://i.imgur.com/BXIxuyC.png);
}

.forum_read_subforum {
    background-image: url(https://i.imgur.com/dOW7AFF.png);
}
}

@-moz-document domain("forum.snahp.it") {
.action-bar .search-box {
    border-color: #1d1d1d;
}

.breadcrumbs .crumb:before {
    content: '\2039';
    font-weight: 700;
    padding: 0 .5em;
    color: #2ebb00;
}

.pagination li a {
    background: #000;
    filter: none;
    border-color: #1d1d1d;
    box-shadow: none;
    -webkit-box-shadow: none;
    color: #2ebb00;
}

.pagination li.active span {
    background: #2ebb00;
    color: #000;
}

.forumbg {
    background-color: #1d1d1d;
}

.button {
    border-color: #000;
    background-color: #1d1d1d;
    color: #2ebb00;
}

.row .pagination li a {
    background: #2ebb00;
    color: #000;
    border: none;
    font-size: 1em;
}

.topic_read_mine {
    background-image: url(https://i.imgur.com/qDMuTXO.png);
}

.topic_read_locked {
    background-image: url(https://i.imgur.com/wLG2tky.png);
}

.topic_read_locked_mine {
    background-image: url(https://i.imgur.com/TkmepYc.png);
}

ul.topiclist dd {
    border-left-color: #565656;
}

.pm_read, .topic_read {
    background-image: url(https://i.imgur.com/HLjb5Sn.png);
}

.global_read_locked_mine {
    background-image: url(https://i.imgur.com/hKcuBzK.png);
}

.sticky_read {
    background-image: url(https://i.imgur.com/4o4OKc1.png);
}

.sticky_read_locked {
    background-image: url(https://i.imgur.com/Za9kqNo.png);
}

.sticky_read_mine {
    background-image: url(https://i.imgur.com/pHS3lsM.png);
}

.announce_read_locked {
    background-image: url(https://i.imgur.com/p7WO40H.png);
}
}

@-moz-document domain("forum.snahp.it") {
.codebox {
    background: #000 url(https://i.imgur.com/en4qhzF.png) repeat-x;
    border-color: #1d1d1d;
}

.codebox {
    padding-top: 13px;
    border: 1px solid #565656;
    border-radius: 6px;
    font-size: 1em;
    margin: 1em 0 1.2em;
    word-wrap: normal;
}

.codebox p {
    border-bottom-color: #565656;
}

div.content>dl.hidebox.hi {
    background-color: #2ebb00;
    border: 0;
}

.notice {
    border-top-color: #565656;
}

.postprofile {
    color: #eee;
    border-color: #565656;
}

blockquote {
    background-color: #222;
    border-color: #565656;
}

.content h2, .panel h2 {
    color: #fff;
    border-bottom-color: #565656;
}

.inputbox {
    background-color: #000;
    border-color: #565656;
    color: #eee;
}

a.button1, input.button1, input.button3, a.button2, input.button2 {
    color: #000;
    background-color: #2ebb00;
}

a.button1:hover, input.button1:hover, a.button2:hover, input.button2:hover, input.button3:hover {
    border-color: #35d701;
    color: #d31141;
    background-color: #35d701;
}

.pagination li a:hover, .pagination li a:hover .icon, .pagination .dropdown-visible a.dropdown-trigger, .nojs .pagination .dropdown-container:hover a.dropdown-trigger {
    background: #39ec00;
    border-color: #39ec00;
    filter: none;
    color: #da3434;
    text-shadow: none;
}
}

@-moz-document domain("forum.snahp.it") {
.tabs .activetab>a, .tabs .activetab>a:hover {
    background-color: #222222;
    border-color: #222;
    color: #d31141;
}

.tabs .tab>a {
    background: #222222;
    color: #2ebb00;
}

.navigation .active-subsection a {
    background: #000;
    color: #2ebb00;
}

.navigation a {
    color: #2ebb00;
    background: #111;
}

.panel-container .panel li.row {
    border-bottom-color: #565656;
}

ul.cplist {
    border-top-color: #565656;
}

.panel-container h3, .panel-container hr, .cp-menu hr {
    border-color: #565656;
}


}

@-moz-document domain("forum.snahp.it") {
.inputbox:hover {
    border-color: #9e9e9e;
}

hr {
    border-color: #565656;
    border-top-color: #565656;
}

table.table1 tbody tr:hover, table.table1 tbody tr.hover {
    background-color: #373737;
    color: #000;
}
}
