css:
@-moz-document domain("tvmaze.com") {
/* Dark Website assets */

:root {      
    --main-bg: /*[[mainbg]]*/;
    --alt-bg: /*[[altbg]]*/;
    
    --main-accent: /*[[mainaccent]]*/;
    --alt-accent: /*[[altaccent]]*/;
    
    --main-text: #ddd;
    --alt-text: #fff;
    
    --ran-1: #6BD6CB;
    --ran-2: #3F3F3F;
    --ran-3: #347f78;
    --ran-4: #b9e3de;
    --ran-5: #3C948B; 
    
    /*
    --main-bg: #333;
    --alt-bg: #222;
    
    --main-accent: #3C948B;
    --alt-accent: #005B61;
    
    --main-text: #347f78;
    --alt-text: white;
    */
}

/* * = All, Baseline changes to all elements  */

* {
    box-shadow: none !important;
    text-decoration: none !important;
    border-left: none !important;
    border-right: none !important;
    border-top: none !important;
    border-color: var(--main-bg) !important;
    border-bottom-color: var(--main-accent) !important;
}


/* Change the background of these elements to match /--main-bg/ */
body, .cell, select, select > option, label, .button, #following:hover, #user-info .callout, .cell .rate, #w0 > table tr {
    background: var(--main-bg) !important;
}

/* Change the background of these elements to match /--alt-bg/ */
#site-header, .breadcrumbs, [role=menuitem] > a:hover, .cell .today, #fp-articles > div > div > div, #user-menu, #personal, header > nav > ul > li > a:hover, #countdown-shows-today > div > article > div > header, #popular-shows-today > div > article > div > header, #logo, div.small-1.medium-1.cell.network, div.content.auto.cell, .fa-ul li, #countdown article section, #countdown article > div, #airing-today tr, .cell .today, .cell .today:hover, #sticky-container .menu {
    background: var(--alt-bg) !important;
}


/* Change the background of these elements to match /--main-accent/ */
#calendar-header, #calendar-header > div, article > div > hgroup, #w1 > div > div > div, .following, .rate, #site-navigation, hgroup.title.content, div.title, #personal > ul > li > a:hover, #tools > ul > li > a:hover, #contributor-points:hover, .pagination .current, .pagination a:hover, span.rate.auto.cell.dropdown-rating {
    background: var(--main-accent) !important;
}

/* Change the background of these elements to match /--alt-accent/ */
#general-info-panel, #show-tags, .callout, #episode-list-short tr, #airing-today th, .label, div.row.hours > div, div.columns.followed, .char-names, .fa-ul, #countdown div > header, .dayofmonth, li.active, time > em, time > strong, #w0 > table th, .is-active {
    background: var(--alt-accent) !important;
}

/* Change the background of these elements to be None */
.card, .entry:hover, .js-fulltriwatch > div > div, .airtime, .follow-toggle, .epname a, span.title a, hgroup.title, #sticky-container > div, #w0 > div:nth-child(2) > article {
    background: none !important;
}


/* Color Text to match /--main-text/ (#ddd) */
body, .cell, select, select > option, label, .button, #following:hover, #user-info .callout, .cell .rate {
    color: var(--main-text) !important;
}

/* Color Text to match /--alt-text/ (#fff) */
h1, h2, h3, h4, h5, a:hover, #contributor-points a, .pagination a, span.title a:hover {
    color: var(--alt-text) !important;
}

/* Add a Border-Bottom with the properties: 1px / solid / main-accent */
#search .button, .calendar .entry, #search input, #forum-section article, #forum-topic article, #topics-added li, #thread .comment, #search .button, .calendar .entry, #sticky-container > div, article.grid-x.episode-row:hover {
    border-bottom: 1px solid var(--main-accent) !important;
}

/* Add a Border-Bottom with the properties: 1px / solid / alt-accent */
#airing-today tr, #w0 table tr {
    border-bottom: 1px solid var(--alt-accent) !important;
}


/* Search Boxes & Buttons */
input, .postfix, .button, #search button, #forum-section input[type=text], #forum-topic input[type=text], input[type=text]:nth-child(2) {
    text-align: center !important;
    background: var(--alt-bg) !important;
    color: var(--alt-text) !important;
    border-radius: 0 !important;
    margin: 0px;
}

.button:hover, #following, #contributor-points, #achievements > section > section > ul > li {
    background-color: var(--main-accent) !important;
    color: var(--main-bg) !important;
}

/* Edit specific Margin / Paddings */
#calendar-wrap > section > section > header {
    margin: 3px 0;
}

#sticky-container > div {
    padding: 0px;
}

.cell .today {
    margin: 1px;
}

/* Unique Color Patterns & background: none (non important)*/
.airtime, .follow-toggle, .epname a, span.title a {
    color: var(--alt-accent) !important;
}

.title {
    background: none;
}

/* Links Colors */
a, #popular-shows-today a, #countdown-shows-today a {
    background: none;
    color: var(--main-accent);
}

/* /Schedule */
div.row.shows > div {
    background: var(--main-bg);
    color: var(--main-accent);
    border-right: 1px solid var(--main-accent) !important;
}

/* Special properties */
#countdown > section > section {
    flex: none;
}

.dayofmonth, li.active, time > em, time > strong, #calendarsettings > select, .tributton-watch > div {
    border: none !important;
}

article.grid-x.episode-row {
    border-bottom: 1px solid var(--main-bg) !important;
}

.title, .description, #fp-articles footer {
    text-align: center !important;
}

#w1 div > div > footer > div {
    border-top: 1px solid var(--alt-accent) !important;
}
}

@-moz-document domain("tvmaze.com") {
/* Remove Ads, and other unwanted elements */
body > div.row.hide-for-print.collapse > div > section,
body > div:nth-child(1n) > div > section > span,
.blad,
.blad-300x600,
.cinereus {
    display: none;
}
}
