	height: 4px;
	background-color: var(--theme-color);
	transform: scalex(.6);
}

/*小电视加载*/
.load-state .loading:before {
	filter: var(--filter-darken);
}

/*筛选列表、综合排序、最多点击、全部时长、电视剧那里*/
.filter-wrap {
	border: none;
	width: 980px;
}

.filter-wrap .filter-item {
	transition: .25s;
	border-radius: 20px;
}

.filter-wrap .filter-item:not(.active):hover {
	background-color: var(--fill-primary)
}

.filter-wrap .filter-item.active {
	box-shadow: 0 3px 8px var(--theme-light-color);
	background-color: var(--theme-color);
}

.filter-wrap .filter-item a {
	color: var(--text-primary)!important;
}

.filter-wrap .filter-item.active a {
	color: white!important;
}

.filter-wrap .filter-item:hover:not(.active) a {
	color: var(--text-primary)!important;
}

.sub-filter {
	background-color: var(--fill-secondary)!important;
	border: none!important;
	z-index: 200;
	border-radius: 50px!important;
}


/*去除箭头*/
.filter-wrap .icon-tid2-arr {
	display: none;
}

.sub-filter .filter-item {
	background-color: transparent!important;
	box-shadow: none!important;
	border-radius: 50px;
	overflow: hidden;
}

.sub-filter .filter-item:hover {
	background-color: var(--fill-secondary)!important;
}

.sub-filter .filter-item.active {
	background-color: var(--fill-primary)!important;
}

.sub-filter .filter-item a,
.sub-filter .filter-item.active a {
	color: var(--text-primary)!important;
}

/*更多筛选、收起*/
.filter-wrap .fold i {
	filter: var(--filter-white);
}

.filter-wrap .fold {
	right: 20px;
	width: auto;
	padding: 0 10px;
	color: var(--text-secondary);
	border-radius: 20px;
	transition: .25s;
}

.filter-wrap .fold.down {
	width: 74px;
}

.filter-wrap .fold:hover {
	color: var(--theme-color);
	background-color: var(--fill-primary);
}

/*切换列表/表格显示*/
.switch-wrap {
	right: 20px;
	filter: var(--filter-blue-turn-pink);
}

.switch-wrap div:not(.active):hover i {
	filter: var(--filter-white);
}

/*居中显示搜索的结果*/
.result-wrap,
.live-result {
	width: 1020px;
	margin: 0 auto;
}

/*显示结果列表*/
.up-item,
.all-class,
.synthetical,
.user-item,
.article {
	animation: .6s floatUp ease-out;
}

/*无结果*/
.error-wrap,
.recom-wrap .no-result {
	background-color: var(--bg-primary);
	border: none;
	border-radius: 0 0 var(--radius) var(--radius);
	margin-top: 0;
}

/*类型标识统一调整，如指视频列表模式显示时标题左边显示《影视剪辑》并带圆角边框，与番剧影视标题左边显示对应类型的《番剧》、《影视》*/
.video.list .type,
.act-card-wrap .act-card .type,
.bangumi-list .bangumi-item .right-info .headline .bangumi-label,
.pgc-list .pgc-item .right-info .headline .pgc-label {
	background-color: var(--fill-secondary);
	border: none!important;
	color: var(--text-tertiary)!important;
}

/*找出的av视频结果*/
.video.list .headline.singleav .avid {
	color: white!important;
	box-shadow: 0 3px 8px var(--pink-light);
}

/*相关结果信息，主要是游戏*/
.act-part,
/*番剧*/
.bangumi-list,
/*影视*/
.pgc-list,
.gather-wrap {
	background-color: var(--bg-primary);
	border: none;
	border-radius: var(--radius);
	box-shadow: var(--card-shadow);
	overflow: hidden;
}

/*影视*/
.pgc-item,
/*番剧*/
.bangumi-item {
	background: transparent;
	margin: 0!important;
	padding: 20px;
	border: none!important;
	transition: .25s;
}

.act-part {
	margin-top: 20px;
}

.pgc-item:hover,
.bangumi-item:hover {
	background-color: var(--fill-quaternary);
}

/*孤儿，总是那么特别*/
.gather-wrap .gather,
.act-card {
	transform: scale(.96);
	border-color: transparent!important;
}

/*相关结果反馈按钮*/
.up-feedback .up-fb-btn:hover {
	background-color: var(--bg-tertiary)!important;
}

/*视频、相册卡片效果*/
.video-contain .video,
.photo-list .photo {
	overflow: hidden;
	animation: .6s showVideo;
	transition: background .25s;
	background-color: var(--bg-primary);
	border: none;
	border-radius: var(--radius);
	box-shadow: var(--card-shadow);
}

/*前面容器宽度调了，视频、相簿、专栏文章宽度、高度也一起调整防止图片变形*/
.video.matrix,
.photo-list .photo {
	width: 178px;
}

.video.matrix {
	height: 220px;
}

.video.matrix .img {
	height: 110px;
}

.photo-list .photo {
	height: 266px;
}

.photo-list .photo .img {
	height: 178px;
}

.video.list,
.article-wrap .article {
	width: 848px;
}

/*游戏标题*/
.act-card .title,
/*视频标题*/
.video .title,
/*相簿*/
.photo .title {
	color: var(--text-primary)!important;
	transition: .25s!important;
}

.act-card .title:hover,
.video .title:hover,
.photo .title:hover {
	color: var(--blue)!important;
}

.video-contain .video,
.video-contain .video .lazy-img,
.photo-list .photo,
.photo-list .photo img {
	transition: .25s
}

.video .img,
.photo-list .photo .img {
	border-radius: 0!important;
}

.video-contain .video:hover,
.photo-list .photo:hover {
	background-color: var(--elevated-primary);
	box-shadow: var(--card-shadow2)!important;
}

.video-contain .video:hover .lazy-img,
.photo-list .photo:hover img {
	transform: scale(1.1);
}

.video-contain .video.list {
	margin-bottom: 20px;
	border-radius: var(--radius);
}

.video.list {
	padding-top: 0;
	padding-bottom: 0;
	margin-top: 20px;
	animation: .6s floatUp;
}

.video.list .img {
	top: 0;
	left: 0;
}

.video.list .info,
.video.list .tags {
	margin-left: 10px;
}

.video.list .info {
	margin-top: 8px;
}

.video.list .tags {
	bottom: 5px;
}

.video.list .des {
	margin-top: 5px;
}

/**
 **番剧
 **/
.bangumi-list {
	margin-top: 20px;
}

.bangumi-list .synthetical {
	border-bottom: 1px solid var(--bg-secondary);
}

/*卡片标题（番剧、影视）*/
.right-info .headline .title,
/*卡片信息*/
.right-info .intros .value {
	color: var(--text-primary)!important;
	transition: .25s;
}

.right-info .headline .title:hover {
	color: var(--blue)!important;
}

/*更多番剧结果*/
.card-more {
	background-color: var(--bg-secondary);
}
.card-more a {
	color: var(--blue);
}

/*番剧、影视选集按钮，防止宽度太长从而挤到下一行*/
#pgc-navigate-wrap .ep-box.grid .ep-sub .ep-item {
	width: 50px;
}

.ep-item {
	transition: .25s;
	color: var(--text-primary)!important;
	background-color: var(--fill-secondary);
	border-color: transparent!important;
}

.ep-item:hover {
	color: #fff!important;
}

.card-more {
	padding-top: 15px;
	border-radius: 0 0 var(--radius) var(--radius);
}

/**
 **直播
 **/
/*直播全部与主播结果筛选*/
.live-wrap .live-tabs {
	/*对其上面*/
	margin-top: 0;
	padding: 20px 20px 30px;
	border-radius: 0 0 var(--radius) var(--radius);
	background-color: var(--bg-primary);
	box-shadow: var(--card-shadow);
}

/*全部、主播筛选*/
.live-wrap .live-tabs a {
	color: var(--text-secondary)!important;
}
.live-wrap .live-tabs a.active {
	color: var(--text-primary)!important;
	border-color: var(--theme-color);
}

.live-wrap .live-user,
.live-wrap .live-room {
	background-color: var(--bg-primary);
	padding: 30px 20px 30px;
	border-radius: var(--radius);
	box-shadow: var(--card-shadow);
}

/*主播、直播间标题调整*/
.live-wrap .headline h3,
.live-wrap .live-room .headline h3 {
	color: var(--text-primary);
	font-size: 20px;
	font-weight: 600;
}

.live-tabs a:not(.active):not(:hover) {
	color: var(--text-primary)!important;
}

.user-item,
.room-item {
	transition: .25s;
	background-color: var(--elevated-primary);
	border-radius: var(--radius)!important;
	box-shadow: 0 0 8px var(--shadow-color)!important;
	overflow: hidden;
}

.user-item:hover,
.room-item:hover {
	transform: scale(1.1);
	background-color: var(--elevated-secondary);
	box-shadow: 0 0 30px 5px var(--shadow-dark-color)!important;
}

/*主播名字调整*/
.live-user .user-item .item-right .uname {
	color: var(--text-primary);
}

/*收藏人数，直播类型*/
.live-user .user-item .item-right .info .star,
.live-user .user-item .item-right .info .type {
	color: var(--text-secondary);
}

/*直播间*/
.room-item .item-info {
	padding: 10px 0;
	position: relative;
	left: 10px;
}

/*按钮调整*/
.live-wrap .headline a {
	transition: .25s;
	padding: 5px;
	background-color: var(--fill-secondary);
	border: none;
	border-radius: 4px;
	color: var(--text-tertiary)!important;
}

.live-wrap .headline a:hover,
.live-wrap .headline a.active {
	background-color: var(--theme-color);
	color: #fff!important;
}

.live-wrap .live-room .headline .filter a i {
	filter: var(--filter-white);
}

.live-wrap .live-room .headline .filter a:hover i,
.live-wrap .live-room .headline .filter a.active i {
	filter: contrast(0) brightness(2);
}

/**
 **专栏、话题通用
 **/
/*专栏、话题用户卡片效果*/
.article-wrap .article,
.topic-wrap .synthetical,
.user-list .up-item {
	background-color: var(--bg-primary);
	margin-top: 20px;
	border: none;
	border-radius: var(--radius);
	transition: .25s;
	box-shadow: var(--card-shadow);
}

.article-wrap .article:hover,
.topic-wrap .synthetical:hover,
.user-list .up-item:hover {
	background-color: var(--elevated-primary);
	box-shadow: var(--card-shadow2);
}

/*右侧信息调整*/
.article-wrap .article .info,
.topic-wrap .synthetical .right-info,
.topic-wrap .synthetical .detail {
	width: 800px!important;
	height: 90px!important;
	overflow: hidden;
	position: relative;
	left: 20px!important;
}

.article-wrap .article .tags,
.topic-wrap .synthetical .detail {
	bottom: 0!important;
}

.user-list .up-item .info-wrap {
	padding: 20px;
}

/*左侧图片调整*/
.topic-wrap .synthetical .left-img {
	margin-left: 20px;
	margin-right: 20px!important;
}

.user-list .up-item .up-face {
	margin: 20px;
}

.article-wrap .article .cover {
	right: 20px;
}

/**
 **专栏
 **/
.article-wrap .article .title {
	transition: .25s;
	color: var(--text-primary);
}



/**
 **用户
 **/
/*下拉筛选、默认排序、全部排序*/
.user-wrap .dropdown-wrap .select-wrap {
	border: none;
}

.dropdown-wrap {
	margin-bottom: 20px;
	background-color: var(--bg-primary);
	padding-bottom: 20px;
}

.user-wrap .dropdown-wrap .total-wrap {
	padding-top: 0;
}

.user-wrap .dropdown-wrap .bili-dropdown {
	transition: .25s;
	top: -5px;
	height: auto!important;
	padding: 5px!important;
}

.user-wrap .dropdown-wrap .bili-dropdown span {
	color: var(--text-tertiary)!important;
}

.user-wrap .dropdown-wrap .bili-dropdown i {
	filter: var(--filter-white);
}

/*用户名字、视频标题调整*/
.user-list .up-item .title,
.user-list .up-item .up-videos .video-item .video-desc {
	transition: .25s;
	color: var(--text-primary);
}

/*稿件、粉丝*/
.user-list .up-item .up-info > span,
/*自我介绍*/
.user-list .up-item .desc {
	color: var(--text-secondary);
}

/*全部x个稿件*/
.user-list .up-item .up-videos .video-more {
	transition: .25s;
	color: var(--blue);
}

/**
 **底部
 **/
.page-wrap .pager {
	background-color: transparent;
}
.footer-wrp {
	display: none;
}
}

@-moz-document domain("space.bilibili.com") {
/***************空间主页****************/
body {
	padding-bottom: 20px;
}

/*直播间、公告、最近玩过的游戏。。。标题*/
.section-title,
.section-title .t {
	color: var(--text-primary);
}

/**
 **空间顶部背景部分
 **/
/*将容器调至100%以至于个人自定义背景能完全拉伸填充浏览器宽度*/
.h > .wrapper {
	width: 100%!important;
	/* 	max-width: 2560px; */
	max-width: unset;
}

/*用padding将高度调大，以至于不会让原来卡片长宽比改变*/
.h .h-inner {
	padding-top: 120px;
}

/*暗黑色渐变遮罩，用于在亮色背景中更加清楚的显示文字*/
.h .h-gradient {
	height: 160px;
	background: linear-gradient(to bottom, transparent, rgba(0, 0, 0, .4));
}

/*视角：我自己、新访客、我的粉丝*/
.h .be-dropdown.h-version {
	position: absolute;
	width: 120px;
	left: 50%;
	margin-left: 430px;
}

@media (min-width: 1420px) {
	.h .be-dropdown.h-version {
		margin-left: 520px;
	}
}

/*用户名、介绍、头像部分*/
.h .h-info {
