}


/*空间滑动导航条*/
.n .n-cursor {
	height: 0;
}

.n .n-cursor:after {
	content: '';
	position: absolute;
	width: 100%;
	height: 4px;
	background-color: var(--theme-color);
	transform: scalex(.8) translatey(-5px);
}

/*空间导航栏*/
.n-tab-links a {
