/*去除手机上观看与下载客户端按钮*/
#playpage_mobileshow,
.app-download,
.app-box {
	display: none;
}

/*助手调整*/
#arc_toolbar_report > .bilibili-helper > span {
	color: var(--text-tertiary);
}

#arc_toolbar_report > .bilibili-helper:hover > span {
	color: var(--blue);
}

/*活动信息*/
.activity-m .inside-wrp {
	background-color: var(--bg-primary);
	border: none;
	color: var(--text-primary);
	margin: 0;
	/* 	margin-bottom: 20px; */
	border-radius: var(--radius) var(--radius) 0 0;
}

.activity-m .inside-wrp .inside-bg:before {
	display: none!important;
}

/*标签信息*/
#v_tag li {
	transition: .2s;
	border: none;
}

#v_tag li:not(:hover) {
	background-color: var(--fill-secondary)!important;
}

#v_tag li > a {
	color: var(--text-tertiary);
}

#v_tag li:hover {
	background-color: var(--fill-primary);
}

/*添加视频标签*/
.s_tag .tag-area > a:not(:hover) {
	border-color: transparent!important;
}

.s_tag .tag-area > a:hover {
	background-color: transparent!important;
}

/*视频标签*/
.tag-info-pane {
	background-color: var(--elevated-secondary)!important;
	box-shadow: var(--card-shadow2)!important;
	border-radius: var(--radius);
}

.tag-info-pane .btn-right-box {
	border: none;
}

.tag-info-pane a,
.tag-info-pane .text {
	color: var(--text-primary)!important;
}

.tag-info-pane a:hover {
	color: var(--blue)!important;
}

.tag-info-pane a.btn-close:hover,
.tag-info-pane a.btn-report:hover {
	filter: none;
	background-color: var(--fill-secondary)!important;
}

/*标签修改记录、查看标签使用说明*/
.s_tag .btn-view-tag a {
	color: var(--text-secondary);
}

.video-desc-m .reprint/*未经作者授权 禁止转载*/
,
.video-desc .reprint {
	color: var(--pink);
}

.video-desc-m .info {
	color: var(--text-tertiary)!important;
}

/*去广*/
.video-bottom-ad {
	display: none;
}

/*看过该视频的还喜欢*/
.also-like .also-like__title,
.video-like-m .title {
	color: var(--text-primary)!important;
}

#recommend_report,
.also-like {
	transform: scale(.95);
	margin: 0!important;
}

/**
 **右侧内容
 **/
.r-wrapper {
	padding-top: 0;
}

/*充电榜*/
.elecrank-wrapper {
	background-color: var(--bg-primary)!important;
	padding: 0px;
}

.elecrank-wrapper [class*=elecrank] {
	background-color: transparent!important;
	border: none;
}

/*名字调整，防止过界限*/
.elecrank-header .name {
	margin-top: 20px;
}

.elecrank-header .name,
.elecrank-wrapper .elecrank-header .name {
	background-color: transparent;
	color: var(--blue)!important;
}

.elecrank-header .name:hover {
	color: var(--pink)!important;
}


/*历史充电人数*/
.elecrank-header .title {
	margin-top: 5px;
	color: var(--text-primary);
}

/*本作品充电、本月所有充电标签栏*/
.elecrank-header .rank-tab {
	margin-top: 20px;
}


/*充电用户名的容器*/
.elecrank-wrapper .elecrank-content {
	margin-top: 20px;
}

/*排行数字*/
.elecrank-wrapper .elecrank-content .rank-item .item-num {
	background-color: var(--fill-primary);
}

/*充电用户名*/
.elecrank-wrapper .elecrank-content .rank-item .item-name {
	color: var(--text-primary);
}

/*充电用户名留言*/
.elecrank-wrapper .elecrank-content .rank-item .msg {
	color: var(--text-tertiary);
	background-color: var(--fill-secondary);
	border-radius: 0 8px 8px 8px;
}

.elecrank-wrapper .elecrank-content .rank-item .msg .arrow {
	display: none;
}

/*大家都在看的直播*/
#live_recommand_report {
	display: none;
}
}

