	box-shadow: none!important;
	border-bottom: 1px solid rgba(120, 120, 128, .2);
}

/*去除所有杂七杂八的，仅保留搜索栏🌚*/
/*这个使顶栏的左侧内容，包含音频区、直播区、商店、漫画下载等等乱七八糟的超链接*/
.nav-con.fl,
/*大会员，，*/
[report-id="big_tab_click"],
/*分区导航栏*/
#primary_menu,
/*接下来是首页大部分内容的容器*/
#app > .bili-wrapper,
/*右侧位置跳转导航条与向上按钮*/
.elevator-module,
.bili-footer {
