}

	/*粉色亮色*/
	--bgcolor: rgb(239, 239, 244);

	--topbar-color: rgba(255, 255, 255, var(--topbar-opacity));

	--bg-primary: rgba(255, 255, 255, var(--opacity));
	--bg-primary-solid: rgb(255, 255, 255);
	--bg-secondary: rgba(239, 239, 244, var(--opacity));
	--bg-secondary-solid: rgb(239, 239, 244);
	--bg-tertiary: rgba(220, 220, 234, var(--opacity));
	--bg-tertiary-solid: rgb(220, 220, 234);

	--blue-light: hsla(195, 100%, 42%, .4);
	--pink-light: hsla(340, 85%, 66%, .4);
	--yellow-light: hsla(33, 95%, 61%, .4);
	--green-light: hsla(142, 61%, 55%, .4);

	--theme-color: var(--pink);
	--theme-light-color: var(--pink-light);

	--content-border-color: transparent;

	--line-color: rgb(231, 231, 240);

	--text-primary: #000;
	--text-secondary: hsl(0, 0%, 50%);
	--text-tertiary: hsl(0, 0%, 20%);

	--fill-primary: rgba(120, 120, 128, .24);
	--fill-secondary: rgba(120, 120, 128, .14);
	--fill-tertiary: rgba(120, 120, 128, .08);
	--fill-quaternary: rgba(120, 120, 128, .05);

	--line-color: var(--fill-tertiary);

	--elevated-primary: rgba(255, 255, 255, var(--opacity));
	--elevated-secondary: rgba(255, 255, 255, var(--opacity));
	--elevated-tertiary: rgba(255, 255, 255, var(--opacity));

	--shadow-dark-color: hsla(0, 0%, 0%, .08);
	--shadow-color: hsla(0, 0%, 0%, .03);

	--topbar-shadow: 0 5px 10px var(--shadow-color), 0 10px 30px var(--shadow-dark-color);
	--card-shadow: 0 5px 10px var(--shadow-color);
