}

.search .search-keyword:focus {
	background: var(--bg-primary)!important;
	box-shadow: var(--card-shadow2)!important;
}

/*这是搜索框旁边的放大镜*/
.search .searchform button.search-submit {
	top: -11%;
	margin-top: 20px;
	background-color: transparent;
}

.search .search-keyword + .search-submit {
	transition: right .3s, transform .3s!important;
}

.search .search-keyword + .search-submit:hover {
	transform: scale(1.3) rotate(-10deg) translate(1px, -2px)!important;
}

.search .searchform,
.search .bilibili-suggest {
	transition: .3s !important;
	box-shadow: var(--card-shadow);
